INSERT INTO `content` (`id`, `id_cat1`, `id_cat2`, `title`, `comment`, `text`, `keywords`, `description`, `lang`) VALUES
(1, 5, 1, 'ПРИМИТЕ ТО', '', 'Мы – авторы своей Вселенной личной.</br>\r\nМы – архитекторы души, судьбы и тела. </br> \r\nМы – <i>существа</i>, чей разум без предела. </br>\r\nСпособность и талант в нас безграничны. </br>\r\n</br>\r\nМы – уникальность средь миров арены. </br>\r\nМы – кладезь счастья, доброты и веры. </br>\r\nПримите то и распахнутся двери, </br>\r\nВ том месте, где нам видятся лишь стены. </br>', NULL, NULL, NULL),
(2, 5, 1, 'ТВОЕ ТВОРЕНИЕ', NULL, 'И прежде, чем вперед шагнуть, </br>\nЗнай! Жизнь <i> твоя – твоё </i> творение: </br>\nКуда <i>твое</i> ведет стремление,  </br>\nТам <i>твоей</i> жизни ляжет путь. </br>', NULL, NULL, NULL),
(3, 5, 1, 'ИСКУСТВО ЖИТЬ', 'По изречению Альберти\n', 'Не для того даёт природа человеку </br>\nВеличие таланта и ума, </br>\nЧтоб тяготела праздности сума,</br>\nЧтобы изнеженность взяла над ним опеку.</br>\n</br>\nА для того, чтоб пребывать всегда в желанье</br>\nРаскрыть в себе узорчатость души</br>\nИ трепетность её познать в тиши,</br>\nИ чтоб искусство жить постичь в благом деянье.</br>', NULL, NULL, NULL),
(4, 5, 1, 'МЫСЛИ И РУКИ', NULL, 'Мы просим у Господа: Дай, Боже, дай!  </br>\nЯви, помоги, ниспошли и создай. </br>\nНо всё, что он мог, нам отдал на поруки,  </br>\nИсполнят желанья все – мысли и руки. </br>', NULL, NULL, NULL),
(5, 5, 1, 'ТРИ КАЧЕСТВА', NULL, 'Три качества являют чудотворство, <br/>\nТы к ним внимание и силу устреми: <br/> \n<i>Воображение, движение, упорство</i> <br/> \nНас делает великими людьми. <br/>', NULL, NULL, NULL),
(6, 5, 1, 'ПРЕДЕЛ', NULL, 'Часы отчаянной нужды, </br>\nДостигли, кажется предела... </br>\nНе видьте Божеской вражды, </br>\nЗаметьте!</br>\nВоля затвердела,</br>\nДуша по-новому запела,</br>\nМы силой новою горды,</br>\nИ в руки вновь берём бразды, </br>\nказав себе: Пора за дело!</br>', NULL, NULL, NULL),
(7, 5, 1, 'БЕРИСЬ ЗА ДЕЛО', NULL, 'Берись за дело с жаждою решимости. </br>\nПоверь душой в свою реальную звезду. </br>\nЗнай, не свежа вода в недвижимом пруду, </br>\nА ключ, бурлящий, полон исцелимости.\n</br>', NULL, NULL, NULL),
(8, 5, 1, 'КАК ТРУДНО ЭТО', 'По изречению Е. Леца', 'Прозреть стараться и не перезреть.</br>\nБыть сильным львом и также не звереть. </br>\nГореть внутри и также не иссякнуть,</br>\nНе закоснеть и также не размякнуть.</br>\nБыть на посту и не стоять на месте.</br>\nБыть уникальностью и быть со всеми вместе. </br>\nБыть справедливым и прощать ошибки. </br>\nБыть несгибаемым и быть при этом гибким, </br>\nРазносторонним быть, не став двуличным. </br>\nКак трудно это – быть таким различным.\n</br>', NULL, NULL, NULL),
(9, 5, 1, 'БЕЗПОКОЙСТВО', NULL, 'Бывает, что в делах опять расстройство, </br>\nНо лозунг я б такой провозгласил: </br>\nСпокойным будь! Приносит беспокойство </br>\nПустую трату времени и сил.\n</br>', NULL, NULL, NULL),
(10, 5, 1, 'ЦЕНИ ДОВЕРИЕ', NULL, 'Стабильным будь в житейских буднях, </br>\nВсё взвесь пред тем, как переступишь. </br>\nЦени <i>доверие</i> в себе и в людях,</br>\nПойми, <i>доверие</i> не взвесишь и не купишь.\n</br>', NULL, NULL, NULL),
(11, 5, 1, 'БОГАТЫЙ', NULL, 'Богат не тот, кто всех имеет больше, </br>\nА тот богат, кто блага создаёт.</br>\nИ пусть ваш кошелек и будет толще, </br>\nНо славен тот, кто больше отдаёт.\n</br>', NULL, NULL, NULL),
(12, 5, 1, 'ЧУДО В СТАБИЛЬНОСТИ', NULL, 'Доходим часто до истерики: </br>\nМы Чуда ждём от эзотерики, </br>\nМы от религии ждём Чуда, </br>\nОт Библии и от Талмуда. </br>\n</br>\nВсе наши заблужденья, леность, </br>\nБезделье – порождают тленность. </br>\nЗнай, Чудо есть в твоём уделе – </br>\n<i>Стабильным</i> будь в любимом деле!\n</br>', NULL, NULL, NULL),
(13, 5, 1, 'ОТНОШЕНИЕ К ДЕНЬГАМ', NULL, 'Раз к деньгам идёте в рабство, </br>\nНе создать вовек богатства. </br>\nБудете богато жить </br>\nИх заставив – вам служить! \n</br>', NULL, NULL, NULL),
(14, 5, 1, 'УНИКАЛЬНОСТЬ', NULL, 'Ты богат, то пойми и реши. </br>\nТы большое наследство имеешь. </br>\nТы сокровищем ценным владеешь – </br>\n<i>Уникальностью</i> сердца, души. </br>\nТы с ним нужное дело сверши! </br>\n</br>', NULL, NULL, NULL),
(15, 5, 1, 'НЕ КРИТИКУЯ', NULL, 'Во избежанье бедствий не малых величин,</br>\nНе критикуйте следствий, не зная всех причин.\n</br>', NULL, NULL, NULL),
(16, 5, 1, 'ГЛАВНАЯ ОШИБКА', NULL, 'Я понял <i>главную</i> из всех ошибок,</br>\nЧто зря страдал и не ценил, что мог.</br>\nЯ плакал горько, что нет новеньких ботинок, </br>\nПока не встретил вдруг счастливца и без ног.\n</br>', NULL, NULL, NULL),
(17, 5, 1, 'РЕЗУЛЬТАТ', NULL, 'Хочешь в бизнесе иметь заслуги? </br>\nХочешь благ цветущих пышный сад? </br>\nХватит тратиться за видимость услуги, </br>\nА пора платить за результат!\n</br>', NULL, NULL, NULL),
(18, 5, 1, 'РАЗ НЕТ ТЕРПЕНИЯ', NULL, 'Раз нет терпенья добрый внять совет,</br>\nНатерпишься потом нежданных бед.</br>', NULL, NULL, NULL),
(19, 5, 1, 'ДЕНЬГИ', NULL, 'Наши души от денег порой не свободны.</br>\nМолим часто у Бога наличие их.</br>\nКто ж считает, что деньги всё сделать способны, </br>\nЗначит, сам тот способен всё сделать за них.</br>', NULL, NULL, NULL),
(20, 5, 1, 'МАГИЯ СЛОВА', NULL, 'Мы сами создаем свой мир депрессий</br>\nПод магией услышанного слова.</br>\nСвой вклад несём в акт массовых репрессий </br>\nИ жизнь свою калечим этим снова.</br>', NULL, NULL, NULL),
(21, 5, 1, 'К ПОЛУВЕКУ', NULL, 'Привычки вредные забудь,</br>\nНаполни мудрым светом грудь,</br>\nСлужи порядочным манерам</br>\nИ к полувеку стань примером.</br>', NULL, NULL, NULL),
(22, 5, 1, 'КОНТРОЛЬ', NULL, 'Ты в жизни главную приемли роль:</br>\n<i>Твоя</i> действительность и <i>твой</i> контроль.</br>', NULL, NULL, NULL),
(23, 5, 1, 'БОГАТСТВО В РАЗНЫХ РУКАХ', NULL, 'Когда низкий богат, </br>\nТо насилье в ребро,</br>\nКогда мудрый богат,</br>\nОн творит всем добро.</br>', NULL, NULL, NULL),
(24, 5, 1, 'УДАЧА – ЖЕНЩИНА', NULL, 'Зовется женщиной-красавицей <i>удача!</i> </br>\nОна капризна и твердит нам: Нет!</br>\nНо, если хочешь стать счастливей и богаче, </br>\nНе принимай всерьёз ее ответ.</br>', NULL, NULL, NULL),
(25, 5, 1, 'НЕ БУДЬТЕ', NULL, 'Не будьте с жизнью гордым и небрежным:  </br>\nИз горя редко выбирается кто прежним.</br>', NULL, NULL, NULL),
(26, 5, 1, 'ТВОЁ ДЕЛО', NULL, 'Гордость жжет, твою душу губя.</br>\nТвоё дело – не лучше тебя.</br>\n(Недостатки пока живы в людях),</br>\nНе гордись им, повсюду трубя.</br>', NULL, NULL, NULL),
(27, 5, 1, 'ЦЕНИ СЕБЯ', NULL, 'Он жизнь свою ценит на грош</br>\nИ вот на бомжа уж похож,</br>\nА тот оценил в миллион,</br>\nУспешный и радостный он.</br>\nЗа что же судьбу нам винить?</br>\nСебя раз не можем ценить.</br>', NULL, NULL, NULL),
(28, 5, 1, 'УЧИСЬ СЛУШАТЬ', 'По изречению Помпония', 'Пусть и не благотворить, только бы не рушить. </br>\nНе умеешь говорить, так учись же слушать.</br>', NULL, NULL, NULL),
(29, 5, 1, 'НЕ СРАВНИВАЙ', 'По изречению М. Туровского', 'Опять дела – ни то, ни сё.</br>\nВыравнивай!</br>\nИ стань-ка прозорливым:</br>\nВ сравненье познаётся всё.</br>\nНе сравнивай!</br>\nРаз хочешь быть счастливым.</br>', NULL, NULL, NULL),
(30, 5, 1, 'УМЕЙТЕ ПРИНИМАТЬ И ОТДАВАТЬ', NULL, 'Нам нужно истину понять. </br>\nУмей со всей душой принять:</br>\nПодарки, деньги, комплименты.</br>\nБудь благодарным в те моменты.</br>\n</br>\nВсегда ведёт к твоей удаче</br>\nИ бескорыстность при отдаче.</br>\nДелясь, кто радостно поёт,</br>\nТому, поверьте, Бог даёт!</br>', NULL, NULL, NULL),
(31, 5, 1, 'МАЛО СПОРЯТ', 'По изречению Г. Матюшова', 'Что спор о правоте, пусть тот вас донимает. </br>\nЗнай, мало спорят те, кто много понимает.</br>', NULL, NULL, NULL),
(32, 5, 1, 'ЖИЛИ СЧАСТЛИВО И ДОЛГО', 'По изречению С. Янковского', 'И жили они счастливо и долго.</br>\nНо о других узнать</br>\nВдруг стала жизнь их долгом.</br>\nИ счастье не вернулось к ним уж больше.</br>\nКак только стали знать:</br>\nСчастливее живут и дольше.</br>', NULL, NULL, NULL),
(33, 5, 1, 'ПРАВИЛЬНО ЖИТЬ', NULL, 'В деньгах у нас отрада.</br>\nГотовы им служить.</br>\nА думать лучше надо,</br>\nКак <i>правильно</i> бы жить.</br>', NULL, NULL, NULL),
(34, 5, 1, 'ТОГО НЕ УБЕДИШЬ', 'По изречению А. Платона', 'Совет не пригодится и спором навредишь,</br>\nКто сам не убедится, того не убедишь.</br>', NULL, NULL, NULL),
(35, 5, 1, 'МНЕНИЕ', NULL, 'Пора понять – живём печально как.</br>\nЗнать по всему, от боязни сбоим.</br>\nУшли опять мы с мнением начальника,</br>\nХотя пришли к нему мы с мнением своим.</br>', NULL, NULL, NULL),
(36, 5, 1, 'СОВЕТ ПРАВИТЕЛЮ', NULL, 'Где ты, как ты, не скроет гласность.</br>\nХочешь жизнь из часов спокойных?</br>\nЧто ж, повысь свою безопасность,</br>\nПривлекая к себе достойных.</br>\n', NULL, NULL, NULL),
(37, 5, 1, 'ТОЛЬКО ДЛЯ ГЛУПЦОВ', 'По изречению М. де Монтеня', 'Пусть хоть как вы ловки – хитрец из хитрецов,</br>\nНо хитрости, уловки обманут лишь глупцов.</br>\n', NULL, NULL, NULL),
(38, 5, 1, 'ИСКУШЕНИЕ', NULL, 'Сулит нам искушенье</br>\nОбманчивые сласти.</br>\nЧем выше достиженье,</br>\nЗаманчивее страсти.</br>\n</br>\nЧтоб путь твой на вершину</br>\nНе пройден был напрасно,</br>\nСбрось искушенья глину,</br>\nА то снесёт лавина</br>\nВ крутящую пучину,</br>\nЧто больно и опасно!</br>', NULL, NULL, NULL),
(39, 5, 1, 'ЖИЗНЬ ГОСУДАРЕВА', NULL, 'На ложе золотом лежат цари.</br>\nПред ними слуги, яства, ароматы,</br>\nНо держит смерть с той роскошью пари</br>\nИ сверху нависает меч булатный.</br>', NULL, NULL, NULL),
(40, 5, 1, 'ОСТАВЬТЕ', NULL, 'Оставьте темы и обмен словами,</br>\nРаз скучно с теми, кому скучно с вами.</br>\n', NULL, NULL, NULL),
(41, 5, 1, 'ГОРДОСТЬ', NULL, 'Дерзкую гордость внутри затвори,</br>\nУкори, присмири, побори, замори.</br>\nВниманьем не давай ей насыщенья.</br>\nНе выводи её на уровень общенья.</br>', NULL, NULL, NULL),
(42, 5, 1, 'СЕНЕКОВСКАЯ КЛЯЧА', NULL, 'Хвалясь сейчас богатством и ездою,</br>\nОн слыл вчера блудливым босяком.</br>\nНо суть вся в том, что с золотой уздою</br>\nНе станет кляча гордым рысаком.</br>', NULL, NULL, NULL),
(43, 5, 1, 'ВСЁ ВЫНОСИМО', NULL, 'Поверь в мольбе, что слово возносимо.</br>\nСкажи себе: Всё в жизни выносимо!</br>\n', NULL, NULL, NULL),
(44, 5, 1, 'СТРЕМЛЕНИЕМ ЛЮБИТЬ', NULL, 'Есть сила в мире помощней свинца,</br>\nДобрейшим сделает она скупца,</br>\nПриветливым и нежным – гордеца,</br>\nОткрытым и душевным – хитреца,</br>\nСпокойным и послушным – сорванца.</br>\nСтремитесь силу ту употребить:</br>\nНевидимым стремлением любить</br>\nМы сможем открывать сердца.</br>\n', NULL, NULL, NULL),
(45, 5, 1, 'ФОРТУНА', NULL, 'Умей достойно отступать,</br>\nКак и отважно наступаешь,</br>\nФортуне нужно дать поспать,</br>\nКогда удачно с ней играешь.</br>\n</br>\nПусть милости ее кратки,</br>\nЗато прекрасны и велики.</br>\nНо может та, раздать шлепки,</br>\nКогда достанут чьи-то лики.</br>', NULL, NULL, NULL),
(46, 5, 1, 'НЕ ДОВЕРЯЙ ТОМУ', NULL, 'Спокойней будь к уму, раз слишком воспаряет.</br>\nНе доверяй тому, кто всем всё доверяет.</br>\n\n', NULL, NULL, NULL),
(47, 5, 1, 'НУЖНО МУДРОСТЬ ОБРЕСТИ', NULL, 'Зря рвёшься к цели, в кровь сдирая ноги.</br>\nСначала нужно мудрость обрести:</br>\nКто счастья не находит на пути,</br>\nТот счастья не найдёт в конце дороги.</br>\n', NULL, NULL, NULL),
(48, 5, 1, 'УМЕЙ ВЛАДЕТЬ СОБОЙ', NULL, 'Чтоб не был сердца сбой,</br>\nУмей владеть собой.</br>\n</br>\nЧтоб выиграть свой бой,</br>\nУмей владеть собой.</br>\n</br>\nЧтоб справиться с судьбой,</br>\nУмей владеть собой.</br>\n</br>\nТо, должен знать любой,</br>\nУмей владеть собой.</br>', NULL, NULL, NULL),
(49, 5, 1, 'ТОМУ', NULL, 'Тому счастье кажет честь,</br>\nКто доволен тем, что есть.</br>', NULL, NULL, NULL),
(50, 5, 1, 'МУДРОСТЬ ИЗ БИБЛИИ', 'Экл. 7.12\n', 'Глупость заставляет</br>\nЖизнь свести к червонцу.</br>\nМудрость помогает</br>\nНам радоваться солнцу.</br>', NULL, NULL, NULL),
(51, 5, 1, 'ДИСТАНЦИЯ', NULL, 'Дистанцию введите в моду,</br>\nМанёвренности дав свободу.</br>', NULL, NULL, NULL),
(52, 5, 1, 'ПЛАТИТЬ И ПОПЛАТИТЬСЯ', NULL, 'Бывает, любим в жизни покутить,</br>\nБывает, любим чем-нибудь прельститься.</br>\nЗа удовольствия приходится платить,</br>\nНо, иногда, и можем поплатиться.</br>', NULL, NULL, NULL),
(53, 5, 1, 'ВОЗОБНОВИСЬ', NULL, 'И превосходное стареет, с ним и слава.</br>\nПривычка заменяет нам восторги.</br>\nУж кто-то новый зреет из другого сплава,</br>\nСвои устраивая всюду торги.</br>\n</br>\nМеняй, как солнце, свой прекрасный облик.</br>\nИграй, как лучики из радуг в бриллианте.</br>\nОбворожи мир новизной символик,</br>\nВозобновись в победе и в таланте.</br>', NULL, NULL, NULL),
(54, 5, 1, 'НАСТЫРНОСТЬ', NULL, 'Кто опротиветь из-за выгоды готов,</br>\nВ том есть настырность – качество плутов.</br>\n', NULL, NULL, NULL),
(55, 5, 1, 'ПРЕСТУПНЫЕ РАДОСТИ', 'По латинскому изречению', 'Так часто в маете</br>\nСмысл жизни уже трупный.</br>\nНо как несчастны те,</br>\nЧьи радости преступны.</br>', NULL, NULL, NULL),
(56, 5, 1, 'УЙДИ ВОВРЕМЯ', NULL, 'Не жди</br>\nПока не станешь солнцем уходящим!</br>\nНе жди</br>\nРаз чувствуешь, спиною повернутся.</br>\nНе жди</br>\nРаз знаешь, горечь подадут на блюдце.</br>\nНе медли, уходи!</br>\nВерь, будешь вновь входящим.</br>', NULL, NULL, NULL),
(57, 5, 1, 'ДУХ КОЛЛЕКТИВА', 'По изречению Г. Аронова', 'Дух коллектива может и мешать,</br>\nРяды теснее и трудней дышать.</br>', NULL, NULL, NULL),
(58, 5, 1, 'РАССУДОК', NULL, 'Не бесконечные гулянья, песни, пляски</br>\nВ нас порождают радостную суть.</br>\nПолезна праздность иногда для встряски.</br>\nРассудок порождает счастья путь.</br>', NULL, NULL, NULL),
(59, 5, 1, 'ТВОЙ ВЫБОР', NULL, 'Прочь, скажи типичному формату.</br>\nЧеловек в своих делах новатор,</br>\nМожно и руками взять лопату,</br>\nНу а можно сделать экскаватор.</br>', NULL, NULL, NULL),
(60, 5, 1, 'ЖАДНОСТЬ', NULL, 'Жадности – зарок, жадность – к ликвидации.</br>\nЖадность ведь порок, ведущий к деградации.</br>', NULL, NULL, NULL),
(61, 5, 1, 'ИСТИННЫЕ УДОВОЛЬСТВИЯ', NULL, 'В пустую днях пролистанных –-</br>\nНет радостей и ценностей.</br>\nБез истинных потребностей –</br>\nНет удовольствий истинных.</br>', NULL, NULL, NULL),
(62, 5, 1, 'ВЫЖДИ', NULL, 'Житейской мудрости запас</br>\nНам говорит о том:</br>\n«Не бойся отступить сейчас,</br>\nЧтоб победить потом!»</br>', NULL, NULL, NULL),
(63, 5, 1, 'УМЕНИЕ ОТДАВАТЬ', NULL, 'Нечего на счастье уповать,</br>\nЕсли не умеешь отдавать.</br>', NULL, NULL, NULL),
(64, 5, 1, 'БИБЛИЯ', 'ЕК. 5.17', 'Нет радости в жизни иной:</br>\nПокушать, поспать и попить,</br>\nЧтоб труд твой полезный земной</br>\nДобром смог людей окропить.</br>', NULL, NULL, NULL),
(65, 5, 1, 'ДЕРЖИСЬ УМНЫХ', NULL, 'Знай, толку нет от чьих-то криков шумных,</br>\nКогда совсем в судьбе</br>\nНеясен путь,</br>\nДержись людей порядочных и умных,</br>\nКуда идти тебе,</br>\nДай им шепнуть.</br>', NULL, NULL, NULL),
(66, 5, 1, 'МЫСЛИ', NULL, 'Мыслям необдуманным воли не давай.</br>\nКогда всё продумано, поле засевай.</br>', NULL, NULL, NULL),
(67, 5, 1, 'ДВИЖЕНЬЕ И ПОКОЙ', 'По изречению Эпикура\n', 'Есть движенье, есть покой – их двое.</br>\nНаходи в обоих наслажденье:</br>\nБезмятежность, безболезненность – в покое.</br>\nРадость, удовольствие – в движенье.</br>', NULL, NULL, NULL),
(68, 5, 1, 'НЕ ВАЖНО, КАК ВОШЁЛ', NULL, 'Когда войдёшь, бывает, не заметят,</br>\nНе это волновать тебя должно,</br>\nНе все светящего достойно встретят,</br>\nА вот уйдёшь, увидят, что темно.</br>', NULL, NULL, NULL),
(69, 5, 1, 'КТО МОЖЕТ МЫСЛИТЬ', NULL, 'Старайся смысл найти всегда во всём.</br>\nКто может мыслить – значит, может всё.</br>', NULL, NULL, NULL),
(70, 5, 1, 'ФОРМАЛИЗМА НЕ НУЖНО ВУАЛЕЙ', NULL, 'По утрам на восходе вставайте.</br>\nС лепестков нежных воду спивайте.</br>\nВ небо взглядом с восторгом взвивайте.</br>\nКраски жизни в сердечко вливайте.</br>\n</br>\nФормализма не нужно вуалей.</br>\nЧто вам знать, избегайте моралей.</br>\nЗабывайте банальность деталей.</br>\nВырывайтесь из вихря спиралей.</br>', NULL, NULL, NULL),
(71, 5, 1, 'ПОСРЕДСТВЕННОСТЬ И ОДАРЕННОСТЬ', NULL, 'Усердием посредственность порой достигнет больше,</br>\nЧем без усердия достигнет одарённость.</br>\nИ результаты первого цениться будут дольше.</br>\nВсегда дороже труд, чем просто увлечённость.</br>\n', NULL, NULL, NULL),
(72, 5, 1, 'ВЯЛО', NULL, 'Тот, кто ходит вяло – больно ушибается.</br>\nТот, кто мыслит мало – много ошибается.</br>\n', NULL, NULL, NULL),
(73, 5, 1, 'ЛЕГКОЕ И ТРУДНОЕ ДЕЛО', NULL, 'За дело легкое берись,</br>\nКак будто б, то трудно,</br>\nЗа дело трудное борись,</br>\nНо верь, легко оно.</br>\nИ в первом случае тогда,</br>\nНе влезет расслабленье,</br>\nА во втором, как сталь тверда,</br>\nВойдет в тебя стремленье.</br>', NULL, NULL, NULL),
(74, 5, 1, 'НЕ БУДУТ ДОСТИГНУТЫ', 'По изречению Демокрита', 'Ни искусство, ни мудрость не будут достигнуты,</br>\nЕсли знанья ни в том, ни в другом не постигнуты.</br>\n', NULL, NULL, NULL),
(75, 5, 1, 'НЕЧТО СОКРОВЕННОЕ', 'По изречению Баал Шем Тов', 'Не озирайтесь так беспомощно кругом.</br>\nВнутри ищите нечто сокровенное,</br>\nВнутри таится нечто драгоценное,</br>\nТо нечто, чего нет в другом.</br>', NULL, NULL, NULL),
(76, 5, 1, 'НЕ ТОРОПИСЬ', NULL, 'Раз заранее считаешь пир свершённым,</br>\nМожешь дел, потом своих не завершить.</br>\nПламя гордости старайся потушить,</br>\nРаньше времени, всё – не считай решённым.</br>', NULL, NULL, NULL),
(77, 5, 1, 'ЗОВ СЕРДЦА', NULL, 'Зов сердца твоего – есть самый дорогой.</br>\nНе делай же того, что сделал бы другой.</br>', NULL, NULL, NULL),
(78, 5, 1, 'БУДЬ К КАЖДОМУ, КАК К ДРУГУ', NULL, 'Пусть много брызг и суеты, и шума,</br>\nПлыла на том же месте и плывёт,</br>\nА, кажется, что движется вперёд</br>\nРазвития и счастья наша шхуна.</br>\n</br>\nНо, есть совет, как не грести по кругу,</br>\nИ тормозящие откинуть якоря,</br>\nПомчаться в лучезарные моря:</br>\nСоздать умение– быть к каждому, как к другу!</br>\n', NULL, NULL, NULL),
(79, 5, 1, 'ОБЛАДАЙ НЕОБХОДИМЫМ', NULL, 'Правоту раз нужно доказать,</br>\nОбладай необходимым для того,</br>\nЧтоб заставить всех до одного,</br>\nТо признать, что хочешь рассказать.</br>', NULL, NULL, NULL),
(80, 5, 1, 'ГЛУПОСТЬ В РАДОСТЬ', 'По изречению из Библии Пр.15.21', 'У кого ума совсем немного,</br>\nТому в радость глупостей дорога.</br>\n', NULL, NULL, NULL),
(81, 5, 1, 'ПОЗНАЁТСЯ В БЕДЕ', 'По древнеиндийскому изречению', 'Не узнать то в окруженье блага.</br>\nНе узнать то в радостной среде;</br>\nДруг, жена, рассудок и отвага</br>\nПознаются только лишь в беде.</br>', NULL, NULL, NULL),
(82, 5, 1, 'УМЕЙ ЦЕНИТЬ', NULL, 'Раз не ценишь то, что есть,</br>\nДолжен кое-что учесть:</br>\nЧтобы не было дано,</br>\nНе оценишь вс¸ – равно.</br>', NULL, NULL, NULL),
(83, 5, 1, 'ПРЕДУСМОТРИТЕЛЬНОСТЬ', 'По турецкому изречению', 'Предусмотрительность – продумать все пути</br>\nИ знать где выход, если хочется войти.</br>', NULL, NULL, NULL),
(84, 5, 1, 'КОГДА НАСТУПЯТ', 'По изречению Ф. Ницше', 'То твёрдость в чувствах, то они уступят,</br>\nЧто там внутри, таинственен ответ.</br>\nИ лишь тогда, когда на нас наступят,</br>\nУзнается, есть жало в нас иль нет.</br>', NULL, NULL, NULL),
(85, 5, 1, 'ЗА ДЕНЬГИ', NULL, 'Друзей за деньги, знают все, не заведёшь,</br>\nНо, можешь интересное найти знакомство,</br>\nЗабаву, искушение и вероломство.</br>\nНе станет денег, и знакомых не найдёшь.</br>', NULL, NULL, NULL),
(86, 5, 1, 'НАВЯЗЧИВАЯ МЫСЛЬ', 'По изречению А. Франса', 'Навязчивая мысль наглеет очень быстро,</br>\nС ней потеряешь смысл,</br>\nС ней станет жизнь терниста.</br>', NULL, NULL, NULL),
(87, 5, 1, 'ДОМОХОЗЯЙКА', NULL, 'Сеет время жизни решето.</br>\nТруд домохозяйки бесконечен.</br>\nИ ни кем обычно не примечен,</br>\nЛишь заметят, когда бросит делать то.</br>\n', NULL, NULL, NULL),
(88, 5, 1, 'ЧТО ГЛУХИМ', 'По изречению И. Крылова', 'В разумности того, ты убедись сперва,</br>\nВедь глупым, что глухим разумные слова.</br>\n', NULL, NULL, NULL),
(89, 5, 1, 'РАЗ РАЗУМЕН', NULL, 'Меньше нужно бы придир,</br>\nКаждый для тебя собрат.</br>\nРаз разумен командир,</br>\nДоблесть вырастет солдат.</br>', NULL, NULL, NULL),
(90, 5, 1, 'ДЛЯ ЧЕГО ТЫ ЗДЕСЬ?', 'По изречению А. Локтева и Л. Толстого\n', 'Ну неужели без причин явился ты сюда:</br>\nНапутать, сделать глупость и исчезнуть.</br>\nНе могут быть бессмысленно пусты плоды труда</br>\nИ просто так, всё взять и кануть в бездну.</br>\nНе для того горит в тебе Божественный огонь,</br>\nЧтобы коптить им над собою небо.</br>\nИ не к чему по жизни всюду сеять пустозвонь</br>\nИ уходить, как будто здесь и не был.</br>\n', NULL, NULL, NULL),
(91, 5, 1, 'ТВОИ ДЕЛА', 'По изречению Марка Аврелия', 'Ты такие дела должен в жизни исполнить,</br>\nЧтоб на жизненном склоне хотелось их вспомнить.</br>', NULL, NULL, NULL),
(92, 5, 1, 'ВМЕСТЕ С СОТНЕЙ', NULL, 'Один булыжник не удержит груз,</br>\nНо множество воздвигнуться горой.</br>\nКто в одиночку может быть и трус,</br>\nТот вместе с сотней может быть герой.</br>', NULL, NULL, NULL),
(93, 5, 1, 'ПОВЕСТЬ', NULL, 'Такая вот повесть:</br>\nСебя напряжёшь</br>\nИ сладко налжёшь,</br>\nНо ты не обманешь совесть.</br>', NULL, NULL, NULL),
(94, 5, 1, 'ХРАБРОСТЬ БЕЗ УМА', NULL, 'Чрезмерной храбростью своей не тяготись,</br>\nБез дружбы с разумом ты уязвим весьма.</br>\nУмом без храбрости возможно обойтись,</br>\nНо бесполезна храбрость без ума.</br>', NULL, NULL, NULL),
(95, 5, 1, 'СКУПОСТЬ', 'Артха – Веды\n\n', 'Жить для себя – большая глупость,</br>\nЗовётся она словом – скупость.</br>', NULL, NULL, NULL),
(96, 5, 1, 'ОТ ЧЕГО ЗАВИСИТ СЧАСТЬЕ', 'По изречению Д. Карнеги\n', 'Счастье не зависит от того, что мы имеем.</br>\nСчастье зависит от того, как мыслить мы умеем.</br>', NULL, NULL, NULL),
(97, 5, 1, 'ВАЖЕН', NULL, 'Как важен мир весь – кладезь разных дум.</br>\nБожественной природы важен шум.</br>\nВажнейший на планете – человек,</br>\nНо важен в человеке – только ум.</br>\n', NULL, NULL, NULL),
(98, 5, 1, 'ОШИБКИ', NULL, 'Вселенной Сила велика,</br>\nОна не может нас обидеть,</br>\nНужны ошибки, чтоб увидеть,</br>\nЧто истина совсем близка,</br>\nЧрез них расти и свысока</br>\nЗаметить путь из тупика.</br>\n', NULL, NULL, NULL),
(99, 5, 1, 'ЗВЁЗДНЫЙ ЧАС', 'По изречению Евгения Кащеева', 'Иди смелее по решённому маршруту,</br>\nТвой звёздный час наступит в трудную минуту.</br>', NULL, NULL, NULL),
(100, 5, 1, 'ПОЗАБОТЯТСЯ МЫСЛИ', 'По изречению из Библия Пр. 2.10.11', 'Светлый разум навеет тепла,</br>\nКогда к знанью откроется дверца.</br>\nПозаботятся мысли о сердце,</br>\nКогда истина станет мила.</br>', NULL, NULL, NULL),
(101, 5, 1, 'МЕЧТА И ВИДЕНИЕ', NULL, 'Когда мечтаешь, но не видишь, как добиться,</br>\nТо это бесполезная мечта.</br>\nА без мечты раз начинаешь суетиться,</br>\nТо есть бесплодность, то рутина, пустота.</br>\nНадежду мира составляют вместе</br>\nМечта и виденье в едином жесте.</br>', NULL, NULL, NULL),
(102, 5, 1, 'ТОМУ, КТО ТРУДИТСЯ', 'По народному изречению', 'Кто трудится тем, и успех, и почёт.</br>\nПод камень лежачий вода не течёт.</br>', NULL, NULL, NULL),
(103, 5, 1, 'ПОКОРИТЬ УМОМ', NULL, 'Не годится встать и обомлеть</br>\nПеред скалами в неведенье немом.</br>\nРаз не можешь силой одолеть,</br>\nПостарайся одолеть умом.</br>', NULL, NULL, NULL),
(104, 5, 1, 'НИКУДА НЕ ПОПАДУ', NULL, 'По жизни просто так бреду,</br>\nВсего боюсь, всего страшусь.</br>\nРаз ни на что я не решусь,</br>\nТо никуда не попаду.</br>', NULL, NULL, NULL),
(105, 5, 1, 'ПОЧЕМУ УСТУПАЕМ СУДЬБЕ', 'По изречению Жермены де Сталь', 'Мы уступаем</br>\nНе вследствие силы судьбы,</br>\nМы уступаем,</br>\nПоскольку мы сами слабы.</br>', NULL, NULL, NULL),
(106, 5, 1, 'ЛАБИРИНТ', 'По изречению А. Брейтер', 'Слишком уж кто-то цивилен,</br>\nПробует в знаниях спринт.</br>\nЕсли избыток извилин –</br>\nЭто уже лабиринт.</br>\n\n ', NULL, NULL, NULL),
(107, 5, 1, 'СТЕНЫ, ГДЕ НУЖНЫ МОСТЫ', NULL, 'Люди одиноки потому,</br>\nЧто меняют свет на полутьму.</br>\nИ по жизни, проходя версты,</br>\nСтроят стены, где нужны мосты.</br>\n', NULL, NULL, NULL),
(108, 5, 1, 'СУДЬБА НЕ ПРОТИВОСТОИТ', 'По изречению Демокрита\n', 'Раз, тобой продуманы решения,</br>\nТо, судьба не против их свершения.</br>\n\n', NULL, NULL, NULL),
(109, 5, 1, 'ВО ЗДРАВИЕ', 'По изречению А. Круглова\n', 'Чьи-то бредовые мысли –</br>\nВычурный акт своенравия.</br>\nСтолько бы здравого смысла,</br>\nСколько бы нужно во здравие.</br>\n', NULL, NULL, NULL),
(110, 5, 1, 'НАДЕЖДА', NULL, 'Не бойся в жизни деньги потерять</br>\nПускай не купишь: кров, еду, одежду.</br>\nПорой нас Бог так любит проверять.</br>\nТы бойся только потерять надежду.</br>', NULL, NULL, NULL),
(111, 5, 1, 'НАШ ГЛАВНЫЙ УДЕЛ', NULL, 'Нашим главным быть должно уделом:</br>\nУважать всех мыслью, словом, делом.</br>', NULL, NULL, NULL),
(112, 5, 1, 'СДЕЛАЙ РАЗУМ ЛУЧИСТЫМ', NULL, 'Не будь рабом мыслей телесных,</br>\nГлупых, пустых, бесполезных.</br>\nНе думай о пошлом, пустячном,</br>\nГрубом, низменном, мрачном.</br>\nСделай свой разум чистым,</br>\nРадостным, светлым, лучистым.</br>', NULL, NULL, NULL),
(113, 5, 1, 'ОСТОРОЖНО С МЫСЛЯМИ', NULL, 'Будь с мыслями своими осторожен,</br>\nТе могут пулей стать подаренной виску.</br>\nИм, всё – равно!</br>\nЧто меч достать из ножен,</br>\nЧто жизнь дарить по-новому витку.</br>', NULL, NULL, NULL),
(114, 5, 1, 'БЛАГОЕ КАЧЕСТВО', NULL, 'Нет качества в нас более благого:</br>\nБудь терпеливым к нетерпению другого.</br>', NULL, NULL, NULL),
(115, 5, 1, 'ПРОЗОРЛИВЫЙ УМ', NULL, 'Прозорлив ум, когда малость видел и слышал,</br>\nНо потом бесконечно о том размышлял.</br>\nВ результате, узор мудрых выводов вышил,</br>\nКлюч к разгадке сокрытых явлений сваял.</br>\n', NULL, NULL, NULL),
(116, 5, 1, 'СИЛА РАЗУМА', NULL, 'Кто-то вновь стал кровавой лужей.</br>\nПросыпайтесь!…</br>\nДавно уже сказано,</br>\nЧто мощнейшая сила разума,</br>\nВ сто раз твёрже, чем сталь оружий.</br>\n', NULL, NULL, NULL),
(117, 5, 1, 'ГОЛОСОК', 'По изречению Саади', 'Доброта и нежный голосок</br>\nПоведут слона за волосок.</br>\n', NULL, NULL, NULL),
(118, 5, 1, 'СИЛА ДУХА И УМА', NULL, 'Все вроде равны Божьей волею свыше.</br>\nНо бьют нас по-разному жизни шторма:</br>\nКого-то во прах разотрёт кутерьма,</br>\nО славе кого-то слагают тома.</br>\n------------------------------------------------------</br>\nВ одном человек пред другим только выше,</br>\nЛишь силою духа и силой ума.</br>', NULL, NULL, NULL),
(119, 5, 1, 'ВСЁ ЖИВОЕ И НЕ ЖИВОЕ ВО ВЛАСТИ БОГА', 'По восточному ведическому трактату', 'Пусть каждый своей долей дорожит.</br>\nТо тому является предлогом:</br>\nЧто вокруг – всё есть во власти Бога,</br>\nОдному Ему принадлежит.</br>\nНам дано не мало и не много.</br>\nС этим жизнь старайся постигать</br>\nИ на это все надежды возлагать.</br>\nВсё, что нужно, выделено Богом.</br>\n', NULL, NULL, NULL),
(120, 5, 1, 'МЫСЛЬ', NULL, 'Мысль моя – есть живая сила!</br>\nХочу, чтобы благо она приносила.</br>', NULL, NULL, NULL),
(121, 5, 1, 'ОДНО ИЗ ЗАБЛУЖДЕНИЙ', NULL, '«От процветания зависит счастье» –</br>\nОдно из заблуждений человека.</br>\nПорою, денег не вредит участье</br>\nИ их до уровня какого-то опека.</br>\nНо дальше, ну хоть б, сколько богатели,</br>\nСчастливее от денег уж не станем.</br>\nИх, охраняя, многие вспотели.</br>\nОт их излишества, возможно, и устанем.</br>', NULL, NULL, NULL),
(122, 5, 1, 'НЕ ПО СЕРДЦУ', 'По изречению А. Подводного', 'Мысль из недр подсознанья всплывает.</br>\nШепчешь ей: Ты сложна! Не хочу!</br>\nНе по сердцу раз мысль та бывает,</br>\nЗначит мысль тебе не по плечу.</br>', NULL, NULL, NULL),
(123, 5, 1, 'БЕЗ СВОБОДЫ', 'По изречению Ф. Шиллера', 'Раз нет свободы – правды нет суждений.</br>\nГде нет свободы – много заблуждений.</br>', NULL, NULL, NULL),
(124, 5, 1, 'НЕЧЕСТНОЕ БОГАТСТВО', NULL, 'Нечестным способом добытое богатство</br>\nНамного хуже, чем нагая бедность.</br>\nВедь счастье отвергает нечестивцев братство.</br>\nА к бедным может проявлять любезность.</br>', NULL, NULL, NULL),
(125, 5, 1, 'ПЕРЕМЕНЫ', NULL, 'Раз в мыслях будут перемены,</br>\nСудьбы своей разрушишь стены.</br>\nНа чём те мысли остановишь,</br>\nТо в жизнь свою и уготовишь.</br>', NULL, NULL, NULL),
(126, 5, 1, 'СДВИНЕТ СЕБЯ', 'По изречению Сократа', 'Зря тащим мы буксир с излишеством хлопот:</br>\nКто хочет сдвинуть мир, себя пусть сдвинет тот.</br>', NULL, NULL, NULL),
(127, 5, 1, 'СОВЕТ МУДРЕЦА', NULL, 'Пусть завистник, пусть благожелатель,</br>\nУважающий пусть, пусть насмешник,</br>\nПусть то щедрый, пусть вор, пусть стяжатель,</br>\nПусть то праведник, пусть то грешник.</br>\n</br>\nНу, зачем не о том хлопочешь,</br>\nНапрягаешь его испугом,</br>\nРаз влиять на кого-то хочешь,</br>\nСтановись ему просто другом.</br>', NULL, NULL, NULL),
(128, 5, 1, 'ИДЕАЛЬНОЕ СОЗНАНИЕ', NULL, 'Плод зрелый начинает гнить</br>\nИ с нами может статься деградация,</br>\nКогда себе сумеем возомнить,</br>\nЧто мудрою сверх полны информацией.</br>\n</br>\nА плод зелёный продолжает зреть,</br>\nСтремитесь к идеальному сознанию,</br>\nОно гласит, полезно бы узреть,</br>\nКак глубоки и бесконечны знания.</br>', NULL, NULL, NULL),
(129, 5, 1, 'ГЛАВНЫЙ ПРИНЦИП', NULL, 'Раз желаем процветанья, блага умноженья,</br>\nГлавный принцип воспитанья – к старшим уваженье.</br>', NULL, NULL, NULL),
(130, 5, 1, 'ПОМЫСЛЫ И ПОСТУПКИ', NULL, 'Безумны промыслы и радости их хрупки. </br>\nРаз мысль блудлива, значит, мысль тлетворна.</br>\nРазумны помыслы и хороши поступки.</br>\nМысль справедлива, значит плодотворна.</br>', NULL, NULL, NULL),
(131, 5, 1, 'РАЗ ВАЖНО', NULL, 'Не будет жизнь твоя в покое,</br>\nРаз важно мнение людское.</br>', NULL, NULL, NULL),
(132, 5, 1, 'УМ ВЗИРАЕТ', NULL, 'Полезным будет путь, иль в бездну канет.</br>\nНа перст судьбы твоей – твой ум взирает.</br>\nНе только думай, что он выбирает,</br>\nНо выбирай, о чём он думать станет.</br>', NULL, NULL, NULL),
(133, 5, 1, 'ВКУС РАЗВИТИЯ', NULL, 'Если в деле своём устаёшь,</br>\nТы не в том направленьи идёшь.</br>\nЕсли правилен путь твой и груз,</br>\nТо к развитию явится вкус.</br>', NULL, NULL, NULL),
(134, 5, 1, 'СОЗДАЙ УМЕНИЕ', NULL, 'Открыто всё тебе, что нужно знать.</br>\nЖелай лишь видеть, слышать, понимать.</br>', NULL, NULL, NULL),
(135, 5, 1, 'ИЗЛЕЧИМ ЧУВСТВА ОПЫТОМ', 'По изречению В. Фишера', 'Бывает мыслями, и лоб свой раскроим,</br>\nНо так бесцельно тем себя увечим.</br>\nВедь, логикой мы чувства не излечим,</br>\nИзлечим чувства опытом своим.</br>', NULL, NULL, NULL),
(136, 5, 1, 'СЕРЬЁЗНОСТЬ МИРА', NULL, 'О серьёзности мира свой ум не тревожь,</br>\nМир серьёзен: и лживый – получит в нём ложь.</br>\nЖдущий истину – истины свет получает,</br>\nКто достоин чего, это он и встречает.</br>', NULL, NULL, NULL),
(137, 5, 1, 'КРАЙНОСТЬ', NULL, 'К чему же крайность – то утрата меры,</br>\nВ вопросах красоты, добра и веры.</br>\n', NULL, NULL, NULL),
(138, 5, 1, 'НЕ ЗАСТАВЛЯЙ НАСИЛЬНО', NULL, 'Слова не донеслись,</br>\nПусть правота есть в том,</br>\nТы молча помолись,</br>\nПоймёт он всё потом.</br>\nНе заставляй того</br>\nНасильно сделать что-то,</br>\nВедь неприязнь его</br>\nПерерастёт в болото.</br>', NULL, NULL, NULL),
(139, 5, 1, 'ЮЖНЫЙ ПУТЬ', NULL, 'Воздвигнется стена</br>\nПорою в храм алмазный,</br>\nПусть истина одна,</br>\nК ней путь бывает разный.</br>\n</br>\nРаз с севера идёшь,</br>\nХолодный путь и вьюжный,</br>\nСебя ты сбережёшь,</br>\nСменив его на южный.</br>\n', NULL, NULL, NULL),
(140, 5, 1, 'КУДА УТКНЁМСЯ', NULL, 'Проблем бывает сто, бывает радость градом.</br>\nМы видим в жизни то, куда уткнёмся взглядом.</br>\n', NULL, NULL, NULL),
(141, 5, 1, 'ТЕРПЕНИЕ', NULL, 'Давно утвердилось житейское мнение:</br>\nРазвития нет без простого терпения.</br>\nРоди его в сердце ещё молодом,</br>\nВзрасти плодотворным, усердным трудом.</br>', NULL, NULL, NULL),
(142, 5, 1, 'ВЫБОР', 'По восточному ведическому трактату', 'Ради своих</br>\nГордых идей</br>\nСчастьем не жертвуй чужим.</br>\nРади других</br>\nДобрых людей</br>\nСчастьем пожертвуй своим.</br>', NULL, NULL, NULL),
(143, 5, 1, 'НА ДЕЛЕ', NULL, 'О том, что так умен, рассказом не плени,</br>\nРаз знаньем наделён – на деле примени.</br>', NULL, NULL, NULL),
(144, 5, 1, 'ПЛОТНОСТЬ ЖИЗНИ', 'По восточному ведическому трактату', 'Этот день восприми,</br>\nБудто те минуты последние,</br>\nУглуби, растяни,</br>\nИ оставь в нём своё наследие.</br>\n</br>\nСтанет жизнь глубока,</br>\nИзвлечётся безмерное счастье,</br>\nГод продлится века,</br>\nНо твоё в том должно быть участье.</br>\n\n', NULL, NULL, NULL),
(145, 5, 1, 'МИР НАМ НЕ ВРАГ', 'По изречению В. О. Рузова', 'Если снова овраг,</br>\nЗнать, не отдал долги:</br>\nПомни, мир нам – не враг,</br>\nМы себе же – враги.</br>', NULL, NULL, NULL),
(146, 5, 1, 'ТОГДА И НЕ ЕШЬ', 'По изречению из Библии 2 Фес. 3.10', 'Раз не можешь добиться, себя тем не тешь.</br>\nРаз не хочешь трудиться, тогда и не ешь.</br>\n', NULL, NULL, NULL),
(147, 5, 1, 'ЕСЛИ ХОЧЕШЬ ЧТО-ТО ПОЛУЧИТЬ', NULL, 'Если хочешь что-то получить,</br>\nЕсть двойной не мало важный штрих:</br>\n1. Рукава придётся засучить,</br>\n2. Результат пусть радует других.</br>', NULL, NULL, NULL),
(148, 5, 1, 'ЛУЧШЕ ВЛАСТИ НЕТ', 'По изречению Сенеки', 'Сильнее всех – владеющий собою.</br>\nИ лучше власти нет, чем эта власть.</br>\nС холодным разумом, со светлою душою,</br>\nС горячим сердцем будет жизнь во сласть.</br>', NULL, NULL, NULL),
(149, 5, 1, 'СКУКА', NULL, 'Делом скуку заглуши, скука – пустота души.</br>', NULL, NULL, NULL),
(150, 5, 1, 'СОВЕСТЬ', NULL, 'Совесть спать не даёт, просыпаешься.</br>\nИ опять от бессонницы маешься.</br>\nЗнать, по жизни идёшь ты не там, где бы мог.</br>\nПо чужим головам твой ступает сапог.</br>', NULL, NULL, NULL),
(151, 5, 1, 'ТРИ ПЛОДА', 'По изречению Демокрита', 'Нам дарит мудрость три полезных плода:</br>\nПрекрасно мыслить и прекрасно говорить,</br>\nПрекрасно поступать её природа.</br>\nСтарайтесь мудрость в свою душу водворить.</br>', NULL, NULL, NULL),
(152, 5, 1, 'НЕЗАМЕТНО', NULL, 'Скука входит через заспанную лень –</br>\nЖизнь проходит незаметно, будто тень.</br>', NULL, NULL, NULL),
(153, 5, 1, 'БИЗНЕС ЭТО, ИЛИ ВОЙНА', NULL, 'Раз за деньги жизнь меняем смело,</br>\nЭто необъятная цена.</br>\nБизнес переводится – как – дело,</br>\nНо не дело это, а война.</br>', NULL, NULL, NULL),
(154, 5, 1, 'КТО МУДР', NULL, 'Кто мудр, спокоен, в том его отвага,</br>\nКто мудр, тот совестлив, душа полна тепла.</br>\nКто мудр, тот прост, не выбирает блага,</br>\nКто мудр, тот терпелив, тот избегает зла.</br>', NULL, NULL, NULL),
(155, 5, 1, 'ЕСЛИ ЛЕНИ ТЫ ПОКОРЕН', NULL, 'Если лени ты покорен, об тебя споткнёшься,</br>\nЕсли в лени ты упорен, что-нибудь добьешься.</br>', NULL, NULL, NULL),
(156, 5, 1, 'ДЛИНА ЖИЗНИ', 'По изречению Шира Терани', 'С длиною жизни не поделать ничего,</br>\nНо можно сделать с шириной и глубиной.</br>\nСебя отдать ей можно самого,</br>\nИ на земле жизнь сделать неземной.</br>\n\n', NULL, NULL, NULL),
(157, 5, 1, 'БУДЬ АККУРАТЕН СО СВОБОДОЙ', NULL, 'Со свободой обращайся аккуратно.</br>\nСвод законов прочитай заранее,</br>\nЛучше изначальное внимание,</br>\nЧем потом его читать стократно.</br>\n</br>\nКодекс тот, не для меня! – не заикайся:</br>\nОт сумы и от тюрьмы не зарекайся!</br>', NULL, NULL, NULL),
(158, 5, 1, 'МУТНО ОЗЕРЦО', NULL, 'Не чисто течёт словцо, если мутно озерцо.</br>', NULL, NULL, NULL),
(159, 5, 1, 'КАК ЧАСТО В ЖИЗНИ', NULL, 'Как часто в жизни мы пытаемся блуждать,</br>\nСтремиться к цели, не желая ждать.</br>\nНа ровном месте свои чувства возбуждать</br>\nИ непонятно чем кому-то досаждать.</br>\nЖизнь коротка, зачем же нам скучать.</br>\nК чему ж тоска, к чему всё удручать.</br>\nРаз жив пока, жизнь нужно величать.</br>\n', NULL, NULL, NULL),
(160, 5, 1, 'ДЕЛАЙ СВОЁ ДЕЛО', 'Восточная мудрость', 'Если Миссия тобой не овладела,</br>\nПомоги ей сердцем и душой.</br>\nЛучше плохо делай своё дело,</br>\nЧем чужое – делай хорошо.</br>', NULL, NULL, NULL),
(161, 5, 1, 'С ЯЗЫКА', 'По азербайджанскому изречению', 'Пришла опять нужда, но не издалека:</br>\nНа голову беда свалилась с языка.</br>\n', NULL, NULL, NULL),
(162, 5, 1, 'ЧТОБ ИЗБЕЖАТЬ', NULL, 'Предусмотрительности свои чувства вверь,</br>\nЧтоб избежать убытков и потерь.</br>\nА снисходительности подари весь вздор,</br>\nЧтоб избежать всех споров, срывов, ссор.</br>', NULL, NULL, NULL),
(163, 5, 1, 'ПРАВДЕ БЕСПРЕПЯТСТВЕННЫ ГРАНИЦЫ', NULL, 'Мы находимся под действием законов,</br>\nНикуда от них не убежим,</br>\nПусть оформим паспорт на Дракона,</br>\nНо при этом, не становимся мы им.</br>\nУезжаем зря, меняем лица,</br>\nПравде беспрепятственны границы.</br>', NULL, NULL, NULL),
(164, 5, 1, 'СЧАСТЛИВЫЕ ВОЗМОЖНОСТИ', NULL, 'Пора оставить темы, что жизнь – сплошные сложности,</br>\nИ превратить проблемы в счастливые возможности.</br>', NULL, NULL, NULL),
(165, 5, 1, 'БУДЬ ОСТОРОЖЕН', NULL, 'Будь осторожен, пусть и знаешь все уловки.</br>\nБудь осторожен, пусть в делах не новичок:</br>\nБесплатный сыр бывает только в мышеловке,</br>\nА под наживкою скрывается крючок.</br>', NULL, NULL, NULL),
(166, 5, 1, 'НЕ ПРИЕМЛЯ, ОСКОРБЛЯЕШЬ', NULL, 'Если, не приемлем чьи-то мысли,</br>\nЭтим человека оскорбляем.</br>\nИ свой взгляд навязывать нет смысла,</br>\nЛучше, когда мы в себя влюбляем.</br>', NULL, NULL, NULL),
(167, 5, 1, 'ДОСТОЙНО СЛАВЫ', 'По изречению В. Гюго', 'В душе старайся утвердить подсказок мудрых сплавы:</br>\nНе победить, а убедить – вот что достойно славы.</br>', NULL, NULL, NULL),
(168, 5, 1, 'ДЕЛАЙ, ЧТО ЗАВИСИТ ОТ ТЕБЯ', 'По древнеиндийскому изречению', 'Ты с другими не живи грубя,</br>\nНо от дел беги, зависящих от них.</br>\nНесёт радость, что зависит от тебя.</br>\nНесёт горе, что зависит от других.</br>', NULL, NULL, NULL),
(169, 5, 1, 'ИДЕЮ ДОНОСИ С ЛЮБОВЬЮ', NULL, 'Возможно, это для тебя не будет новью,</br>\nВозможно, скажешь: Этим я владею!</br>\nКогда желаешь донести идею,</br>\nТо доноси её с заботой и любовью.</br>', NULL, NULL, NULL),
(170, 5, 1, 'В СРОК', NULL, 'Не постигнем знаний в срок –</br>\nЖизнь преподнесёт урок.</br>', NULL, NULL, NULL),
(171, 5, 1, 'ТВОЯ ЗАДАЧА', NULL, 'Твоя задача, чтобы было в сердце чисто,</br>\nСветло, уютно, радостно, цветисто.</br>\nПусть выполняет сердце роль Богини,</br>\nДела ж твои оценятся другими.</br>', NULL, NULL, NULL),
(172, 5, 1, 'ПОЛЬЗА НАЧИНАЕТСЯ С СЕБЯ', NULL, 'Любая польза начинается с себя,</br>\nРаз убедил свой ум – придёт победа.</br>\nКогда по истинному ступишь следу,</br>\nПолезную с людьми начнёшь беседу.</br>', NULL, NULL, NULL),
(173, 5, 1, 'ПРАВДИВОСТЬ', NULL, 'Когда едины мысли, слово, дело,</br>\nТо значит ты – правдивый человек.</br>\nЖиви с правдивостью уверенно и смело,</br>\nЛжи – правду жизни не сломать вовек!</br>', NULL, NULL, NULL),
(174, 5, 1, 'ПОПУТНЫЙ ВЕТЕР', NULL, 'Пред собой кто не видит и метра,</br>\nТем не будет попутного ветра.</br>', NULL, NULL, NULL),
(175, 5, 1, 'ДУРАК НЕ ТОТ', NULL, 'Дурак, не тот, кто не имеет мудрых знаний,</br>\nУмнейшие из умных не познают всё.</br>\nУпрямства с гордостью достаточно стараний,</br>\nЧтоб в списки дураков был кто-то занесён.</br>', NULL, NULL, NULL),
(176, 5, 1, 'ШАНС', NULL, 'Когда все окружающие люди</br>\nВоспримут нас культурно и с добром,</br>\nТо, это шанс благословенный будет,</br>\nУдачу сделать жизненным ядром.</br>', NULL, NULL, NULL),
(177, 5, 1, 'УЧЕНИЕ ХРИСТА', NULL, 'Не жди, просто так, волшебства,</br>\nЗнай, вера без действий мертва.</br>', NULL, NULL, NULL),
(178, 5, 1, 'ДОСТАТОЧНО', 'По изречению И. Щевелёва', 'Пуд соли нужно с человеком съесть,</br>\nЧтоб вычислить его морали кредо.</br>\nНо чтоб понять, как мыслит его честь,</br>\nДостаточно и одного обеда.</br>', NULL, NULL, NULL),
(179, 5, 1, 'ПРОВОЦИРОВАТЬ ЗАВИСТЬ НЕЛЬЗЯ', NULL, 'Очень ярко, бывает, блеснём,</br>\nВидя, рядом скромнее стезя,</br>\nМы ответственность этим несём:</br>\nПровоцировать зависть нельзя!</br>', NULL, NULL, NULL),
(180, 5, 1, 'ЦЕННОСТЬ ЖИЗНИ', NULL, 'Раз впустую час потратить смеешь,</br>\nЗначит, жизнь ценить ты не умеешь.</br>', NULL, NULL, NULL),
(181, 5, 1, 'ГЛУПОСТЬ И РАЗУМ', 'По изречению Б. Андреева', 'Злость всех ненавидит,</br>\nДоброта умеет верно выручать,</br>\nГлупость всё увидит,</br>\nРазум многое не станет замечать.</br>', NULL, NULL, NULL),
(182, 5, 1, 'ТЕРПИМОСТЬ И БЛАГОДАРНОСТЬ', NULL, 'Чтоб найти меж собой солидарность,</br>\nЧтоб свободно, спокойно вздохнуть,</br>\nНам два качества нужно вернуть:</br>\nТо – терпимость и благодарность.</br>', NULL, NULL, NULL),
(183, 5, 1, 'КНУТ И ПРЯНИК', NULL, '«Кнут и пряник», без всякой иронии,</br>\nЕсть активные части гармонии.</br>', NULL, NULL, NULL),
(184, 5, 1, 'СПОР С ГЛУПЦОМ', 'По изречению из Библии Пр. 29.29.', 'Раз вступишь в спор с глупцом,</br>\nЗакружит спор кольцом.</br>\nРассердишься, иль рассмеёшься,</br>\nДо истины не доберёшься.</br>', NULL, NULL, NULL),
(185, 5, 1, 'ВЫРАЖЕНИЕ ЛИЦА', NULL, 'Макияж наносим на лицо</br>\nИ выходим важно на крыльцо,</br>\nТолько краска – впечатленье для глупца,</br>\nУмный видит выражение лица.</br>', NULL, NULL, NULL),
(186, 5, 1, 'ВЕРА ПОБЕЖДАЕТ СТРАХ', NULL, 'Боимся многого, такая в нас манера,</br>\nЛюбой из страхов побеждает вера.</br>', NULL, NULL, NULL),
(187, 5, 1, 'ПРИЗНАК БЛАГОРОДСТВА', NULL, 'Чтоб не взяла господства</br>\nПорочная черта,</br>\nЗакрой пред ней врата.</br>\n-------------------------------</br>\nЕстественность и доброта –</br>\nВот признак благородства.</br>', NULL, NULL, NULL),
(188, 5, 1, 'ПРОСТОТА – НАДЁЖНОСТЬ ПРОГРЕССА', NULL, 'Просим у Бога мы денег мешок,</br>\nА вот для ног то является шок.</br>\nЭтим мешком их совсем подкосим.</br>\nКаждый наш шаг уже еле сносим.</br>\nЗнай, простота – есть надёжность прогресса.</br>\nДвигаться легче без лишнего веса.</br>\n', NULL, NULL, NULL),
(189, 5, 1, 'НЕ ДЕЛИТЕ МИР', NULL, 'Война на злобный пир пребудет госпожой,</br>\nПока мы делим мир на свой и на чужой.</br>', NULL, NULL, NULL),
(190, 5, 1, 'ЧЕСТЬ', NULL, 'Пусть случится момент чрезвычайный,</br>\nНо не может то быть отнято.</br>\nТвою честь не отнимет никто,</br>\nЕсли сам не уронишь случайно.</br>', NULL, NULL, NULL),
(191, 5, 1, 'РАЗРУШИТЕЛЬ И СОЗИДАТЕЛЬ', NULL, 'Раз рушишь, то начнёшь с нуля и снова,</br>\nРаз созидаешь, пересмотришь то, что есть.</br>\nИль разрушение – судьбы основа,</br>\nИль созидание тебе являет честь.</br>\n', NULL, NULL, NULL),
(192, 5, 1, 'МНОГОЗНАЙСТВО', 'По изречению Гераклита', 'Тот, кто мудр, ум от Бога получит.</br>\nМногознайство уму не научит.</br>', NULL, NULL, NULL),
(193, 5, 1, 'ГОЛОС СОВЕСТИ', NULL, 'Зря совести боимся строгих кар,</br>\nЗря совести боимся силуэта.</br>\nБогообщения утерян дар,</br>\nНо раз желаем, восстановим это.</br>\nУслышим совесть, совесть – Божий глас.</br>\nУслышим Бога весть, а Бог услышит нас.</br>', NULL, NULL, NULL),
(194, 5, 1, 'ЛЮБОЙ КОНФЛИКТ СТАРАЙСЯ УНИМАТЬ', NULL, 'Раз кто-то меж собой, не поделив, воюет.</br>\nТо чью-то сторону не нужно принимать,</br>\nНет победителей, то нужно понимать,</br>\nВойна – агония и каждый в ней горюет,</br>\nЛюбой конфликт всегда старайся унимать.</br>', NULL, NULL, NULL),
(195, 5, 1, 'О ЖИЗНИ', NULL, 'Раз жизнь проживаешь, как надо.</br>\nСтановится жизнь, как награда!</br>', NULL, NULL, NULL),
(196, 5, 1, 'СОБЛЮДЕНИЕ ЗАПОВЕДЕЙ', 'Библия Сир. 32.24', 'Ты сможешь заповедей Божьих не нарушить,</br>\nКогда во всяком деле будешь слышать душу.</br>\nТы сможешь заповедей Божьих мудрость счесть,</br>\nКогда во всяком действе будешь слышать честь.</br>', NULL, NULL, NULL),
(197, 5, 1, 'СЕМЬЯ', NULL, 'Семья – не полигон для вожделения.</br>\nСемья – система дружбы и развития,</br>\nОбщения, духовного стремления.</br>\nИдей, влечений, интересов слитие.</br>', NULL, NULL, NULL),
(198, 5, 1, 'ЗАЖИГАЯ СВЕТ', NULL, 'Свет зажигая каждой встреченной судьбе,</br>\nПуть озаряешь людям и себе.</br>', NULL, NULL, NULL),
(199, 5, 1, 'МУДРЫЙ СОВЕТ', 'По изречению М. Эбнер-Эшенбах', 'Этот мудрый совет не забудьте,</br>\nПригодится он в деле любом:</br>\nСвоей воли хозяином будьте,</br>\nСвоей совести станьте рабом.</br>', NULL, NULL, NULL),
(200, 5, 1, 'ДИСТАНЦИЯ – КУЛЬТУРА', NULL, 'Порою, равенство и братство иллюзорно,</br>\nОбида, зависть – человечества натура.</br>\nБалансы в отношениях нужны, бесспорно,</br>\nДистанция между людьми – культура.</br>', NULL, NULL, NULL),
(201, 5, 1, 'КТО ТЕРПЕЛИВ', NULL, 'Пускай, отлив, волна не бьётся, жизнь бормочет.</br>\nКто терпелив, всего добьётся, что он хочет.</br>', NULL, NULL, NULL),
(202, 5, 1, 'ПРАЗДНИК НЕПРЕРЫВНЫЙ', 'По изречению из Библии Прит. 15.15', 'Исполнится мечта:</br>\nПокинет маета,</br>\nОткроются врата,</br>\nРай позовёт призывно.</br>\n----------------------------</br>\nСовесть раз чиста –</br>\nПраздник непрерывный.</br>', NULL, NULL, NULL),
(203, 5, 1, 'ДОСТОЙНО ПОНИМАНИЯ', NULL, 'Чтоб поняли, о чём же речь,</br>\nБеседуй внятно и пристойно.</br>\nНо постарайся то изречь,</br>\nЧто понимания достойно.</br>', NULL, NULL, NULL),
(204, 5, 1, 'ЗАВТРАШНИЙ ДЕНЬ', 'Библия. Прит. 27.1', 'Хвалиться не стоит нам завтрашним днём,</br>\nВедь, так как не знаем, что явится в нём.</br>', NULL, NULL, NULL),
(205, 5, 1, 'СОВЕСТЬ НЕ ТЕРЯЙ', 'По изречению В. Сумбатова', 'Ты совесть одобряй,</br>\nТы совести вверяй,</br>\nТы совесть не теряй.</br>\nНайдётся как – знобя,</br>\nСвербя и теребя,</br>\nЗамучает тебя.</br>', NULL, NULL, NULL),
(206, 5, 1, 'ИЗБЫТОК', 'По изречению Колина Кларка', 'Чрезмерно свои знанья не цени,</br>\nВедь не познать всё, каждый здесь – тупица.</br>\nКогда ж избыток знаний, эрудиций,</br>\nВоображенья вязнут шестерни.</br>', NULL, NULL, NULL),
(207, 5, 1, 'ОПРАВДАНИЕ', NULL, 'Совет нам нужно полезный принять:</br>\n«Оправдываться, значит – себя обвинять!»</br>', NULL, NULL, NULL),
(208, 5, 1, 'ИБО В ЭТОМ ЗАКОН', 'По изречению из Библии Мф. 7.12', 'Есть закон, побеждающий тягостный мрак,</br>\nПомогающий править своею судьбой:</br>\nС этим миром во всём поступай всегда так,</br>\nКак хотелось, чтоб мир поступал бы с тобой.</br>', NULL, NULL, NULL),
(209, 5, 1, 'ЖИЗНЬ НЕ СТОИТ НА МЕСТЕ', NULL, 'Притаился без действий на кочке.</br>\nПоселился в единственной точке.</br>\nПрекратил продвиженье вперёд.</br>\nВниз катиться настанет черёд,</br>\nТак как жизнь, иль паденье, иль взлёт.</br>', NULL, NULL, NULL),
(210, 5, 1, 'ПОКА НЕ ЧЕСТНЫ', NULL, 'Пока не честны пред собой и другими,</br>\nСебе и другим нам не быть дорогими.</br>', NULL, NULL, NULL),
(211, 5, 1, 'СЕТЬ ДЛЯ СЕБЯ', 'По изречению из Библии Иов. 20.5', 'Кто в грехах погряз своих бездонных,</br>\nТот плетёт сеть для своих размеров.</br>\nКратковременно веселье беззаконных,</br>\nБыстротечна радость лицемеров.</br>', NULL, NULL, NULL),
(212, 5, 1, 'КТО ИЛИ ЧТО ПОРАБОЩАЕТ НАРОД', 'По изречению Генри Джорджа', 'Всегда у массы власть в руках.</br>\nКто, короли на облаках?</br>\nКто, президенты на верхах?</br>\nНа чей мы уповаем взмах?</br>\nВнизу – бездумность, леность, страх.</br>\nВверху – те ж мы, не божий Спас,</br>\nНевежество – всех губит нас.</br>', NULL, NULL, NULL),
(213, 5, 1, 'МНОГО ДОСТОИНСТВ', NULL, 'Раскройте качества благих задатков.</br>\nМы все – владельцы фонда золотого.</br>\nНе важен тот, в ком мало недостатков,</br>\nА важен тот, в ком есть достоинств много.</br>', NULL, NULL, NULL),
(214, 5, 1, 'УДОВОЛЬСТВИЯ', NULL, 'Без удовольствия бывает скучно</br>\nИ многие с ним просто неразлучны.</br>\nНа удовольствие глядят и скупо,</br>\nСчитая, только для него жить глупо.</br>', NULL, NULL, NULL),
(215, 5, 1, 'ЧИСТОТА', 'По изречению из Библии Тит. 1.15', 'Чтоб стала жизнь чудесна и лучиста,</br>\nСвой ум и совесть чистотой спасём.</br>\nДля чистых в этом мире чисто всё.</br>\nА для неверных, осквернённых, всё нечисто.</br>', NULL, NULL, NULL),
(216, 5, 1, 'ВОЛЯ', NULL, 'Об этом напевы гласили;</br>\nВоля – орудие силы!</br>\nОб этом рассказы служилы;</br>\nУ воли – стальные жилы!</br>\nОб этом наследие деда;</br>\nТвердая воля – победа!</br>', NULL, NULL, NULL),
(217, 5, 1, 'БЕГИ ОТ ГРЕХА', 'По изречению из Библии Сир. 21.2\n', 'Соблазны и грехи – совсем не чепуха,</br>\nТот глуп, кто в мышеловке сыр расхвалит.</br>\nКак от лица змеи, беги ты от греха,</br>\nА подойдёшь к нему, знай, он ужалит.</br>\n', NULL, NULL, NULL),
(218, 5, 1, 'УПЛАТА СЧАСТЬЮ', NULL, 'Давно возносили</br>\nНеистовство силы,</br>\nНо, это могилы,</br>\nИ это – утрата,</br>\nИ тяжкое бремя.</br>\nМы Дух воскресили,</br>\nУ Бога спросили,</br>\nИ нам возгласили,</br>\nЧто, счастью уплата –</br>\nТерпенье и время.</br>', NULL, NULL, NULL),
(219, 5, 1, 'ПРИВЫЧКИ\nПРИВЫЧКИ', NULL, 'Опасна густота болотной тины,</br>\nЗатянет незаметно в черны клети.</br>\nНачальные привычки – паутины,</br>\nА поздние привычки – прочны сети.</br>', NULL, NULL, NULL),
(220, 5, 1, 'ЗАБЛУЖДЕНИЕ', NULL, 'Нелепа мысль, что счастье входит в вещи.</br>\nЛомалась жизнь порой с богатством резче.</br>\nС блестящей роскошью и всякой новизною</br>\nМы видим и тщеславье показное.</br>\n</br>\nНад нами не должны держать господства:</br>\nКомфорт, предметы разного удобства.</br>\nЧтоб вещи нас не взяли в осажденье,</br>\nМы не введёмся ими в заблужденье.</br>\n', NULL, NULL, NULL),
(221, 5, 1, 'ЗАГНАННЫЙ ПОРОКОМ', NULL, 'Чтоб избежать не нужной хворы,</br>\nСмотри на жизнь разумным оком:</br>\nТы будешь загнанный пороком,</br>\nРаз начал с ним переговоры.</br>', NULL, NULL, NULL),
(222, 5, 1, 'ЦЕНИТЕ ЖИЗНЬ', NULL, 'Доставая деньги – мы здоровье тратим.</br>\nЧтоб вернуть здоровье – деньги тратим мы.</br>\nЖизнь не ценим будто, вечно не утратим.</br>\nТак умрём не живши, во тьму войдя из тьмы.</br>', NULL, NULL, NULL),
(223, 5, 1, 'УНИЖАЕТ И МЕНЯ', 'По изречению У. Уитмена', 'Как встретим вдруг такого, </br>\nкто судит всех черня,</br>\nНужно сразу вспомнить фразу:</br>\n«Унижающий другого, унижает и меня»</br>', NULL, NULL, NULL),
(224, 5, 1, 'РАЗВИТИЕ ПАМЯТИ', NULL, 'Наша память удлиняет время</br>\nИ что нужно, то даёт уму.</br>\nПрорасти в себе вниманья семя,</br>\nРазгони в своем сознанье тьму.</br>\n</br>\nСконцентрируйся на данном деле,</br>\nПроявляй к деталям интерес.</br>\nИ что раньше замечалось еле,</br>\nТо покажет значимость и вес.</br>', NULL, NULL, NULL),
(225, 5, 1, 'КЛЕЙМО ТЩЕСЛАВИЯ', NULL, 'Многим хватает твёрдости,</br>\nУйти от тщеславья клейма.</br>\nЗнают, что сколько гордости,</br>\nСтолько нехватка ума.</br>', NULL, NULL, NULL),
(226, 5, 1, 'ВНИМАНИЕ', NULL, 'Внимание к сути нам роет канал,</br>\nПо-которому внутрь течет знание.</br>\nНе внимательный ум три детали познал,</br>\nНо еще тридцать – дарит внимание!</br>\n', NULL, NULL, NULL),
(227, 5, 1, 'МЕСТО В МИРЕ', 'По изречению А. Круглова', 'Себя внутри не торопись пленять.</br>\nДавит гордость подобно гире.</br>\nИмей величие души понять,</br>\nКак мало твоё место в мире.</br>', NULL, NULL, NULL);
INSERT INTO `content` (`id`, `id_cat1`, `id_cat2`, `title`, `comment`, `text`, `keywords`, `description`, `lang`) VALUES
(228, 5, 1, 'ОДНО ДЕЛО', NULL, 'Мы считаем, что думаем в миг обо всем.</br>\nНаш же ум между мыслями мчится.</br>\nОни очередь кругом устроили в нём.</br>\nНо чему? Можем так научиться.</br>\n</br>\nОдно дело возьми, иль идею одну.</br>\nУстреми силу к ней и внимание.</br>\nИ найдёшь и ответ, и черпнешь новизну,</br>\nИ увидишь ее обаяние.</br>\n', NULL, NULL, NULL),
(229, 5, 1, 'ПРИБАВЬ СКРОМНОСТЬ', NULL, 'Ты всеми будешь чтим,</br>\nВо всем тебе преклонность,</br>\nК достоинствам своим,</br>\nПрибавь лишь только скромность.</br>\n', NULL, NULL, NULL),
(230, 5, 1, 'ВЫСОТА', NULL, 'Талантливый, восторженный ученый,</br>\nСпособный, устремлённый, утончённый,</br>\nВ науке достигает высоты,</br>\nУвидев в истине источник красоты.</br>', NULL, NULL, NULL),
(231, 5, 1, 'УЗНАЙ О СЕБЕ', NULL, 'Раз кажется, будто ты солнцу подобен,</br>\nЗачем находится в дурманящем хмеле?</br>\nУзнай о себе, раз к такому способен,</br>\nПытаясь то самое сделать на деле.</br>', NULL, NULL, NULL),
(232, 5, 1, 'ЗАМОК НА ГОРЕ', NULL, 'Тот замка стоит стеной</br>\nУ пика горы Изобилия,</br>\nКто платит за то ценой</br>\nОпасности и сверх усилия.</br>\n', NULL, NULL, NULL),
(233, 5, 1, 'ПОД ТЕНЬЮ СУЕТЛИВОГО ЭКСТАЗА', NULL, 'Под тенью суетливого экстаза,</br>\nПервы, правы, и хороши, и хватки,</br>\nСвои достоинства мы замечаем сразу,</br>\nНе замечаем только долго недостатки.</br>\n', NULL, NULL, NULL),
(234, 5, 1, 'ЧЕЛОВЕК – ТВОРЕЦ', 'Мутазилиты', 'Хочешь, хижину строй,</br>\nХочешь, райский дворец,</br>\nЧеловек сам – герой</br>\nИ поступков творец!</br>\n…...…...…...…...…...…...…...…...….</br>\nА иначе природа была б не сметлива</br>\nИ пред Богом ответственность не справедлива.</br>\n', NULL, NULL, NULL),
(235, 5, 1, 'ПОД НОГИ ВСЁ Ж НАДОБНО СМОТРЕТЬ', 'По изречению Сергея Скотникова\n\n', 'Судьбу не изменить и не стереть,</br>\nВсевышним Господом та писана на небе.</br>\nНо под ноги всё ж надобно смотреть,</br>\nИ самому чуть-чуть задуматься о хлебе.</br>', NULL, NULL, NULL),
(236, 5, 1, 'ЛЮДИ ВЕРЯТ', NULL, 'Раз о себе сказал ты плохо.</br>\nАвторитет пойдёт по швам;</br>\nТвои слова – к тебе ж с подвохом,</br>\nВедь люди верят тем словам.</br>\n', NULL, NULL, NULL),
(237, 5, 1, 'ВЫЖДАТЬ ИЛИ СОЗДАТЬ', 'По изречению А. Афиногенова', 'У каждого разные нравы.</br>\nМысли являют контраст.</br>\nВыждет свой случай слабый,</br>\nСильный его создаст.</br>', NULL, NULL, NULL),
(238, 5, 1, 'БУДЬ ВЕЖЛИВО УЧАСТЛИВ', NULL, 'В судьбе своей будь вежливо участлив,</br>\nНа что-то щедр, на что-то очень скуп.</br>\nНе уважающий себя – в душе несчастлив,</br>\nГордящийся собою в мыслях – глуп.</br>', NULL, NULL, NULL),
(239, 5, 1, 'ПРЕКРАСНАЯ ИДЕЯ', 'По изречению А. Лейкина', 'Примите, друзья, настоятельства,</br>\nДоверьтесь прекрасной идее:</br>\nКогда вас сильней обстоятельства,</br>\nТогда нужно бы стать их – умнее.</br>', NULL, NULL, NULL),
(240, 5, 1, 'ДОКАЖЕШЬ НА ДЕЛЕ', 'По изречению К. Гельвеция', 'Каким дурманом бы людей ни напои,</br>\nЗнай, долго не удержишь их во хмеле.</br>\nПризнаются достоинства твои,</br>\nКоторые докажутся на деле.</br>\n\n', NULL, NULL, NULL),
(241, 5, 1, 'В НАС НЕ ХВАТАЕТ СПОСОБНОСТИ', 'По изречению Гелия Аронова', 'В чём же причина ущербности?</br>\nЕсли возникли потребности,</br>\nНо помешали нам сложности,</br>\nНе появились возможности,</br>\nНужно понять то, без злобности,</br>\nВ нас не хватает способности.</br>', NULL, NULL, NULL),
(242, 5, 1, 'БЛАГОРОДНЫЙ МУЖ', 'По изречению Конфуция', 'Пусть себе то каждый уяснит,</br>\nНеудача скрыта в нас самих.</br>\nБлагородный муж себя винит,</br>\nНизкий человек винит других.</br>\n', NULL, NULL, NULL),
(243, 5, 1, 'КАЖДЫЙ КУЗНЕЦ СВОЕГО СЧАСТЬЯ', 'По высказыванию Плавта', 'Донесла молва – людской гонец:</br>\n«Человек, пойми же, наконец,</br>\nЧто в твоих руках меч-кладенец,</br>\nИ с тобою выбора венец:</br>\nМожешь быть как тигр и как птенец,</br>\nМожешь стать как пух и как свинец,</br>\nЗнай, ты – счастья своего кузнец!»</br>', NULL, NULL, NULL),
(244, 5, 1, 'МАНИЯ БЕЗРАЗЛИЧИЯ', NULL, 'В нас к безразличью мания.</br>\nСебя ж ты вразуми:</br>\nНемножечко внимания</br>\nВ общении с людьми.</br>\n', NULL, NULL, NULL),
(245, 5, 1, 'МОЖНО ТО МГНОВЕННО ПОТЕРЯТЬ', NULL, 'Цени душой всей жизненное благо,</br>\nПока оно способно озарять.</br>\nНе понимает, возгордившись, бедолага,</br>\nЧто можно то мгновенно потерять.</br>\n', NULL, NULL, NULL),
(246, 5, 1, 'ПОРОЙ ДОСТАТОЧНО', NULL, 'Не нужно многим людям мудрых тем,</br>\nРаз рядом ты, в том Господа опека.</br>\nИзбавить, чтоб от множества проблем,</br>\nПорой достаточно послушать человека.</br>', NULL, NULL, NULL),
(247, 5, 1, 'СПЕСЬ', NULL, 'Есть разные в жизни обличия,</br>\nЕсть свойственность к разным речам.</br>\nСпесь – мания в людях величия,</br>\nПрисущая мелким рвачам.</br>', NULL, NULL, NULL),
(248, 5, 1, 'ДОСТАТОК НЕ ХУЖЕ ИЗБЫТКА', 'По английскому изречению', 'Предела у роскоши нет,</br>\nСтремление к ней, чаще – пытка.</br>\nПримите мудрейший совет:</br>\nДостаток не хуже избытка.</br>\n', NULL, NULL, NULL),
(249, 5, 1, 'ПРОСТО УМНЫЙ', 'По изречению из Библии Пр. 11.12.', 'Меж людьми всегда бывает трение.</br>\nКто есть кто, ребёнок различит.</br>\nСкудоумный выразит презрение,</br>\nПросто умный человек смолчит.</br>\n', NULL, NULL, NULL),
(250, 5, 1, 'НЕ ВСЁ НАМ ПОЛЕЗНО', 'Мысли из Библии 1 Кор. 6.12', 'Это так упоительно!</br>\nЭто очень любезно!</br>\nМного нам позволительно,</br>\nНо не всё нам полезно.</br>', NULL, NULL, NULL),
(251, 5, 1, 'СУТЬ НЕПОЛНОЦЕННОСТИ', NULL, 'Избегай уродства</br>\nИ живи в почтенности.</br>\nВ чувстве превосходства –</br>\nСуть неполноценности.</br>', NULL, NULL, NULL),
(252, 5, 1, 'СТРАСТЯМ, СУДЬБЕ, ЗАКОНАМ', NULL, 'Диоген оставил нам фразы:</br>\n«Страстям покажите разум!»</br>\nЗовите напористость, гордость,</br>\n«Судьбе подарите твёрдость!»</br>\nСвободу введите в моду,</br>\n«Законам явите природу!»</br>', NULL, NULL, NULL),
(253, 5, 1, 'ПОЛЬЮТ НА НИЗИНЫ', 'По индийскому изречению', 'Почтеньем наполни корзины,</br>\nДобро с дружбой пусть поведёт.</br>\nКак воды польют на низины,</br>\nТак благо к тебе припадёт.</br>\n', NULL, NULL, NULL),
(254, 5, 1, 'СЕКРЕТ ВОСТОКА', NULL, 'На Востоке говорят в роду:</br>\nЕсть два способа свершения желаний.</br>\nПервый: быть у них на поводу,</br>\nДостигая цель путём любых блужданий.</br>\n</br>\nИ второй есть способ: ограничив,</br>\nКонтролируешь желаний каждый шаг,</br>\nСчастье со свободой увеличив.</br>\nТе являются, вручая море благ.</br>', NULL, NULL, NULL),
(255, 5, 1, 'ГИБКОСТЬ', NULL, 'Нам в жизни нужно гибким быть, поверь!</br>\nЗнай, гибкость – высота талантов!</br>\nРаз «стоп» сюда, туда подергай дверь:</br>\nЧем ты мудрее – больше вариантов.</br>', NULL, NULL, NULL),
(256, 5, 1, 'ДЕНЬГИ НЕСУТ НАСТРОЕНИЕ', NULL, 'Деньги несут настроение</br>\nТого, кто тебе их отдал.</br>\nВозникнет в душе упоение,</br>\nО добром, раз тот рассуждал.</br>\n</br>\nА если дают негативно,</br>\nОни негативность несут.</br>\nИ чувствуешь интуитивно,</br>\nОни твою душу трясут.</br>', NULL, NULL, NULL),
(257, 5, 1, 'К СОЧУВСТВИЮ ЗОВУТ', 'По изречению из Библии Пр. 21.13.)', 'От вопля бедного кто затыкает уши,</br>\nУслышан тот не будет, завопя.</br>\nК сочувствию взовите свои души,</br>\nЖивите милосердно и терпя.</br>', NULL, NULL, NULL),
(258, 5, 1, 'НАСЛЕДСТВО', NULL, 'Не деньги нужны потомкам,</br>\nИм нужен хороший характер.</br>\nНаследство сгинет в обломках</br>\nПри первом неправильном акте.</br>\n</br>\nВы правильно их обучите,</br>\nВы правильно их воспитайте,</br>\nИх сердце добром размягчите</br>\nИ это – наследством считайте.</br>', NULL, NULL, NULL),
(259, 5, 1, 'ДЕНЬ ЖЕ НАЧИНАЕТСЯ', 'По изречению Сенеки', 'Просящих часто оставляем без ответа,</br>\nСчитаем недостойными мы их.</br>\nВозможно, многие и недостойны света,</br>\nНо день же начинается для них.</br>', NULL, NULL, NULL),
(260, 5, 1, 'НЕ ХВАЛИТЕСЬ', NULL, 'Зря кому-то сильно голову вскружили,</br>\nПохвалившись тем, чего вы не достигли,</br>\nЧтоб преграду силы жизни не воздвигли,</br>\nНе хвалитесь даже тем, что заслужили.</br>', NULL, NULL, NULL),
(261, 5, 1, 'СЛОВО', NULL, 'Пред тем, как завершить, начав всё снова,</br>\nПрости, забудь мгновения коварности,</br>\nА напоследок раз решишь оставить слово,</br>\nПускай то будет слово благодарности.</br>', NULL, NULL, NULL),
(262, 5, 1, 'ПРАВИЛА СЧАСТЬЯ', NULL, 'Нелепо предписывать правила счастья</br>\nИ требовать их выполнять.</br>\nНасилие – это тиранства зачатье,</br>\nПора это людям понять.</br>', NULL, NULL, NULL),
(263, 5, 1, 'НЕ В МЕРУ', NULL, 'Не в меру</br>\nМы в мышлении хромаем.</br>\nСвоих поступков не осознаём.</br>\nК примеру:</br>\nКаплями советы принимаем</br>\nИ вёдрами советы раздаём.</br>', NULL, NULL, NULL),
(264, 5, 1, 'В ПОИСКАХ ОПРАВДАНЬЯ', NULL, 'Чтоб мыслей своих утвердить назиданья,</br>\nГотовы беседовать с кем-то часами,</br>\nМы просим совета, ища оправданья</br>\nРешеньям, которые приняли сами.</br>', NULL, NULL, NULL),
(265, 5, 1, 'ДЕНЬ ЖЕ НАЧИНАЕТСЯ', 'По изречению Сенеки', 'Просящих часто оставляем без ответа,</br>\nСчитаем недостойными мы их.</br>\nВозможно, многие и недостойны света,</br>\nНо день же начинается для них.</br>', NULL, NULL, NULL),
(266, 5, 1, 'НЕ ХВАЛИТЕСЬ', NULL, 'Зря кому-то сильно голову вскружили,</br>\nПохвалившись тем, чего вы не достигли,</br>\nЧтоб преграду силы жизни не воздвигли,</br>\nНе хвалитесь даже тем, что заслужили.</br>', NULL, NULL, NULL),
(267, 5, 1, 'СЛОВО', NULL, 'Пред тем, как завершить, начав всё снова,</br>\nПрости, забудь мгновения коварности,</br>\nА напоследок раз решишь оставить слово,</br>\nПускай то будет слово благодарности.</br>\n', NULL, NULL, NULL),
(268, 5, 1, 'ПРАВИЛА СЧАСТЬЯ', NULL, 'Нелепо предписывать правила счастья</br>\nИ требовать их выполнять.</br>\nНасилие – это тиранства зачатье,</br>\nПора это людям понять.</br>', NULL, NULL, NULL),
(269, 5, 1, 'НЕ В МЕРУ', NULL, 'Не в меру</br>\nМы в мышлении хромаем.</br>\nСвоих поступков не осознаём.</br>\nК примеру:</br>\nКаплями советы принимаем</br>\nИ вёдрами советы раздаём.</br>', NULL, NULL, NULL),
(270, 5, 1, 'В ПОИСКАХ ОПРАВДАНЬЯ', NULL, 'Чтоб мыслей своих утвердить назиданья,</br>\nГотовы беседовать с кем-то часами,</br>\nМы просим совета, ища оправданья</br>\nРешеньям, которые приняли сами.</br>', NULL, NULL, NULL),
(271, 5, 1, 'ЗАПРЕТ В СОВЕТЕ', 'По изречению Саади', 'Вложи в свои уста запрет,</br>\nВедь люди разные на свете.</br>\nУпрямцу, раз даёшь совет,</br>\nТы сам нуждаешься в совете.</br>', NULL, NULL, NULL),
(272, 5, 1, 'НАМ ЛЕГЧЕ', NULL, 'Нам жаль энергии потерь,</br>\nНам ближе поиск выгод.</br>\nНам легче указать на дверь,</br>\nЧем указать на выход.</br>', NULL, NULL, NULL),
(273, 5, 1, 'ОДИН ИЗ ТЫСЯЧИ', 'По изречению из Библии Сир. 6.6.', 'Пусть жизни длинная дорога,</br>\nЛюдей пусть рядом будет много,</br>\nСоветник твой – один из тысячи,</br>\nОдин из тысячи – от Бога.</br>\n', NULL, NULL, NULL),
(274, 5, 1, 'ОСОБЫЙ КОД', 'По восточному изречению', 'К душе твоей – особый Божий код,</br>\nЧужой совет чужого будет цвета.</br>\nВедь даже самый умный в мире кот,</br>\nМышонку умного не даст совета.</br>', NULL, NULL, NULL),
(275, 5, 1, 'НЕ СТАРАЙСЯ', NULL, 'Своим советом не старайся всех лечить,</br>\nСвоим советом не пытайся облегчить.</br>\nПусть даже сможешь дать совет разумный,</br>\nНо этим разуму не сможешь научить.</br>\n', NULL, NULL, NULL),
(276, 5, 1, 'КОГДА БЕДА НА МОРЕ', 'По финскому изречению', 'Глянь, умники все в сборе,</br>\nСоветы дают строго.</br>\nКогда беда на море,</br>\nНа суше умных много.</br>', NULL, NULL, NULL),
(277, 5, 1, 'ПРОЛОЖИСЬ МОСТОМ', NULL, 'Принакрылся одиночества зонтом,</br>\nЗагрустил обидчиво при том,</br>\nЧто всех лучше, остальные уж потом.</br>\n----------------------------------------------</br>\nКаждый человек – огромный том,</br>\nПрочитай и перекинься в мир мостом.</br>', NULL, NULL, NULL),
(278, 5, 1, 'НЕ ОБИЖАТЬ, А СПАСАТЬ', NULL, 'Зачем участвовать в безумной драке,</br>\nСужденьем, злобою мир этот сотрясать.</br>\nПоскольку здесь мы пребываем все во мраке,</br>\nНе обижать друг друга нужно, а спасать.</br>\n', NULL, NULL, NULL),
(279, 5, 1, 'ДАЖЕ БОГ', NULL, 'Ты не требуй отчёта,</br>\nНе суди, что убог,</br>\nНе гони за порог,</br>\nНе кричи, как ты мог,</br>\nРаз присутствие чьё-то,</br>\nТерпит здесь даже Бог.</br>', NULL, NULL, NULL),
(280, 5, 1, 'ЖДИ, ЧТО СКОРО', NULL, 'Кротость глаз и белизна в перчатках,</br>\nШик в манто, но есть особый штрих;</br>\nО чужих раз шепчет недостатках,</br>\nЖди, что скоро он зашепчет о твоих.</br>', NULL, NULL, NULL),
(281, 5, 1, 'ПРОСТИ И НЕ ПРОЩАЙ', NULL, 'Прости того, кто о тебе промолвил плохо,</br>\nНо сделал это явно сгоряча.</br>\nНо не прощай принёсшего волоха,</br>\nТу весть тебе, лукаво щебеча.</br>', NULL, NULL, NULL),
(282, 5, 1, 'НЕ ПРОСЛЫТЬ НАМ УМНЫМИ', NULL, 'Зря с репликами шумными</br>\nКлеймим мир ярлыками,</br>\nВедь не прослыть нам умными,</br>\nПрозвав всех дураками.</br>', NULL, NULL, NULL),
(283, 5, 1, 'ЗРЯ СЧИТАЕТ СЕБЯ ТОТ ПРАВДИВЫМ', NULL, 'Зря считает себя тот правдивым,</br>\nКто в речах своих склонен к злословью.</br>\nПусть злословит он с видом шутливым,</br>\nТолько зло с правдой в разных сословьях.</br>\n----------------------------------------------------</br>\nПравда – радостна взглядом гуманным,</br>\nСнисходительна и светла,</br>\nЗло – дочь гордости с ликом туманным.</br>\nПравда – светоч, злословие – мгла.</br>\n', NULL, NULL, NULL),
(284, 5, 1, 'ТОЛЬКО ЗАВИСТНИКИ И ГЛУПЦЫ', NULL, 'Не думай, что сплетня зловеща,</br>\nЧто хитрость и ложь навредят.</br>\nЗавистники только клевещут.</br>\nГлупцы только им подтвердят.</br>', NULL, NULL, NULL),
(285, 5, 1, 'РАЗ ТЫ ЗНАЧИМ', NULL, 'Раз ты значим, жить будешь в сражении,</br>\nК себе жалость сметёшь уважением,</br>\nКлевету одолеешь презрением,</br>\nСвою душу наполнишь прозрением.</br>', NULL, NULL, NULL),
(286, 5, 1, 'РАЗЛИЧАЮЩИЙ', 'По изречению из Библии Пр. 27.6.', 'Честна рана под ударом любящего,</br>\nПоцелуй недоброго коварен.</br>\nРазличающий живящего и рубящего,</br>\nДолжен Богу быть за это благодарен.</br>', NULL, NULL, NULL),
(287, 5, 1, 'ПОЛЕЗНЕЙ', 'По изречению Ж. де Лабрюйера', 'Приладится к чужому нраву</br>\nНам легче и полезней,</br>\nЧем на него искать управу</br>\nИ делать мир любезней.</br>', NULL, NULL, NULL),
(288, 5, 1, 'ЕДИНЫ – РЕЛИГИЯ, ЯЗЫК, РАСА', 'По изречению В. Рузова', 'Религия только одна есть на свете.</br>\nК любви ей откроется дверца.</br>\nЭто – религия сердца.</br>\nРаса одна есть на этой планете.</br>\nМы все одного окраса.</br>\nЭто – людская раса.</br>\nЯзык наш един в языковом букете.</br>\nС ним ты живи и дыши.</br>\nЭто – язык души.</br>', NULL, NULL, NULL),
(289, 5, 1, 'СОБСТВЕННЫЕ ТВОРЕНИЯ', 'По изречению из Талмуда', 'Все дела мои, точки зрения –</br>\nМои собственные творения.</br>\nМои недруги и друзья –</br>\nЭто то, что себе создал я.</br>', NULL, NULL, NULL),
(290, 5, 1, 'ГЛУПОСТЬ И УМ', 'По изречению Г. Матюшова', 'Часто ходит грубость</br>\nВ гости наобум,</br>\nПоднимая шум.</br>\n------------------------------</br>\nДрузей ссорит глупость,</br>\nВрагов мирит ум.</br>', NULL, NULL, NULL),
(291, 5, 1, 'ПУТЬ', NULL, 'Сочувствие и понимание –</br>\nТвой к людям проложенный путь.</br>\nЗа чуткость души и внимание</br>\nМир хочет взаимность вернуть.</br>\nЧрез путь, что ты смог протянуть,</br>\nПридут к тебе те же послания.</br>', NULL, NULL, NULL),
(292, 5, 1, 'ОДНА МИНУТА ТЕРПЕНИЯ', 'По античному изречению', 'Вот тайна счастья эликсира,</br>\nВот тропка райского маршрута:</br>\nТерпения одна минута</br>\nВ себе содержит годы мира.</br>\n', NULL, NULL, NULL),
(293, 5, 1, 'ДЛЯ СЕБЯ', NULL, 'И что же оттого?</br>\nЧто ты кричишь грубя,</br>\nПоддавшись весь смутьянству.</br>\nНе сколько для него,</br>\nА сколько для себя,</br>\nТы уступи упрямству.</br>\n', NULL, NULL, NULL),
(294, 5, 1, 'БУДНИЧНАЯ СУББОТА', 'По изречению из Талмуда', 'Живите по жизни, мужаясь,</br>\nПусть будничной будет суббота,</br>\nПусть лучше работать до пота,</br>\nЧем помощь просить унижаясь.</br>', NULL, NULL, NULL),
(295, 5, 1, 'НЕ ВАЛИСЬ', NULL, 'Ты можешь согревать других и так же греться,</br>\nТакими мы уж просто родились.</br>\nТы можешь в жизни на кого-то опереться,</br>\nНо тяжестью своей всей не вались.</br>', NULL, NULL, NULL),
(296, 5, 1, 'ТВОЙ ДРУГ', 'По изречению из Библии Сир. 6.14\n', 'То, как стена гранита,</br>\nТо, как броня кольчуг:</br>\nНадёжная защита –</br>\nТвой верный лучший друг.</br>', NULL, NULL, NULL),
(297, 5, 1, 'НА ДРУЗЕЙ НЕ ОБИЖАЮТСЯ', NULL, 'Когда друзья во истину сближаются,</br>\nТо, что в них есть, должны они принять.</br>\nВедь на друзей своих не обижаются,</br>\nИх понимают иль стараются понять.</br>\n', NULL, NULL, NULL),
(298, 5, 1, 'ТВОРЕЦ СУДЬБЫ ИЛИ ЕЁ ТВОРЕНИЕ', 'По изречению Оноре де Бальзака', 'Прими!</br>\nИль трус – ты, или ты – борец,</br>\nИль слепость, иль прозрение.</br>\nПойми!</br>\nОдин – своей судьбы творец,</br>\nДругой – её творение.</br>', NULL, NULL, NULL),
(299, 5, 1, 'КОГДА ХОЧЕШЬ', NULL, 'Быть вспыльчивым совсем не модно,</br>\nВойну начнёшь, когда угодно.</br>\nЗнай, только тем беду пророчишь.</br>\nВойну ж не кончишь, когда хочешь.</br>', NULL, NULL, NULL),
(300, 5, 1, 'КТО РОЕТ ЯМУ', 'По изречению из Библии Сир. 27. 29.\n', 'Пусть это к размышленью приведёт.</br>\nПусть это нас мудрее стать побудит:</br>\nКто роет яму, тот в неё же упадёт.</br>\nКто сеть расставит, ей уловлен будет.</br>', NULL, NULL, NULL),
(301, 5, 1, 'НЕ ОСТАВЛЯЙ СТРЕМЛЕНИЯ', NULL, 'Жизнь, когда ты оставишь стремление,</br>\nВызывает к себе сожаление.</br>\nИзучай, познавай явления,</br>\nПостигай, одолев сомнения.</br>', NULL, NULL, NULL),
(302, 5, 1, 'СТАРАЙСЯ', NULL, 'Раз кто-то стремиться не в меру</br>\nВ тебя подорвать твою веру,</br>\nСтарайся его избегать.</br>\nРаз кто-то внушает чувство,</br>\nЧто ты есть творец искусства,</br>\nСтарайся того достигать.</br>', NULL, NULL, NULL),
(303, 5, 1, 'СПОРИТЬ ГОРАЗДО ЛЕГЧЕ', 'По изречению Г. Флобера', 'Бывает, мы любим при встрече</br>\nУмом гордым всех донимать,</br>\nНо спорить гораздо же легче,</br>\nЧем просто людей понимать.</br>\n', NULL, NULL, NULL),
(304, 5, 1, 'НЕ УМОМ, ДОВЕРИЕМ', NULL, 'Доброты преддверием</br>\nРаз хочешь стать соседу,</br>\nНе умом, доверием,</br>\nС ним оживи беседу.</br>', NULL, NULL, NULL),
(305, 5, 1, 'ИЩИ ЛЮДЕЙ И КНИГИ', 'По изречению П. Буаста', 'Ищи вокруг себя людей,</br>\nС кем разговор достоин умной книги чтенья,</br>\nА книгою такой владей,</br>\nВ кой сам философ оказал тебе почтенье.</br>\n', NULL, NULL, NULL),
(306, 5, 1, 'СТЫД ЕМУ', 'По изречению из Библии Пр. 18. 14.\n', 'Тот – плод даёт, тот – пустоцвет,</br>\nТот сеет свет, тот – тьму.</br>\nНе выслушав, кто дал совет,</br>\nТот глуп и стыд ему.</br>', NULL, NULL, NULL),
(307, 5, 1, 'ПУСТЬ КРАСИВА РЕЧЬ', NULL, 'С болтуном не пререкайся,</br>\nПусть красива речь.</br>\nВ речи этой не плескайся,</br>\nРаз ума в ней течь.</br>\n--------------------------------</br>\nВременем уплатишь.</br>\nЗря себя растратишь.</br>\n', NULL, NULL, NULL),
(308, 5, 1, 'ДРУГ И СОБЕСЕДНИК', NULL, 'Кто с вами о других болтает – это сплетник,</br>\nТаков опасен друг и плох советник.</br>\n------------------------------------------------</br>\nКто с вами о себе глаголет – тот зануда,</br>\nС таким дружком по жизни будет худо.</br>\n-----------------------------------------------------</br>\nКто с вами долго говорит о вас –</br>\nХороший друг и собеседник без прикрас.</br>', NULL, NULL, NULL),
(309, 5, 1, 'НЕПРИЛИЧНО ГОВОРИТЬ', 'По изречению Исократа', 'Что, в общем, не завидно,</br>\nТо нужно бы из жизни удалить.</br>\nЧто делать нам постыдно,</br>\nО том и неприлично говорить.</br>', NULL, NULL, NULL),
(310, 5, 1, 'НЕ БРОСАЙТЕ СВЯТЫНИ', 'По изречению из Библии Мф. 7. 9.', 'Псам не бросайте под ноги святыни,</br>\nСвиней не осыпайте жемчугами.</br>\nОкажутся усердия пустыми:</br>\nДары чудесные поправ ногами,</br>\nТе растерзают, оплюют брюзгами</br>\nИ обратятся против вас врагами.</br>\n', NULL, NULL, NULL),
(311, 5, 1, 'КАМНЕМ ПО ГРЯЗИ', NULL, 'К разумности если склоняешься,</br>\nНе спорь с дураком, уравняешься.</br>\nНе будет искры в вашей связи,</br>\nЧто толку бить камнем по грязи.</br>', NULL, NULL, NULL),
(312, 5, 1, 'В СПОРАХ ЗАБЫВАЕТСЯ ИСТИНА', 'По изречению Л. Толстого', 'Раз спорим, то спорим всегда до упора,</br>\nСчитая обычно, что путь тот вернейший.</br>\nНо, жаль, забывается истина в спорах,</br>\nЧто спорить и спор прекращает умнейший.</br>\n', NULL, NULL, NULL),
(313, 5, 1, 'УЛОВ В МАЛОМ', NULL, 'Снова видим хитрость с безрадостным финалом,</br>\nПусть и в шляпе хитрость, только голышом.</br>\nХитрость – это склонность снять улов, но в малом,</br>\nТолько будет после проигрыш в большом.</br>', NULL, NULL, NULL),
(314, 5, 1, 'ДВОЙНАЯ ЖИЗНЬ', NULL, 'Не будет сладкий сон нарушен,</br>\nРаз искренен и прямодушен.</br>\nА кто двойную жизнь ведёт,</br>\nТех одиночество крадёт.</br>', NULL, NULL, NULL),
(315, 5, 1, 'НУЖНО ТАКИМ БЫТЬ', NULL, 'Всегда подражаем зачем-то,</br>\nЗа славой чужой любим плыть.</br>\nНо, мало казаться нам кем-то,</br>\nВедь, нужно таким ещё быть.</br>', NULL, NULL, NULL),
(316, 5, 1, 'НЕ ГОВОРИТЕ ВСЛУХ', NULL, 'Чтоб не сидеть в чужих капканах,</br>\nЗастрявши в нагнанных туманах,</br>\nНе говорите вслух о планах.</br>\nРаз планы ваши велики,</br>\nА люди рядышком мелки.</br>', NULL, NULL, NULL),
(317, 5, 1, 'МЕРА УЧТИВОСТИ', 'По ирландскому изречению', 'Хорошо, когда внутри степенность;</br>\nТам, где вежлив ты, оставь надменность.</br>\nНи к чему в манерах наших хвора;</br>\nГде настойчив ты, там не должно быть спора.</br>\nМил характер, что без перегрева;</br>\nТам, где твёрд ты, не должно быть гнева.</br>', NULL, NULL, NULL),
(318, 5, 1, 'ИСКУССТВО НРАВИТЬСЯ', 'По изречению из Библии Сир. 13.13.', 'Будь мил, чтоб на тебя взглянули,</br>\nБудь весел, чтоб с тобой не приуныли,</br>\nБудь ненавязчив, чтоб не оттолкнули,</br>\nНе отдаляйся, чтоб не позабыли.</br>', NULL, NULL, NULL),
(319, 5, 1, 'В ГОСТИ', NULL, 'Позабыв все огорченья дома,</br>\nИ оставив все заботы взаперти,</br>\nПосле молнии, дождя и грома,</br>\nТолько можно в гости лишь идти.</br>', NULL, NULL, NULL),
(320, 5, 1, 'КАК НЕ НАДОЕСТЬ', NULL, 'Как никому не надоесть?</br>\nВ тебе ответ той тайны есть:</br>\nСебе приятным быть умей!</br>\nСебе надоедать не смей.</br>', NULL, NULL, NULL),
(321, 5, 1, 'ДЕЛИКАТНОСТЬ', NULL, 'Кто деликатен, тот порядочен и гибок,</br>\nНе будет возмущаться и бренчать.</br>\nТерпимость – то прощение ошибок,</br>\nТактичность – их совсем не замечать.</br>', NULL, NULL, NULL),
(322, 5, 1, 'НЕ НУЖНО РАСТВОРЯТЬСЯ', 'По изречению В. Зверева', 'Полезно влиться в коллектив,</br>\nНо в нём не нужно растворяться.</br>\nВсе чувства мира ощутив,</br>\nМы в мире не должны теряться.</br>', NULL, NULL, NULL),
(323, 5, 1, 'ЛЮДИ НЕ ПРОТИВ ТЕБЯ', 'По изречению Д. Фаулера', 'В людях мало мыслей о тебе,</br>\nВсякий раз их мысли о себе.</br>\nИ не думай, будто против все тебя.</br>\nВ большинстве своём все за самих себя.</br>', NULL, NULL, NULL),
(324, 5, 1, 'СТАТЬ ЛИЧНОСТЬЮ', 'По изречению В. Фишера\n', 'Вас сможет масса растоптать</br>\nСвоей огромною стопою.</br>\nСтремитесь личностью вы стать</br>\nИ не стремитесь стать толпою.</br>', NULL, NULL, NULL),
(325, 5, 1, 'ЧИСТОЕ БОГАТСТВО', 'По изречению В. О. Рузова', 'Богатство чистое хранится только тратой</br>\nНа нужные и добрые дела.</br>\nКак и вода в ключе не станет мутноватой,</br>\nРаз застоявшаяся, дальше потекла.</br>', NULL, NULL, NULL),
(326, 5, 1, 'ОБЕЩАНИЕ', 'По изречению из Библии Мф. 20.16.', 'Всякий раз стремись себе напомнить:</br>\nЧто сказал, то нужно воплощать.</br>\nЛучше ничего не обещать,</br>\nЧем, пообещавши, не исполнить.</br>', NULL, NULL, NULL),
(327, 5, 1, 'ЗА ЧТО УГОДНО', 'По изречению Е. Леца', 'Чтоб не стало дело бы бесплодно,</br>\nИзучи состав, что привлечён.</br>\nКто не разбирается ни в чём,</br>\nМожет взяться и за что угодно.</br>', NULL, NULL, NULL),
(328, 5, 1, 'О ЛОПАТЕ', NULL, 'Одной лопатой кто владеет,</br>\nРешит проблемы так твои:</br>\nПод трёхметровые слои</br>\nИх закопает и вспотеет.</br>\nИ каждый день его пои,</br>\nА не напоишь, он сумеет,</br>\nПроблемы раскопать твои.</br>', NULL, NULL, NULL),
(329, 5, 1, 'ЧТО МОГУТ ДАТЬ', NULL, 'Кем недоволен, с тем не будь ворчливым.</br>\nВсё на людей не нужно взгромождать.</br>\nОбщаясь с ними, чтобы стать счастливым,</br>\nПроси лишь их о том, что могут дать.</br>\n', NULL, NULL, NULL),
(330, 5, 1, 'ЧТОБ НЕ ПРОГЛОТИЛИ', 'По восточному изречению', 'Не будь так мягок, чтоб не надкусили.</br>\nНе будь так твёрд, не подавился кто б.</br>\nНе будь так сладок, чтоб не проглотили.</br>\nНе будь так горек и не выплюнули чтоб.</br>', NULL, NULL, NULL),
(331, 5, 1, 'СИНДРОМ ЛОДКИ', NULL, 'Жизнь двигаем своей рукой,</br>\nНо думаем, нависши над рекой,</br>\nДостигли цели, можно – на покой.</br>\n------------------------------------------</br>\nТут наступает лодочный синдром.</br>\nМы вёсла опуская, вплавь ребром,</br>\nНе действуем и больше не гребём.</br>\nНесёт назад не в тот нас водоём.</br>\nВсё происходит сразу невпопад,</br>\nВозможно, даже будет водопад,</br>\nИ жизнь оставив, попадём мы в ад.</br>\n--------------------------------------------------</br>\nПрисущ подъём нам в жизни, а не спад.</br>\n--------------------------------------------------</br>', NULL, NULL, NULL),
(332, 5, 1, 'НЕКТАР', 'По изречению В. О. Рузова «Нектарология»', 'Как осудим в отношеньях панибратство,</br>\nУкротим в себе и зависть, и злорадство.</br>\nКак забудем перебранных ссор кошмар,</br>\nПревратятся отношения в нектар.</br>\n', NULL, NULL, NULL),
(333, 5, 1, 'СОЗНАНЬЕ НЕ ДЕРЖИТЕ ВЗАПЕРТИ', NULL, 'Сознанье не держите взаперти.</br>\nК наследью с новшеством не будьте строги.</br>\nХраня в порядке старые дороги,</br>\nИщите в жизни новые пути.</br>', NULL, NULL, NULL),
(334, 5, 1, 'НЕЛЬЗЯ', NULL, 'Того, чей талант особен,</br>\nНельзя нам за что-то судить.</br>\nВедь тот, кто летать способен,</br>\nКак все, не умеет ходить.</br>', NULL, NULL, NULL),
(335, 5, 1, 'ВЫСОКОМЕРИЕ СУЖДЕНИЙ', NULL, 'Кто хвалит, кто без снисхождений</br>\nК трудам, проделанным гигантом.</br>\nВысокомерием суждений</br>\nНепризнанные мстят талантам.</br>\n', NULL, NULL, NULL),
(336, 5, 1, 'ИМЯ ДОБРОЕ – ВОВЕК', 'По изречению из Библии Сир. 41.16.', 'Чтоб всё, что сделал, проросло,</br>\nСтань человечным, человек.</br>\nДням доброй жизни есть число,</br>\nНо имя доброе – вовек.</br>', NULL, NULL, NULL),
(337, 5, 1, 'В ЧИСТОТЕ И БЕССТРАСТЬЕ', NULL, 'Не пользуйся в деле всеобщим укладом,</br>\nТропы не протопчет идущий за стадом.</br>\nНе рвись сквозь толпу, чтоб поймать своё счастье.</br>\nСвой путь проложи в чистоте и бесстрастье.</br>', NULL, NULL, NULL),
(338, 5, 1, 'КТО ДОСТОИН', NULL, 'Не покрыть имя доброе инеем,</br>\nНе сокрыть дел святых и благих.</br>\nНе стремится за славой и именем,</br>\nКто достоин действительно их.</br>\n', NULL, NULL, NULL),
(339, 5, 1, 'СМЕШНО ЖЕЛАНИЕ ВСЕМ НРАВИТЬСЯ', NULL, 'Смешно желание всем нравиться вокруг.</br>\nС высоким званием расширится твой круг,</br>\nНо помни, в нём не каждый будет друг.</br>\nДурак и умный любят разным дорожить.</br>\nВ великом деле похвалу не заслужить.</br>\n', NULL, NULL, NULL),
(340, 5, 1, 'ВЗАИМНАЯ ТЕРПИМОСТЬ', NULL, 'Мы с разным опытом и разными умами.</br>\nМы видим истину под разными углами.</br>\nВ непонимание того и есть все беды.</br>\n----------------------------------------------------</br>\nВзаимная терпимость – наше кредо.</br>\n', NULL, NULL, NULL),
(341, 5, 1, 'ЗАМКНУТЫЙ КРУГ ИЛИ ТУПИК', NULL, 'Гнёт убеждения раздут, предавши суть недугу.</br>\nПрямолинейные идут по замкнутому кругу.</br>\nЗря наши принципы порой упрямы и так строги,</br>\nБескомпромиссные в тупик все превратят дороги.</br>\n', NULL, NULL, NULL),
(342, 5, 1, 'ОСИЛИТ ПУТЬ ИДУЩИЙ', 'По древнеиндийскому изречению', 'Не нужно обвинять</br>\nПрекрасный день грядущий,</br>\nА нужно бы понять,</br>\nОсилит путь идущий.</br>', NULL, NULL, NULL),
(343, 5, 1, 'ИНСТРУМЕНТ ЖИЗНИ', NULL, 'Чтоб струны возник волшебный звук,</br>\nБольше – меньше натянуть её нельзя.</br>\nЧтобы не было в общенье горьких мук,</br>\nНе клонись и не живи дерзя.</br>\n-------------------------------------------------</br>\nНе стремись, чтоб чувства стали вздорны.</br>\nТо как пламень жечь, хладить, как льдина.</br>\nПерекосы так не плодотворны.</br>\nЛучше – золотая середина.</br>', NULL, NULL, NULL),
(344, 5, 1, 'ПРАВДУ ПОДАВАЙ ПРИЯТНО', NULL, 'Потрудился правдою помочь,</br>\nНо обиделись за что-то непонятно.</br>\nСвято правило в своём уму упрочь –</br>\nПравду подавай всегда приятно.</br>\n</br>\nНе используй в речи приниженье,</br>\nЧто пошлёшь, получишь то в ответ.</br>\nЕсли мил, тому и уваженье</br>\nЗа подсказку, правду и совет.</br>', NULL, NULL, NULL),
(345, 5, 1, 'ТОЛЬКО ВРЕД', NULL, 'Добру отдайся на поруки,</br>\nИ раз умён, оставишь след.</br>\nКто не постиг добра науки,</br>\nТому от знаний только вред.</br>', NULL, NULL, NULL),
(346, 5, 1, 'НАЙТИ СЕБЯ', NULL, 'Без мечты и идей разум сонен и тих.</br>\nПотерять смысл – то грех во плоти.</br>\nСреди мусора мыслей и взглядов чужих</br>\nПостарайся себя ты найти.</br>', NULL, NULL, NULL),
(347, 5, 1, 'БЕЗГРАНИЧНО', NULL, 'Не проживайте жизнь бездельно,</br>\nА проживайте жизнь ритмично.</br>\nНезнанье наше беспредельно,</br>\nПознанье наше безгранично.</br>\n', NULL, NULL, NULL),
(348, 5, 1, 'МНОЖИТСЯ', NULL, 'В учение полезно водвориться,</br>\nТерпение, приняв в своё избранье.</br>\nУчил, что наспех, быстро испарится.</br>\nТрудом усвоенное множится познанье.</br>', NULL, NULL, NULL),
(349, 5, 1, 'ИХ МЕСТО', NULL, 'Только с мудростью живи всегда в родстве,</br>\nНе меси в мозгу банальных фактов тесто.</br>\nНе держи всю эту мелочь в голове,</br>\nВ книгах справочных находится их место.</br>', NULL, NULL, NULL),
(350, 5, 1, 'ПАГУБНО СТРЕМЛЕНЬЕ', NULL, 'Всё познать о мире не гонись,</br>\nУстрани дурную эту манию.</br>\nК многомыслию ума стремись,</br>\nПагубно стремленье к многознанию.</br>', NULL, NULL, NULL),
(351, 5, 1, 'В ЕДИНОЙ ТОЧКЕ', NULL, 'Вмещая всё, становишься ты глум.</br>\nЗабродят мысли, будто в винной бочке.</br>\nНе расширяй, а углубляй свой ум,</br>\nТепло лучей собрав в единой точке.</br>', NULL, NULL, NULL),
(352, 5, 1, 'ПО ШАБЛОНУ', NULL, 'Не учи всех вокруг по шаблону.</br>\nЗнаний разный у всех ореол.</br>\nВ каждой стае свой уровень школ:</br>\nУ людей, и у птиц, и у пчёл.</br>\nОбучая полётам ворону,</br>\nТолько время теряет орёл.</br>', NULL, NULL, NULL),
(353, 5, 1, 'ВСЁ В НАШИХ РУКАХ', 'По изречению Коко Шанель', 'Сложилось, что воля и сила в веках</br>\nУмели судьбу свою ткать.</br>\nИ нужно поверить – всё в наших руках.</br>\nНе стоит нам их опускать.</br>\n', NULL, NULL, NULL),
(354, 5, 1, 'БУДЕТ НАГРАЖДЁН', NULL, 'Если кто от души потрудился,</br>\nТо тогда может быть убеждён,</br>\nРано, поздно, раз пользы добился,</br>\nВсё равно будет он награждён.</br>\n\n', NULL, NULL, NULL),
(355, 5, 1, 'СОВСЕМ НЕ ЗАМЕЧАТЬ', 'По изречению С. Янковского', 'Препятствие, чтоб в жизни одолеть,</br>\nНеобязательно подробно изучать.</br>\nПорой полезней, чтобы уцелеть,</br>\nПрепятствие совсем не замечать.</br>', NULL, NULL, NULL),
(356, 5, 1, 'ГНИЕНЬЕ И ГОРЕНЬЕ', 'По изречению М. Горького', 'Мы за свою судьбу в ответе.</br>\nО ней различны прения.</br>\nДве формы жизни есть на свете:</br>\nГниенья и горения.</br>', NULL, NULL, NULL),
(357, 5, 1, 'ГЛАВНЫЙ ДОЛГ', NULL, 'Наш главный долг – забота.</br>\nОн призывает нас к труду.</br>\nГоните дремоту к стыду.</br>\nВозьмите праздность за узду.</br>\nВся жизнь есть действий нота.</br>\nПороки, скуку и нужду</br>\nВ нас истребит работа.</br>', NULL, NULL, NULL),
(358, 5, 1, 'СЧАСТЛИВЕЕ ВСЕХ', 'По изречению Г. Матюшова', 'Кому-то работа для жизни нужна,</br>\nНу что ж, не осудим мы тех.</br>\nНо тот, кому жизнь для работы важна,</br>\nСредь нас всё ж счастливее всех.</br>', NULL, NULL, NULL),
(359, 5, 1, 'СТЫДНО', 'По изречению из Талмуда', 'Стыдно слышать во вслед:</br>\nМуравью во труде подражай!</br>\nА не примешь совет,</br>\nБесконечно себя устыжай.</br>', NULL, NULL, NULL),
(360, 5, 1, 'ЛУЧШЕ ПОСИДИТЕ', NULL, 'Лучше посиди, сложивши руки,</br>\nЧем работай, опустивши рукава.</br>\nЛучше, дела неисполненного муки,</br>\nЧем испорченного дела похвала.</br>\n', NULL, NULL, NULL),
(361, 5, 1, 'УМЕЙ ПОМОЛЧАТЬ', 'По изречению Штритматтера', 'Нельзя всё речью вышить,</br>\nИ зря она порой звучит свербя,</br>\nСудьбу твою тем начиная удручать.</br>\nКогда желаешь слышать,</br>\nА что же жизнь так хочет от тебя,</br>\nХоть иногда умей немного помолчать.</br>', NULL, NULL, NULL),
(362, 5, 1, 'ПЛЕТЕНЬ ТЕРНОВЫЙ', 'По изречению из Библии Прит. 15. 19.', 'Кто ленив, тому и куш грошовый,</br>\nКто трудолюбив, тому – успех.</br>\nПуть ленивого – плетень терновый.</br>\nПраведного путь, как гладкий мех.</br>', NULL, NULL, NULL),
(363, 5, 1, 'ЛЕНОСТЬ И РАБОТА', 'По изречению Б. Франклина', 'Труд всё явит близким, чудным,</br>\nЛень же, злым, далёким.</br>\nЛеность делает всё трудным,</br>\nА работа лёгким.</br>', NULL, NULL, NULL),
(364, 5, 1, 'ТАЛАНТ БЕЗ ТРУДА НЕ РАСЦВЕТЁТ', NULL, 'Талант – не фейерверк, что, ослепив, спадёт.</br>\nТалант – умение труда воздвигнуть своды.</br>\nПусть без таланта труд не явит миру всходы,</br>\nНо без труда талант никак не расцветёт.</br>', NULL, NULL, NULL),
(365, 5, 1, 'НЕ СЕБЕ В УГОДУ', 'По изречению Л. Фейхтвангера', 'Талант раз дан, то с ним твори умело,</br>\nТвори для мира, не своей угоды.</br>\nВедь не должно источнику быть дела,</br>\nКуда, зачем несутся его воды.</br>', NULL, NULL, NULL),
(366, 5, 1, 'СДЕЛАТЬ ХОРОШО', NULL, 'Чтоб жизнь твоя с годами не тускнела,</br>\nЧтоб не прошёл о жизни той смешок,</br>\nЗапомни, если нужно что-то сделать,</br>\nТо сделать это нужно хорошо.</br>', NULL, NULL, NULL),
(367, 5, 1, 'ТВОРЮ ВСЕЛЕННУЮ СВОЮ', NULL, 'Я из чувств и мыслей состою.</br>\nИми управлять настал черёд.</br>\nС благодарностью, идя вперёд,</br>\nЯ творю Вселенную свою.</br>', NULL, NULL, NULL),
(368, 5, 1, 'УСТА РАЗУМНЫЕ', 'По изречению из Библии Пр. 20.15.', 'Есть много жемчуга и золота на свете,</br>\nНо драгоценна утварь чуть иная.</br>\nЗаписано в божественном завете:</br>\nУста разумные – вот ценность основная.</br>', NULL, NULL, NULL),
(369, 5, 1, 'А ЧТО ВЫЙДЕТ ИЗ УСТ', 'По изречению из Библии Мф. 15.11.', 'Средь людей ходит притча от века до века:</br>\nВ выраженьях своих не хромай как калека,</br>\nБудешь очень страдать оттого.</br>\nНе что входит в уста осквернит человека,</br>\nА что выйдет из уст, оскверняет его.</br>', NULL, NULL, NULL),
(370, 5, 1, 'ЗНАЙ ВСЕГДА', NULL, 'Зря словами ты душу сминаешь,</br>\nЗря напрасно кому-то грубишь:</br>\nНе всегда говори то, что знаешь,</br>\nТолько знай всегда, что говоришь.</br>', NULL, NULL, NULL),
(371, 5, 1, 'ЗНАЕТ ВРЕМЯ', 'По изречению из Библии Сир. 20.6.', 'Иной молчит лишь потому,</br>\nЧто не имеет, что ответить.</br>\nДругой молчит, сумей заметить,</br>\nЧто время знает он всему.</br>\n', NULL, NULL, NULL),
(372, 5, 1, 'ПЕРВЫЙ ШАГ', NULL, 'Весь свой путь увидеть невозможно,</br>\nОттого внутри чуть-чуть тревожно.</br>\nНо нажавши действия рычаг,</br>\nВеря в то, что всё не так уж сложно,</br>\nК светлой цели сделай первый шаг.</br>\n', NULL, NULL, NULL),
(373, 5, 1, 'ДЛЯ ВСЕЛЕННОЙ', NULL, 'Это мы в сети примеров,</br>\nДля Вселенной правил нет,</br>\nДля Вселенной нет размеров:</br>\nКилограммов, метров, лет.</br>\n</br>\nЧтобы быстро и приятно</br>\nНам к мечте заветной плыть,</br>\nНужно, это всем понятно,</br>\nСо Вселенной в мире быть.</br>', NULL, NULL, NULL),
(374, 5, 1, 'РЕЗУЛЬТАТ', NULL, 'Знай, будущее мысль твоя возводит;</br>\nНесчастлив, счастлив, беден, иль богат.</br>\nА что с тобой сегодня происходит,</br>\nТо, твоих прошлых мыслей результат.</br>', NULL, NULL, NULL),
(375, 5, 1, 'ВООБРАЖЕНЬЕ', 'По изречению А. Эйнштейна', 'Мысль твоя – реальное движение,</br>\nМысль твоя – твой жизненный заказ.</br>\nВсеобъемлюще твоё воображение –</br>\nЭто предварительный показ</br>\nТех событий, что начнут вторжение</br>\nВ твой грядущий день, грядущий час.</br>', NULL, NULL, NULL),
(376, 5, 1, 'ДОСТОЙНОЕ МЕСТО', NULL, 'Для каждого, только в своём ремесле,</br>\nДостойное место припрятано миром.</br>\nНайди себя там, где б ты стал ювелиром,</br>\nЧтоб жить полной жизнью на этой земле.</br>', NULL, NULL, NULL),
(377, 5, 1, 'ТОЧКА ЗРЕНИЯ ДРУГОГО', NULL, 'Уменье понять точку зренья другого –</br>\nНадёжный успех и в делах эталон.</br>\nТо мудрое знанье даёт очень много:</br>\nТы смотришь на вещи и сам, и как он.</br>', NULL, NULL, NULL),
(378, 5, 1, 'ДУХОВНЫЕ ЗНАНИЯ', NULL, 'Нет привилегии на знания духовные,</br>\nДля чистых душ – бесценные богатства!</br>\nИ притязать на то, воздействия греховные.</br>\nИх пользователь – совладелец братства!</br>\n', NULL, NULL, NULL),
(379, 5, 1, 'ТЫ – КАПЛЯ', NULL, 'Отбрось всю значимость,</br>\nОставь весь груз интриг,</br>\nОткинь всю важность,</br>\nСбавь свой гнев вулкана,</br>\nВнутри себя очистись от обмана.</br>\nВедь счастлив тот,</br>\nКто истину постиг,</br>\nЧто капля – он,</br>\nВзлетевшая на миг</br>\nИз бездны мирового океана.</br>', NULL, NULL, NULL),
(380, 5, 1, 'МУДРОСТЬ', NULL, 'Летят веками годы.</br>\nЦветёт наука в страже.</br>\nНо, как по кругу воды,</br>\nТак мудрость снова та же.</br>', NULL, NULL, NULL),
(381, 5, 1, 'ПОДЧЕРКНИ ИНДИВИДУАЛЬНОСТЬ', NULL, 'Не общепринятая красота</br>\nУ нас оставит впечатленье.</br>\nВ ней есть стандарт и преснота,</br>\nЕё признанье – заблужденье.</br>\n</br>\nЗато удачно подчеркнув</br>\nСвой индивид, неповторимость,</br>\nЛюбой вздохнёт, на вас взглянув,</br>\nПризнав, что вы – неотразимость.</br>', NULL, NULL, NULL),
(382, 5, 1, 'ЗАКОН ТЯГОТЕНИЯ', NULL, 'О чём мечтаешь ты, к тебе то тяготеет –</br>\nТак Эмерсон сказал: «И нужный звук,</br>\nРодившись на земле, всегда, поверь, сумеет</br>\nПридти из дали на твой сердца стук».</br>', NULL, NULL, NULL),
(383, 5, 1, 'В МЕРУ ОСОЗНАННОСТИ', NULL, 'Осознанности нашей в меру</br>\nСвобода нам дана и власть.</br>\nЖеланье быть должны и вера</br>\nИ что хотим, получим всласть.</br>\n', NULL, NULL, NULL),
(384, 5, 1, 'СУТЬ ЯВЛЕНИЙ', NULL, 'Познавай, очищайся и верь!</br>\nВ суть явлений откроется дверь:</br>\nС волей твёрдой, как гордая сталь,</br>\nС духом чистым, как горный хрусталь.</br>\n', NULL, NULL, NULL),
(385, 5, 1, 'ИСТИНА РЯДОМ', NULL, 'Кто ищет истину вокруг себя,</br>\nНапрасно мечется кругами,</br>\nНе замечает, как ее губя,</br>\nНещадно топчет под ногами.</br>\n', NULL, NULL, NULL),
(386, 5, 1, 'НЕ ПЛОШАЙ', NULL, 'По жизни – и смейся,</br>\nИ мудрость вкушай:</br>\n«На Бога надейся,</br>\nНо сам не плошай»</br>', NULL, NULL, NULL),
(387, 5, 1, 'ЧУВСТВА', NULL, 'Нам чувства разные даны:</br>\nВзгляни на бездну вышины!</br>\nВдохни прохладу глубины!</br>\nПослушай голос тишины!</br>\n', NULL, NULL, NULL),
(388, 5, 1, 'ТО СВЕТ И ВЕРНЁТ', 'По изречению из Библии Ос. 8. 7.', 'Что сделал на свете, то свет и вернёт.</br>\nПосеял кто ветер, тот бурю пожнёт.</br>\n', NULL, NULL, NULL),
(389, 5, 1, 'НЕ ДАЮТ ЖИТЬ', 'По изречению Л. Земскова', 'К нам приставать не устают</br>\nИ комары, и блохи.</br>\nНам хорошо жить не дают</br>\nВсе те, кому жить плохо.</br>\n', NULL, NULL, NULL),
(390, 5, 1, 'НЕ УМСТВУЙ', NULL, 'Кто мудр, устам своим пошлёт и немоту.</br>\nНе умствуй там, где мудрость видит простоту.</br>', NULL, NULL, NULL),
(391, 5, 1, 'ОТКРЫТИЕ ИСТИН', NULL, 'Собрав воедино все лучики солнца,</br>\nСквозь линзу огонь возгоришь.</br>\nТак ум, фокусируя весь до донца,</br>\nОткрытие истин творишь.</br>', NULL, NULL, NULL),
(392, 5, 1, 'СЧАСТЬЕ ПОБЕДЫ', NULL, 'Поправят друг друга сомненье и вера,</br>\nПрибавится знания мера!</br>\nПритрутся друг к другу радости-беды,</br>\nЯвится счастье победы!</br>', NULL, NULL, NULL),
(393, 5, 1, 'ДОЛЬШЕ НЕ РАССКАЗЫВАЙ', NULL, 'Себе порой отказывай, пускай, в душе скребя,</br>\nНо дольше не рассказывай, чем слушают тебя.</br>', NULL, NULL, NULL),
(394, 5, 1, 'МИР МЫСЛИ', NULL, 'Мир мысли мой!</br>\nЯ его создатель.</br>\nМир мысли мой!</br>\nЯ его хранитель.</br>\nМир мысли мой!</br>\nЯ же – разрушитель.</br>\nСоздаю, храню и разрушаю</br>\nМысли мир,</br>\nКак Я решаю!</br>', NULL, NULL, NULL),
(395, 5, 1, 'СЧАСТЛИВЫЙ НЕ ЗНАЕТ', 'По китайскому изречению', 'Кто счастлив, часов не считает.</br>\nОн времени не сторожит.</br>\nВедь только счастливый не знает,</br>\nКак быстро то время бежит.</br>', NULL, NULL, NULL),
(396, 5, 1, 'НЕ УВИДИТ ПУТЬ', NULL, 'Пусть душа твоя хлопочет показать всю суть,</br>\nТолько тот, кто не захочет, не увидит путь.</br>', NULL, NULL, NULL),
(397, 5, 1, 'СУДЬБА', NULL, 'В твоей судьбе не виноват никто.</br>\nТы не случайности в объятья принимаешь.</br>\nОт жизни этой получаешь то,</br>\nО чём в ней постоянно размышляешь.</br>', NULL, NULL, NULL),
(398, 5, 1, 'РОВНОЕ ГОРЕНЬЕ БЫТИЯ', 'По изречению Э. Фромма', 'Счастье – не блестящие подарки,</br>\nСчастье – не веселость пития,</br>\nСчастье – не полёт кометы яркой,</br>\nА ровное горенье Бытия.</br>\n', NULL, NULL, NULL),
(399, 5, 1, 'ТОТ ДРУГ И ВСЕМ', 'По изречению Сенеки', 'Кто сжат в скобе, тот глух и нем,</br>\nКто друг себе, тот друг и всем.</br>', NULL, NULL, NULL),
(400, 5, 1, 'ВЕРУЙ В УСПЕХ', NULL, 'А эта истина для тех,</br>\nКто мудрость слов постигнет сих:</br>\n«Раз твёрдо веруешь в успех,</br>\nТо ты его почти достиг!»</br>', NULL, NULL, NULL),
(401, 5, 1, 'СЧАСТЬЕ ЛИ ЭТО', NULL, '«Своё у каждого счастье», –</br>\nНе раз это фраза пропета.</br>\nЕсть в этом правда отчасти,</br>\nНо только ли счастье это?</br>', NULL, NULL, NULL),
(402, 5, 1, 'МАТЬ ПРАВДЫ', NULL, 'Такому сердцу всюду рады,</br>\nЕсть в коем искренность – мать правды.</br>', NULL, NULL, NULL),
(403, 5, 1, 'ТОТ В ПОЛНОМ ПРАВЕ', NULL, 'Кто объявил коварной лжи суровый бой,</br>\nКто научился честно говорить с собой,</br>\nВ ком начал справедливый суд давно царить,</br>\nТот в полном праве людям правду говорить.</br>', NULL, NULL, NULL),
(404, 5, 1, 'КОГДА МЫ СЧАСТЛИВЫ', NULL, 'Когда мы счастливы,</br>\nТо рады пустякам,</br>\nНикто, ничто не огорчает,</br>\nУспех, удача нас венчают</br>\nС восторгом – к звёздам мы</br>\nИ к мотылькам.</br>\n', NULL, NULL, NULL),
(405, 5, 1, 'ТАЙНА ТЕЛА', NULL, 'Тайна тела: Где мысль, там и кровь.</br>\nТайна дела: Где смысл, там любовь.</br>', NULL, NULL, NULL),
(406, 5, 1, 'ОСОЗНАНЬЕ ДВИГАЕТ', NULL, 'Как собою недовольство разберёт,</br>\nВместо грусти, ощути блаженство:</br>\nОсознанье своего несовершенства</br>\nУже двигает тебя вперёд.</br>', NULL, NULL, NULL),
(407, 5, 1, 'ОКРУЖЕНИЕ', NULL, 'Окружение, как стая,</br>\nНа тебя начнет влиять.</br>\nПусть в ней сможешь жить без лая,</br>\nНо хвостом сумей вилять!</br>', NULL, NULL, NULL),
(408, 5, 1, 'НЕ СРАВНИВАЙТЕ СЧАСТЬЕ', NULL, 'О счастье множество есть мнений и идей:</br>\n«Не сравнивайте счастье у людей!»</br>\n', NULL, NULL, NULL),
(409, 5, 1, 'ИДЕАЛ СЧАСТЬЯ', 'По изречению Л. Кумора', 'У счастья нет предела</br>\nИ разный есть финал.</br>\nУ каждой части тела</br>\nСвой счастья идеал.</br>', NULL, NULL, NULL),
(410, 5, 1, 'НЕ ГОВОРИ О СЕБЕ', 'Из учения Грасиана – испанского философа', 'Лучше о себе не говорить.</br>\nТу привычку нужно усмирить;</br>\nВедь, хвалиться – верное тщеславие,</br>\nМалодушие – себя хулить.</br>', NULL, NULL, NULL),
(411, 5, 1, 'ХОРОШО В ГОСТЯХ', 'По русскому изречению', 'В чьих-то волостях слишком уж докучно.</br>\nХорошо в гостях, когда дома скучно.</br>\n', NULL, NULL, NULL),
(412, 5, 1, 'ДЕТКИ', NULL, 'Наши детки – есть наши тревоги;</br>\nВоспитай их любовью овей.</br>\nНо, чтоб деток поставить на ноги,</br>\nНужно снять бы их с шеи своей.</br>', NULL, NULL, NULL),
(413, 5, 1, 'ГЛУПЕЦ', 'Из учения Грасиана – испанского философа', 'Не тот глупец, кто глупость совершил,</br>\nА тот, кто, совершив её, не скрыл.</br>\nОшибки вызывают у людей насмешки,</br>\nВысмеивать себя оставь свой пыл.</br>', NULL, NULL, NULL),
(414, 5, 1, 'ФАНТИК', 'По изречению В. Кротова', 'Повесив бантик, будем мы с отметкой.</br>\nНаряд же – фантик, чтобы стать конфеткой.</br>', NULL, NULL, NULL),
(415, 5, 1, 'ОМОВЕНЬЕ', NULL, 'Дети черпают под ливнем вдохновенье.</br>\nДождь небесный – есть природы омовенье!</br>\nЧеловек – любитель новшеств и экзотик</br>\nНи к чему взял и придумал зонтик.</br>', NULL, NULL, NULL),
(416, 5, 1, 'ЗА ЧТО-ТО', 'По изречению Грасиана', 'За что-то тебя ненавидят,</br>\nНе сладок тот сахарок,</br>\nНо, зрители больше всё ж видят,</br>\nНежели видит игрок.</br>', NULL, NULL, NULL),
(417, 5, 1, 'КТО ПРИОБРЁЛ', NULL, 'Судьба счастливую тем выделяет долю,</br>\nКто приобрёл в своей душе покой и волю.</br>', NULL, NULL, NULL),
(418, 5, 1, 'НАС НЕ РАДУЕТ', 'По изречению А. Н. Никитина', 'Мы не ценим то, что нет мучений.</br>\nЖаждем удовольствия взрывного.</br>\nНас не радует, что мало огорчений,</br>\nОгорчает нас, что радостей не много.</br>', NULL, NULL, NULL),
(419, 5, 1, 'КУРЬЁЗ', 'По японскому изречению', 'Принять нужно правду простую,</br>\nЕсть истина, есть и курьёз:</br>\nК примеру: пёс лает впустую,</br>\nДругие ж подхватят всерьёз.</br>', NULL, NULL, NULL),
(420, 5, 1, 'ТРИ ВЕЩИ', 'По древнеиндийскому изречению\n', 'Еда,</br>\nпитиё,</br>\nда и доброе слово –</br>\nВсегда будут дороги снова и снова.</br>', NULL, NULL, NULL),
(421, 5, 1, 'РАДОСТЬ ДУХА', 'По изречению Р. Эмерсона', 'Радость слуха,</br>\nЧтоб звуки веселили.</br>\nРадость духа –</br>\nПризнанье его силы.</br>', NULL, NULL, NULL),
(422, 5, 1, 'СТАРОЕ ИЗРЕЧЕНИЕ', NULL, 'Мечтай и неудачи сгинут!</br>\nТо, изреченье испокон.</br>\nСказал когда-то Соломон:</br>\n«Где нет мечты, там люди гибнут.»</br>', NULL, NULL, NULL),
(423, 5, 1, 'ИЩЕТ ЮНОСТЬ', NULL, 'Ищет юность, впадая в ошибки,</br>\nНаслаждения чудный букет,</br>\nНо исканья её очень зыбки,</br>\nИщет там, где его вовсе нет.</br>', NULL, NULL, NULL),
(424, 5, 1, 'ГАРМОНИЯ', NULL, 'О гармонии часто ведутся беседы.</br>\nЧто такое гармония, скажут нам Веды:</br>\nТа придёт, если жизни твоей дорога</br>\nИдеально сольётся с дорогой Бога.</br>', NULL, NULL, NULL),
(425, 5, 1, 'СЧАСТЬЕ И ПРИВЫЧКА', NULL, 'Кто к счастью не нашёл отмычку,</br>\nМеняет счастье на привычку.</br>', NULL, NULL, NULL),
(426, 5, 1, 'ТАИНСТВЕННЫЕ АРСЕНАЛЫ', NULL, 'Таинственны природы арсеналы</br>\nИ часто это видно налицо.</br>\nПо отпечаткам – мы оригиналы,</br>\nА, в общем, все мы на одно лицо.</br>', NULL, NULL, NULL),
(427, 5, 1, 'ЗАБОТЬСЯ О ТОМ, ЧТО ДАНО', NULL, 'На время всё дано: и жизнь, и тело…</br>\nНичто во власти личной не находится.</br>\nВот, правом выбора владеешь ты всецело.</br>\nА что дано, умей о том заботиться.</br>', NULL, NULL, NULL),
(428, 5, 1, 'НА КОЛЕНЯХ', 'По изречению С. Сидорова', 'Отвага бывает на разных ступенях:</br>\nТрудно стоять на своём на коленях.</br>', NULL, NULL, NULL),
(429, 5, 1, 'ЧАЩЕ СТРАХ', NULL, 'Чаще страх – явление дурное,</br>\nТо – вопрос с ответами незнанья.</br>\nЧасто страх внутри – не что иное,</br>\nКак лишенье помощи сознанья.</br>', NULL, NULL, NULL),
(430, 5, 1, 'НАША РОЛЬ', 'По восточному ведическому трактату', 'В чём же посланной жизни является соль?</br>\nКакова наша самая главная роль?</br>\nНа вопросы ответы признаем правдивыми:</br>\n«Будь счастливым и делай других счастливыми.»</br>', NULL, NULL, NULL),
(431, 5, 1, 'ТОТ, КТО СЛАБ', 'По изречению В. Фишера', 'Волей слаб и душа твоя в коме –</br>\nЧужаком будешь в собственном доме.</br>', NULL, NULL, NULL),
(432, 5, 1, 'ТРУСОСТЬ – РАБСТВО СТРАХА', NULL, 'Пугаясь кинжала взмаха,</br>\nПрячется жизнь на запятках.</br>\nТрусливый в рабстве у страха.</br>\nДуша его стоптана в пятках.</br>', NULL, NULL, NULL),
(433, 5, 1, 'ОНА НЕ ЗАБУДЕТ', NULL, 'Наш каждый поступок известен Вселенной.</br>\nМы можем забыть его в жизни мгновенной.</br>\nМы можем забыть, но она не забудет.</br>\nОна и решит, что нам дать и как будет.</br>', NULL, NULL, NULL),
(434, 5, 1, 'ЦЕПНАЯ РЕАКЦИЯ', NULL, 'Мысль – в материю, реакция цепная:</br>\nМысль свиная, значит жизнь срамная.</br>', NULL, NULL, NULL),
(435, 5, 1, 'ЖИЗНЬ', NULL, 'Для ума жизнь – просветленье,</br>\nДля души жизнь – размышленье,</br>\nЖизнь для воли – устремленье,</br>\nЖизнь для тела – продвиженье,</br>\nДля любви жизнь – приближенье.</br>', NULL, NULL, NULL),
(436, 5, 1, 'УШИ ИМЕЮТ СТЕНЫ', 'По еврейскому изречению', 'Как встретятся злые гиены,</br>\nИль просто без чуткости души,</br>\nЗнай, уши имеют стены,</br>\nА стены имеют уши.</br>\n', NULL, NULL, NULL),
(437, 5, 1, 'БОЛЕЗНИ НЕУДАЧНИКОВ ЗАРАЗНЫ', NULL, 'Светлый, тёмный мир,</br>\nБольной, здоровый; разный.</br>\nВ нём болезни неудачников заразны.</br>\nСлабая душа с безвольным телом</br>\nМогут растоптать всё неумело.</br>', NULL, NULL, NULL),
(438, 5, 1, 'ПРИМЕТЫ ДУРАЧИНЫ', NULL, 'Приметы в людях дурачины:</br>\nДобра не видеть и вражды,</br>\nСердиться злостно без причины,</br>\nБолтать часами без нужды</br>\nИ лезть туда, куда не нужно,</br>\nИскать повсюду ссор и драк,</br>\nВсё делать с важностью натужной</br>\nИ попадать всегда впросак.</br>', NULL, NULL, NULL),
(439, 5, 1, 'СОСУД', 'По изречению Горация', 'Раз сосуд недостаточно чисто промыл,</br>\nСкиснет всё, что туда бы потом бы не влил.</br>', NULL, NULL, NULL),
(440, 5, 1, 'ЖИВИТЕ БЕЗ ОГЛЯДКИ', NULL, 'Поверьте всё в порядке, раз трудитесь стараясь.</br>\nЖивите без оглядки, немножко озираясь.</br>', NULL, NULL, NULL),
(441, 5, 1, 'НЕ ПРОЕЦИРУЙ СЕБЯ НА МИР', NULL, 'Зря себя проецируешь в мир,</br>\nВозмущаясь, что он не такой!</br>\nСвой у каждого жизненный пир:</br>\nС разным счастьем и разной тоской.</br>', NULL, NULL, NULL),
(442, 5, 1, 'ВЕК ЖИВИ', NULL, 'Век живи, век учись,</br>\nДураком помрёшь.</br>\nДурака же лучше не учи,</br>\nДурака умнее не найдёшь.</br>', NULL, NULL, NULL),
(443, 5, 1, 'УЗНАЕМ', 'По изречению Эзопа', 'Осла и в львиной коже мы узнаём по крику.</br>\nГлупца и с важным видом мы узнаём по лику.</br>\n', NULL, NULL, NULL),
(444, 5, 1, 'ЧТОБЫ ОТРИЦАТЬ, НУЖНО ЗНАТЬ', NULL, 'Услышал где-то, прочитал и толковать,</br>\nВсем пылом наставлять, критиковать.</br>\nРаз, не знаток, не мог то созерцать,</br>\nКак можно спорить или что-то отрицать.</br>\n', NULL, NULL, NULL),
(445, 5, 1, 'БЕЗУМНЫЙ МИР', 'По изречению С. Янковского', 'Ох, этот мир безумный,</br>\nГде полусвет – полумрак.</br>\nСглупить может здесь и умный,</br>\nА сумничать – только дурак.</br>', NULL, NULL, NULL),
(446, 5, 1, 'ПРОТИВ ГЛУПОСТИ', 'По изречению Ф. Шиллера', 'Только в глупости становимся убоги,</br>\nПротив глупости бессильны даже Боги.</br>', NULL, NULL, NULL);
INSERT INTO `content` (`id`, `id_cat1`, `id_cat2`, `title`, `comment`, `text`, `keywords`, `description`, `lang`) VALUES
(447, 5, 1, 'ПРИМИ ВИНУ СПОЛНА', NULL, 'Всегда при ссоре виноваты оба,</br>\nЗакон гласит: в обоих есть вина.</br>\nНо, мудрость в том, чтобы унявши злобу,</br>\nМир предложить, приняв вину сполна.</br>', NULL, NULL, NULL),
(448, 5, 1, 'ИГРЫ БЕЗ ПРАВИЛ', 'По изречению Н. Бегемотова', 'На дураков с опаскою взирают.</br>\nОдним законом дело б я исправил:</br>\nПусть с умными по правилам играют,</br>\nА с дураками игры все – без правил.</br>', NULL, NULL, NULL),
(449, 5, 1, 'УМНИЦА', 'По русскому изречению', 'Делом проверяется,</br>\nВременем смывается.</br>\nУмница смиряется,</br>\nГлупый надувается.</br>\n', NULL, NULL, NULL),
(450, 5, 1, 'ТВЕРДОСТЬ И ГИБКОСТЬ', NULL, 'Порой жизнь не пряма,</br>\nПорой она нема.</br>\nНо, твердость от характера</br>\nИ гибкость от ума.</br>', NULL, NULL, NULL),
(451, 5, 1, 'ЧУЖИЕ ЗЕМЛИ', 'По изречению М. Сафира', 'Запретом можешь молодых обидеть,</br>\nРаз держишь их, они приходят в ярость.</br>\nЧужие земли хочет молодость увидеть,</br>\nА свои собственные хочет видеть старость.</br>', NULL, NULL, NULL),
(452, 5, 1, 'ДОСРОЧНАЯ КОНЧИНА', NULL, 'Позорные, порочные</br>\nИ прочие понятия</br>\nНесут в себе досрочную</br>\nКончину и распятие.</br>', NULL, NULL, NULL),
(453, 5, 1, 'КРАСОТА – ЭТО КАЧЕСТВО ДУШИ', NULL, 'Порой обманна внешность у людей,</br>\nКрасива вроде, только суть не в ней.</br>\nИ с выводом о том, всё ж, не спеши,</br>\nВедь красота – есть качество души.</br>\nМир красоту относит к чудесам,</br>\nЕё почувствуешь душой ты сам.</br>', NULL, NULL, NULL),
(454, 5, 1, 'ШЕПОТ НЕБА', NULL, 'Раз заглянул в подземное хранилище,</br>\nПридётся совести пройти чистилище</br>\nИ помнить впредь, что в подземелье б не был,</br>\nКогда бы слушал сердцем шепот неба.</br>', NULL, NULL, NULL),
(455, 5, 1, 'РАЗУМ', NULL, 'Разум – ощущенье истинных вестей.</br>\nРазум – пониманье мудрости вещей.</br>\nРазум – есть вонзающейся сути острие.</br>\nРазум – есть осознанное духа бытие.</br>', NULL, NULL, NULL),
(456, 5, 1, 'НЕ УДЕРЖИВАЙ СЛОВО', 'По изречению из Библии Сир. 4.27', 'Завещанье Христово</br>\nВ своём сердце упрочь:</br>\nНе удерживай слово,</br>\nРаз то может помочь.</br>', NULL, NULL, NULL),
(457, 5, 1, 'НЕТЕРПЕНИЕ И ТЕРПЕНИЕ', NULL, 'Жизнь скупо счастье продаёт,</br>\nГде нетерпенья взрыв,</br>\nЖизнь даром счастье отдаёт,</br>\nТому, кто терпелив.</br>', NULL, NULL, NULL),
(458, 5, 1, 'МЫСЛИ ИЗ ТАЛМУДА', NULL, 'Тот, кто учиться рад</br>\nУ старых уж давно,</br>\nЕст спелый виноград,</br>\nПьёт старое вино.</br>', NULL, NULL, NULL),
(459, 5, 1, 'КАК ВЗРОСЛЫЕ И ДЕТИ', NULL, 'Как взрослые даём советы</br>\nИ дарим людям в жизнь билеты.</br>\nНо сами часто так не гибки.</br>\nКак дети делаем ошибки.</br>', NULL, NULL, NULL),
(460, 5, 1, 'ГИМН', NULL, 'Являют эти строки гимн:</br>\nТакие мысли шли другим,</br>\nКакие сам принять бы рад.</br>\nЧто дашь – получишь то назад!</br>', NULL, NULL, NULL),
(461, 5, 1, 'ОГОНЬ НЕ СТЫНЕТ', NULL, 'Намёки из басни просты и поныне:</br>\nОгонь хоть и гаснет, но всё же не стынет.</br>', NULL, NULL, NULL),
(462, 5, 1, 'ТОТ ЛУЧШИЙ ВОЖДЬ', 'По изречению Н. Бегемотова', 'И самый прекрасный вождь</br>\nПорою не так уж могуч,</br>\nВедь, чтоб не попасть под дождь,</br>\nБыть нужно ему выше туч.</br>', NULL, NULL, NULL),
(463, 5, 1, 'РАЗУМ И ГЛУПОСТЬ', NULL, 'Каков ты есть, то видно сразу:</br>\nКто терпелив, покажет разум,</br>\nКто жаден, тот покажет скупость,</br>\nКто раздражён, покажет глупость.</br>', NULL, NULL, NULL),
(464, 5, 1, 'ВОРОНА', 'По изречению Л. Дурова', 'Величавость трона бьёт нас в пол челом,</br>\nНа скале ворона кажется орлом.</br>', NULL, NULL, NULL),
(465, 5, 1, 'ОТКАЖЕМСЯ', NULL, 'Под давлением морального недуга</br>\nЗаменяет слово твердь свинца.</br>\nТак откажемся от острого словца,</br>\nЧем за это мы откажемся от друга.</br>', NULL, NULL, NULL),
(466, 5, 1, 'ХВАЛИТЬ И ПОРИЦАТЬ', 'По изречению Леонардо да Винчи', 'Раз хвалишь, то приносишь вред,</br>\nНо есть и погорчей обед,</br>\nРаз порицаешь чей-то след,</br>\nНе смысля, мудрость то, иль нет.</br>', NULL, NULL, NULL),
(467, 5, 1, 'НАМЁК', NULL, 'Недаром ведь намёк в народе сделал кто-то:</br>\nИ светлый ручеёк способен впасть в болото.</br>', NULL, NULL, NULL),
(468, 5, 1, 'СПОСОБ', 'По изречению М. Аврелия', 'В любой битве победить смогу,</br>\nЕсли мысль я постараюсь утвердить:</br>\nНастоящий способ мстить врагу –</br>\nЭто на него не походить.</br>', NULL, NULL, NULL),
(469, 5, 1, 'ЧТО СТАНЕТСЯ', 'По изречению Ванги', 'Что станется, знают пророки:</br>\nИдём к одному порогу.</br>\nПусть жизнь уготовила сроки,</br>\nНо нам выбирать дорогу!</br>', NULL, NULL, NULL),
(470, 5, 1, 'ИЩИ СЕБЯ ТАМ', NULL, 'Иди к тем вратам, где ждут и где встретят,</br>\nИщи себя там, где жнут и где светят.</br>', NULL, NULL, NULL),
(471, 5, 1, 'ОСКОРБЛЕНИЕ', 'По изречению Р. Декарта', 'Оскорбление не тяготит, не рушит.</br>\nПусть оно и будет велико.</br>\nОскорбление не тронет твою душу,</br>\nЕсли душу ты возносишь высоко.</br>', NULL, NULL, NULL),
(472, 5, 1, 'ПЛАТА', 'По изречению Ванги', 'Порой, сам себе высь пророчишь.</br>\nНо, сверху спешат известить:</br>\nЧем больше от жизни ты хочешь,</br>\nТем больше придётся платить.</br>\n', NULL, NULL, NULL),
(473, 5, 1, 'ЛОГИКА', 'По изречению М. Туровского', 'От логики с Истиной дружбой не веет.</br>\nЛогика Истину часто довлеет.</br>\nУ логики пусть математики нрав,</br>\nНо, мысля логично, не думай что прав.</br>\nЖелезная логика также ржавеет.</br>', NULL, NULL, NULL),
(474, 5, 1, 'ТЫ СЕБЯ ПОТЕРЯЛ', 'По изречению Е. Кащеева', 'Если сникла готовность к бою,</br>\nЕсли в мыслях тоскливых застрял,</br>\nЕсли скучно с самим собою,</br>\nЗначит, где-то себя потерял.</br>', NULL, NULL, NULL),
(475, 5, 1, 'ПРЕСТИЖ', NULL, 'Что такое престиж?</br>\nМожет это пустяк?</br>\nМожет это почёт, что воздаст вам дурак?</br>\nИль восторг детворы?</br>\nЗависть злых богачей?</br>\nИль насмешливость взгляда мудрейших очей.</br>', NULL, NULL, NULL),
(476, 5, 1, 'ОСОЗНАЙ СЕБЯ', NULL, 'Иллюзорно все к себе вниманье:</br>\nОсознай себя, как осознанье!</br>', NULL, NULL, NULL),
(477, 5, 1, 'В ОДИНОЧЕСТВЕ', NULL, 'Наедине с собой в своём пророчестве,</br>\nРаз ты высок душой, то мыслей мил вьюнок.</br>\nВедь мудрый сердцем, оставаясь в одиночестве,</br>\nВ минуты эти всего меньше одинок.</br>', NULL, NULL, NULL),
(478, 5, 1, 'ДРЕВНИЙ ЮМОР', NULL, 'Вот юмор вам про рот и уши:</br>\nБог нас сумел премудро сотворить.</br>\nДва уха дал, чтоб больше слушать,</br>\nА рот один, чтоб меньше говорить.</br>', NULL, NULL, NULL),
(479, 5, 1, 'ЧУВСТВА И МЫСЛИ', 'По изречению В. Фишера', 'Чувства пленяются, мысли радеют,</br>\nМысли меняются, чувства твердеют.</br>', NULL, NULL, NULL),
(480, 5, 1, 'В ОБЪЯТИЯХ ДРУГ ДРУГА', 'По изречению Л. Де Крешенцо', 'Не очень весело грести одним веслом,</br>\nЗдесь ловкость рук нужна и всей груди натуга.</br>\nМы – люди, ангелы всего с одним крылом,</br>\nЛетать мы можем, но в объятиях друг друга.</br>\n', NULL, NULL, NULL),
(481, 5, 1, 'УМА НЕ НАДО', 'По русскому изречению', 'Ума в нас мало – счесть своих пороков стадо,</br>\nА блох чужих считать ума не надо.</br>', NULL, NULL, NULL),
(482, 5, 1, 'КОШКИ, МЫШКИ', NULL, 'Чужие недостатки на виду,</br>\nТо – кошки наглые под солнцем на пригорке.</br>\nА со своими мы живем в ладу,</br>\nТо – мышки скромные в подземной тёмной норке.</br>', NULL, NULL, NULL),
(483, 5, 1, 'ЗРЯ', NULL, 'Зря мало размышляем, читаем так небрежно,</br>\nИ мыслим не безгрешно, и судим так поспешно.</br>', NULL, NULL, NULL),
(484, 5, 1, 'ТО ТАКЖЕ БУДЕТ ГРУБОСТЬЮ', NULL, 'Когда довлеемы объектом,</br>\nЯвляется то грубостью.</br>\nБывает, давят интеллектом,</br>\nБывает, давят глупостью.</br>', NULL, NULL, NULL),
(485, 5, 1, 'ЗАЧАТЬ В РАЗМЫШЛЕНЬЯХ', 'По изречению И. Юганова', 'В душе сначала утверждалось,</br>\nА после в прочих проявленьях.</br>\nЧтоб в споре истина рождалась,</br>\nЗачать ту нужно в размышленьях.</br>', NULL, NULL, NULL),
(486, 5, 1, 'ВЕРЬ', 'По изречению из Библии Сир. 32.24.', 'Есть к соблюденью заповедей дверь:</br>\nДуше своей во всяком деле верь.</br>', NULL, NULL, NULL),
(487, 5, 1, 'ЗАБЛУЖДЕНИЯ', 'По изречению И. Канта', 'Наполнен мир всего.</br>\nЕсть в людях заблужденья</br>\nИ опровергнуть их признанья невозможно.</br>\nНо просветить того,</br>\nДойди до снисхожденья,</br>\nЧтоб растворилось в нём познанье то, что ложно.</br>', NULL, NULL, NULL),
(488, 5, 1, 'НЕ МОЖЕТ ЗНАТЬ НИКТО', 'По изречению из Библии Колос. 2.18.', 'Высокопарной речью пусть никто вас не обманет,</br>\nЧто почитаем он у ангелов своих.</br>\nВедь, мудрый это говорить не станет,</br>\nНе могут знать о том среди мирских.</br>', NULL, NULL, NULL),
(489, 5, 1, 'СВЕРШЕНЬЕ ЧАЕМОГО', 'По изречению из Библии Евр. 11.1.', 'Свершишь всё чаемое, раз имеешь веру,</br>\nОткроешь ей вещей невидимую сферу.</br>', NULL, NULL, NULL),
(490, 5, 1, 'СТАЛ НАПЫЩЕННЫМ', 'По изречению Ж. де Лабрюйера', 'Он не жил со смыслом здравым,</br>\nС сердцем жил расхищенным.</br>\nОн мечтал стать величавым.</br>\nНу, а стал напыщенным.</br>', NULL, NULL, NULL),
(491, 5, 1, 'ОДНИ И ДРУГИЕ', 'По изречению из Библии Сир. 21.29.', 'То видишь ты людей одних,</br>\nВ устах же глупых – сердце их.</br>\nТо встретишь ты людей других,</br>\nУста же мудрых – в сердце их.</br>', NULL, NULL, NULL),
(492, 5, 1, 'ПОРА', 'По изречению Ж. Сесброна', 'Пора вниманье уделить своим манерам.</br>\nМы служим зрелищем другим, а не примером.</br>\n', NULL, NULL, NULL),
(493, 5, 1, 'НЕДОВЕРЧИВОСТЬ', NULL, 'Кто недоверчиво в ответ кивает,</br>\nТот видно сам грехи навьючил на горбе.</br>\nКто так легко других подозревает,</br>\nТот заставляет дурно думать о себе.</br>\n', NULL, NULL, NULL),
(494, 5, 1, 'ЗЕРКАЛА', 'По изречению И. В. Гёте', 'Всё видно сразу: кто – эстеты,</br>\nКто есть сплошной сумбур идей,</br>\nКто – чудодей, кто – лиходей.</br>\nНрав и манеры – то дуплеты.</br>\nДуша и внешность – трафареты.</br>\nМанеры – зеркала людей,</br>\nВ которых их видны портреты.</br>', NULL, NULL, NULL),
(495, 5, 1, 'ПОСЛУШАВШИ ЕГО', NULL, 'Сойдётесь в тупике, послушавши его;</br>\nУченость в дураке опаснее всего.</br>', NULL, NULL, NULL),
(496, 5, 1, 'СУЩЕЕ НАКАЗАНИЕ', 'По изречению Л. де Бональда', 'Вновь находимся под чьим-то притязаньем,</br>\nВновь крадут у нас иль час, иль день, иль ночку.</br>\nТе становятся нам сущим наказаньем,</br>\nКто не в силах тратить время в одиночку.</br>', NULL, NULL, NULL),
(497, 5, 1, 'МОРЕ СКУКИ', NULL, 'У кого в речах изжога,</br>\nКто болтает очень много,</br>\nГоворя пустые звуки,</br>\nНагоняет море скуки.</br>', NULL, NULL, NULL),
(498, 5, 1, 'В РАДОСТИ', 'По изречению из Библии Пс. 125.5.', 'Добротою греющие, почивают в сладости.</br>\nСо слезами сеющие, пожинают в радости.</br>', NULL, NULL, NULL),
(499, 5, 1, 'ВЕЧНАЯ МОДА', 'По народному изречению', 'На женщину должны все охать,</br>\nВ веках та мода введена.</br>\nОдета женщина раз плохо,</br>\nНе может умной быть она.</br>\n', NULL, NULL, NULL),
(500, 5, 1, 'МИР – ОТРАЖЕНИЕ ВСЕМУ', NULL, 'Не будь для мира дебоширом,</br>\nУмей прожить в том мире с миром.</br>\nЖеланье можешь заказать,</br>\nНо помни, мир готов сказать,</br>\nНе больше, чем и ты ему,</br>\nМир – отражение всему.</br>', NULL, NULL, NULL),
(501, 5, 1, 'ПЛОД', NULL, 'За ночкой тёмною – лучей златых восход.</br>\nОт корня горького – учёность – сладкий плод.</br>', NULL, NULL, NULL),
(502, 5, 1, 'У РАЙСКОГО ХОЛМА', 'По изречению Г. Филдинга', 'Навозных куч, сомнительных весьма,</br>\nПредпочитает кто-то покорять вершины,</br>\nЧем у подножья райского холма</br>\nМостить свой дом из кирпича и глины.</br>', NULL, NULL, NULL),
(503, 5, 1, 'ОБРАЩЕНИЕ С ЛЮДЬМИ', NULL, 'Поступают так, лишь поумнев:</br>\nКротостью одолевают гнев.</br>\nПростотой смягчают гордеца.</br>\nПравдой прозревают мысль лжеца.</br>\nМузыкой смущают болтуна.</br>\nЛаской побеждают драчуна.</br>\nБезразличием смыкают рот глупца.</br>\nПрямотой сражают речь льстеца.</br>\n', NULL, NULL, NULL),
(504, 5, 1, 'ТАК ГОВОРИ', 'По изречению Сократа', 'Что там внутри, любил или обидел?</br>\nТак говори, чтоб я тебя увидел.</br>\n', NULL, NULL, NULL),
(505, 5, 1, 'НОСТАЛЬГИЯ', NULL, 'Ох! Не родная стихия!</br>\nТвоя не поможет мне лесть.</br>\nНоет в душе ностальгия –</br>\nПокинутой родины месть.</br>', NULL, NULL, NULL),
(506, 5, 1, 'БЕСКОРЫСТНОСТЬ', NULL, 'Если даришь, дари от души</br>\nИ не требуй блюсти этикет.</br>\nЕсли дружишь, себе ты внуши,</br>\nГлупо требовать дружбы обет.</br>\nЕсли любишь, понять поспеши,</br>\nГрустно требовать чувства в ответ.</br>\n', NULL, NULL, NULL),
(507, 5, 1, 'ВЫСКАЖЕТ', NULL, 'Умный подумает, выскажет редко.</br>\nГлупый не думает, выскажет едко.</br>', NULL, NULL, NULL),
(508, 5, 1, 'НЕПРАВИЛЬНАЯ МЫСЛЬ', NULL, 'Мысль неправильная есть у нас,</br>\nЧто наш друг, обязан всё прощать –</br>\nЭто заблуждений злых окрас.</br>\nБрось пренебрежение прельщать!</br>', NULL, NULL, NULL),
(509, 5, 1, 'СВОД ПРАВИЛ', 'По изречению востоковеда В. О. Рузова', 'Есть в отношеньях правил мудрых свод.</br>\nПоследствия меняет их приход:</br>\n1. О человеке нет раз добрых слов,</br>\nТогда молчать о нём ты будь готов.</br>\n2. Не можешь делом, если вдруг помочь,</br>\nТо лучше не мешай и не морочь.</br>\n3. При каждой встрече, пусть хоть с кем-нибудь,</br>\nПриятное всем делать не забудь.</br>\n4. Слова зря обещаний не роняй,</br>\nРаз обещанье дал, то выполняй.</br>', NULL, NULL, NULL),
(510, 5, 1, 'КРАСНОРЕЧИЕ', 'По изречению Цицерона', 'Прекрасное звучание, раз мудрое наречие.</br>\nНо также и в молчании бывает красноречие.</br>\n', NULL, NULL, NULL),
(511, 5, 1, 'ЧЕРЕЗ ОБЛАСТЬ СЕРДЦА', NULL, 'Изучайте мир через себя…</br>\nЧерез область сердца в мироздание</br>\nДверь найдите в тишине сознания,</br>\nВход к сокровищнице мира, тайнам, знаниям…</br>\n-----------------------------------------------------------</br>\nНашей интуиции дыхание</br>\nПриглашает к двери той любя.</br>', NULL, NULL, NULL),
(512, 5, 1, 'ЧЕТЫРЕ СТАДИИ УВАЖЕНИЯ', NULL, '1. Уваженье требую к себе,</br>\nНо других я уважать не собираюсь.</br>\n</br>\n2. С уваженьем кто к моей судьбе,</br>\nУважать того я постараюсь.</br>\n</br>\n3. Уважаю и себя, и всех других,</br>\nНо меня не троньте, я вас тоже.</br>\n</br>\n4. Уважаю всех вас, дорогих,</br>\nВы меня все лучше и дороже.</br>', NULL, NULL, NULL),
(513, 5, 1, 'МЫ – ПТИЦЫ', NULL, 'Мы, любящие – птицы, гнездо стремимся свить,</br>\nНо грустны наши лица, раз в небо нам не взвить.</br>', NULL, NULL, NULL),
(514, 5, 1, 'ДВА ПРИНЦИПА ВОСПИТАНИЯ', 'По ведической психологии', 'Для мальчика:</br>\n1. Собою пытаться владеть.</br>\n2. Заботиться должен уметь.</br>\n</br>\nДля девочки:</br>\n1. Чтоб целомудрие являло честь.</br>\n2. Довольствоваться тем, что есть.</br>', NULL, NULL, NULL),
(515, 5, 1, 'ОТНОШЕНИЕ К ПРОШЛОМУ', 'По изречению из Библии Ек. 1.11.', 'Раз памяти нет о прежнем,</br>\nТо значит о том, что будет,</br>\nРождённый после, небрежно</br>\nИ также просто забудет.</br>', NULL, NULL, NULL),
(516, 5, 1, 'УЗЫ', NULL, 'Когда вешают тяжкие грузы,</br>\nРазрываются брачные узы.</br>', NULL, NULL, NULL),
(517, 5, 1, 'ОСТАНОВИСЬ И СПРОСИ', 'По изречению из Библии Иерем. 6.16.', 'Остановись в своих путях,</br>\nСпроси о древних повестях.</br>\nИ по тому пути направься,</br>\nГде доброта была в гостях.</br>', NULL, NULL, NULL),
(518, 5, 1, 'ПОЛЯ СИРОТ', 'По изречению из Библии Пр. 23.10.', 'Не передвинь древнейший след межи,</br>\nЧто провели старинные мужи.</br>\nОбычай и традиции блюди</br>\nИ на поля сирот не заходи.</br>', NULL, NULL, NULL),
(519, 5, 1, 'НЕ СПОРЬТЕ', NULL, 'Не спорьте с женщиной, у той особый нрав.</br>\nРаз споришь с женщиной, то ты уже не прав.</br>', NULL, NULL, NULL),
(520, 5, 1, 'ПОЖЕНИВШИСЬ', NULL, 'Если чувством временным пленились,</br>\nТо бывает, поженившись, жизнь загубим.</br>\nЛучше полюбить, с кем поженились.</br>\nЧем спешить жениться, как полюбим.</br>', NULL, NULL, NULL),
(521, 5, 1, 'СТОЛЬКО Ж', 'По изречению из Библии Лк. 12.48.', 'Таланта дорога славна с виду только ж,</br>\nДано если много, потребуют столько ж.</br>\nНе быть тут иного, не отдал, отхлыщут.</br>\nРаз вверено много, то больше и взыщут.</br>', NULL, NULL, NULL),
(522, 5, 1, 'КОГО НЕ УВАЖАЮТ', 'По изречению П. Буаста', 'Не нужно там утех, где больно обижают,</br>\nНедолго любят тех, кого не уважают.</br>', NULL, NULL, NULL),
(523, 5, 1, 'ИСТОЧНИК СЧАСТЬЯ', NULL, 'Источник счастья твоего,</br>\nВнутри тебя же самого.</br>', NULL, NULL, NULL),
(524, 5, 1, 'НЕ СТАНЕМ', NULL, 'На колких слов колоду</br>\nСердиться мы не станем.</br>\nЛишь, в дерево без плода</br>\nНе бросят люди камнем.</br>', NULL, NULL, NULL),
(525, 5, 1, 'ЛЮБАЯ СИТУАЦИЯ', NULL, 'Не принимай новацию с надуманной тревожностью.</br>\nЛюбая ситуация становится возможностью.</br>', NULL, NULL, NULL),
(526, 5, 1, 'КАРУСЕЛИ', NULL, 'Порою далеко и много ходим,</br>\nНо в том же месте вновь себя находим.</br>\nКогда в нас мысли крутят карусели,</br>\nТо там, где встали, там же мы и сели.</br>', NULL, NULL, NULL),
(527, 5, 1, 'О ТАЛАНТЕ', NULL, 'Встречаются порою люди-павы</br>\nС большим излишком жестов хвастовских.</br>\nТалант Бог посылает не для славы,</br>\nА чтобы сделать им счастливее других.</br>', NULL, NULL, NULL),
(528, 5, 1, 'НЕ ВСЕГДА', NULL, 'Бывает истина зарыта глубоко,</br>\nРаз ищешь, то надежда умножается,</br>\nНо не всегда глупец идущий далеко,</br>\nК той истине заветной приближается.</br>', NULL, NULL, NULL),
(529, 5, 1, 'ТРУДНЕЙ ВСЕГО', 'По изречению В. Хочинского', 'Вновь я с врагом, что явит бой моим ушам,</br>\nТрудней всего с самим собой речь по душам.</br>', NULL, NULL, NULL),
(530, 5, 1, 'О ВЕЛИКИХ ЛЮДЯХ', NULL, 'Не живут великие на крыше</br>\nИ другую пищу не приемлют.</br>\nПусть на голову они и выше,</br>\nНоги попирают ту же землю.</br>', NULL, NULL, NULL),
(531, 5, 1, 'МАЛАЯ И БОЛЬШАЯ', NULL, 'В море вздымаются волны,</br>\nНебо спокойно царит.</br>\nМалая боль говорит,</br>\nБольшая печаль безмолвна.</br>', NULL, NULL, NULL),
(532, 5, 1, 'В СВОЁМ ДОМЕ', NULL, 'Не видно злых забот и сердце не в истоме,</br>\nСчастливым будет тот, кто счастлив в своём доме.</br>', NULL, NULL, NULL),
(533, 5, 1, 'УКРАШАЮТ МИР', NULL, 'К талантливым много прелюдий,</br>\nО них порасскажут всего.</br>\nДа! Мир украшают те люди,</br>\nЧто есть не от мира сего.</br>', NULL, NULL, NULL),
(534, 5, 1, 'МНЕНИЕ ТАРАНОВА', NULL, 'Себя мы, почему-то, понимаем только;</br>\nЧему бы, кто нас и когда бы не учил.</br>\nА если научить и удалось, хоть сколько,</br>\nВнутри нас что спало, то он и разбудил.</br>', NULL, NULL, NULL),
(535, 5, 1, 'ОБ ИСТИНЕ', NULL, 'Истина мудра и велика,</br>\nВсё пройдёт, она же – на века.</br>', NULL, NULL, NULL),
(536, 5, 1, 'КТО МЫ НА САМОМ ДЕЛЕ', NULL, 'Задумаемся о своём уделе,</br>\nПриняв себя правдиво, без прикрас.</br>\nСпросив себя, кто мы на самом деле,</br>\nА не про то, что думают о нас.</br>', NULL, NULL, NULL),
(537, 5, 1, 'СТРАСТЬ', NULL, 'Страсть, краской яркою рябя,</br>\nИль ненавидя, иль любя,</br>\nВсех хочет помечать.</br>\nНа всё кладет печать.</br>\nЗнать, смысл её явления,</br>\nОставить впечатления.</br>\n', NULL, NULL, NULL),
(538, 5, 1, 'БОЛЬШОЕ ИСКУССТВО', NULL, 'Всегда является большим искусством,</br>\nМирясь с законом, жить умом и чувством.</br>', NULL, NULL, NULL),
(539, 5, 1, 'ПЕРЕСТАЛИ', NULL, 'Его перестали хулить,</br>\nОн стал от того веселиться.</br>\nЕго перестали хвалить,</br>\nПоэтому стал он хвалиться.</br>', NULL, NULL, NULL),
(540, 5, 1, 'СЛУХИ', 'По изречению Грасиана', 'Для слухов нет преграды,</br>\nХоть как их там держи!</br>\nТо, черный вход – для правды,</br>\nПарадный вход – для лжи.</br>', NULL, NULL, NULL),
(541, 5, 1, 'МЫ – ДЕТИ ИСТИНЫ', NULL, 'Что наша истина, порой себя вскружим.</br>\nМы – дети истины и ей принадлежим.</br>', NULL, NULL, NULL),
(542, 5, 1, 'БЫТЬ СВОБОДНЫМ', 'По изречению К. Гельвеция', 'Одно с другим должно быть дружным,</br>\nОдно другому быть исходным,</br>\nЧтоб понимать нам думать нужно,</br>\nЧтоб думать, нужно быть свободным.</br>', NULL, NULL, NULL),
(543, 5, 1, 'ВНУТРЕННЯЯ ЦЕЛОСТНОСТЬ', NULL, 'Говорят, что искать половинку не верно,</br>\nЭто делает тот, кто не полон внутри,</br>\nМы должны быть добры и щедры, как цари,</br>\nИ наполнены целостным счастьем безмерно.</br>', NULL, NULL, NULL),
(544, 5, 1, 'ЦИНИК', NULL, 'Коварным циником тот человек зовётся,</br>\nКто ярко говорит, но делать не берётся</br>', NULL, NULL, NULL),
(545, 5, 1, 'ТЕРПИТЕ ЕГО', 'По изречению из Библии 2 Кор.11.4.', 'Раз кто-то нового вам возвестит Христа,</br>\nПредложит кто-то новый символ веры,</br>\nЕвангелие новое прочтут того уста,</br>\nЕго терпите, раз мудры примеры.</br>', NULL, NULL, NULL),
(546, 5, 1, 'КАРМА', NULL, 'Мы совершаем действия,</br>\nЗа них же – отвечаем,</br>\nА их последствия</br>\nМы кармой величаем.</br>', NULL, NULL, NULL),
(547, 5, 1, 'ЖИЗНЬ – РЕКА', NULL, 'Плывут века, текут закаты в зори,</br>\nИ жизнь река, впадающая в море.</br>', NULL, NULL, NULL),
(548, 5, 1, 'ЗАБЛУЖДЕНЬЯ', NULL, 'Присущи человеку заблужденья.</br>\nПолезно внять нам разные сужденья.</br>\nРазумный из простых бесед сумеет суть извлечь,</br>\nА мудрый и от камня пыльного услышит речь.</br>', NULL, NULL, NULL),
(549, 5, 1, 'ИМЕТЬ НАМ ТОЛЬКО КРЫЛЬЯ – МАЛО', 'По изречению Ильи Шевелёва', 'Знай, не всегда судьба нежна.</br>\nВот, вновь, желанье не совпало.</br>\nИметь нам крылья только – мало,</br>\nПогода лётная нужна.</br>', NULL, NULL, NULL),
(550, 5, 1, 'НУЖНО ВРЕМЯ', '«Современность – это то, что не проверено временем.» Алексис Романов', 'Современен друг – то, не заслуга.</br>\nНужно время, чтоб проверить друга.</br>', NULL, NULL, NULL),
(551, 5, 1, 'СТОЛЬ БЕРЕЖЛИВОЕ', NULL, 'Любви, свободы в нём смешенье,</br>\nТо – чувство кропотливое.</br>\nТерпимость – это отношенье</br>\nК душе, столь бережливое.</br>', NULL, NULL, NULL),
(552, 5, 1, 'ЦВЕТЫ И СОРНЯКИ', 'Мысли из Талмуда', 'Полна жизнь и цветов и сорняков,</br>\nВ ней смех и плачь, но нужно б уточнить:</br>\nПриходит кто в восторг от пустяков,</br>\nТого и мелочь может огорчить.</br>', NULL, NULL, NULL),
(553, 5, 1, 'О РОСКОШИ', 'По высказыванию Анри Дювернуа', 'Глупцы рождают пагубное чувство,</br>\nЧто роскошь может заменить искусство.</br>', NULL, NULL, NULL),
(554, 5, 1, 'РАЗ НЕ ДОРОС', 'По изречению В. Брудзинского', 'В своём пути,</br>\nКто до твоих всех предрассудков не дорос.</br>\nЕго терпи!</br>\nК таким как он не нужен строгий спрос.</br>', NULL, NULL, NULL),
(555, 5, 1, 'ЗАГАДКА', NULL, 'Вся наша жизнь загадка,</br>\nВ ней всё не так уж гладко:</br>\nИ расставаться горько,</br>\nИ любится не сладко.</br>', NULL, NULL, NULL),
(556, 5, 1, 'НАСТОЯЩАЯ БЕДНОСТЬ', NULL, 'По-настоящему, лишь бедность искусили:</br>\nТе, у кого – ни денег, ни ума, ни силы.</br>', NULL, NULL, NULL),
(557, 5, 1, 'ЛОЖЬ', NULL, 'Читают часто нам словесную тираду,</br>\nНо своим мненьем речь ту подытожь.</br>\nЕсть люди без уменья видеть правду,</br>\nЗато прекрасна и так искренна их ложь.</br>\n', NULL, NULL, NULL),
(558, 5, 1, 'ЛЮБИМОЕ ЗАНЯТИЕ', 'По изреченью Вильгельма Фишера', 'Пустяк ничтожный, но о нём раструбят,</br>\nПростая щепка, закричат: Бревно!</br>\nИз чёрной точки люди часто любят</br>\nРаздуть большое чёрное пятно.</br>', NULL, NULL, NULL),
(559, 5, 1, 'СЛУЧАЕТСЯ', NULL, 'Случается, что очень много рвения,</br>\nНо убеждения развесят свои шоры.</br>\nСлучается, что наша точка зрения</br>\nНе совпадает с точкой собственной опоры.</br>', NULL, NULL, NULL),
(560, 5, 1, 'ЛЕГЧЕ УВЕРНУТЬСЯ ОТ СЛОНА', 'По изречению Генри Шоу', 'Нас часто отгоняют ото сна,</br>\nИ мелочи, и слухи.</br>\nНам легче увернуться от слона</br>\nПорою, чем от мухи.</br>', NULL, NULL, NULL),
(561, 5, 1, 'ВСЁ ОТГАДАЕТСЯ', NULL, 'Всё отгадается, напрасно так стенаем,</br>\nНе просто ведь живём, а жизнь творим.</br>\nЧто будет – мы по прошлому познаем,</br>\nА скрытое – по ясному узрим.</br>', NULL, NULL, NULL),
(562, 5, 1, 'СИЛА ПРИВЫЧКИ', 'По изречению Жана де Лабрюйера', 'Великое нас удивляет,</br>\nА мелкое нам не приятно.</br>\nПривычка во всё нас влюбляет,</br>\nПривыкнешь и будет занятно.</br>', NULL, NULL, NULL),
(563, 5, 1, 'ФАНТАЗИЯ', NULL, 'У логики тяжёлая броня.</br>\nФантазия легка и с ней не сходна.</br>\nПуть логики простой, от А до Я.</br>\nФантазия ведёт куда угодно.</br>', NULL, NULL, NULL),
(564, 5, 1, 'МИР ФАНТАЗИИ', NULL, 'Мир фантазии уму необходим:</br>\nВ мире том, и счастлив, и любим,</br>\nВ мире том, куда б не кинул взор</br>\nВсюду видишь жаждущий простор.</br>', NULL, NULL, NULL),
(565, 5, 1, 'СМОТРИ', NULL, 'Проблему взяв за шкворень,</br>\nГотовь её крушину.</br>\nСмотри не только в корень,</br>\nСмотри и на вершину.</br>', NULL, NULL, NULL),
(566, 5, 1, 'ФАКТ', 'По изречению К. Мелихана', 'До скончания века,</br>\nЧто ни дай человеку,</br>\nВсё – мало!</br>\nИ, факт тот:</br>\nОн живёт!</br>\nЧто бы в жизни ни стало,</br>\nКак бы жизнь не ломала.</br>', NULL, NULL, NULL),
(567, 5, 1, 'ШИРОТА – ЭТО ФАКТОР', NULL, 'Широта – это фактор нам нужных исканий,</br>\nПринимай своих взглядов любой оборот.</br>\nС высоты глубина открывается знаний,</br>\nУглубляясь, достигнем мы тех же высот.</br>', NULL, NULL, NULL),
(568, 5, 1, 'ЧТО ДАЖЕ НЕ ПРИСНИТСЯ', 'По изречению Ильи Шевелёва', 'Порой, что невозможно,</br>\nПриходит в сон тревожный.</br>\nНо, в жизни так случится,</br>\nЧто даже не приснится.</br>', NULL, NULL, NULL),
(569, 5, 1, 'В ЛУКАВУЮ ДУШУ', 'По изречению из Библии Прем. 1.4.', 'В лукавую душу премудрость не входит.</br>\nНе селится в теле, погрязшем в грехе.</br>\nЦветы чистой мудрости там не восходят.</br>\nЛишь гнусы плодятся в гнилой шелухе.</br>', NULL, NULL, NULL),
(570, 5, 1, 'КОМЕДИЯ', NULL, 'Богатым обычно в наследие</br>\nОт счастья один суррогат.</br>\nИграется просто комедия</br>\nО счастье у тех, кто богат.</br>\n', NULL, NULL, NULL),
(571, 5, 1, 'ЛЮДИ БОЛЬШЕ', NULL, 'Исцелят от невежества мглы</br>\nДобродетельной жизни потоки.</br>\nЛюди больше глупы, а не злы.</br>\nЗаблуждение – вот их пороки.</br>', NULL, NULL, NULL),
(572, 5, 1, 'ХАОС ЖЕЛАНИЙ', 'По изречению А. Батиевского', 'В душе полно блужданий,</br>\nВсё хочет наша мысль,</br>\nНо, в хаосе желаний,</br>\nТеряешь жизни смысл.</br>', NULL, NULL, NULL),
(573, 5, 1, 'ОПЫТ', NULL, 'Горький опыт – родитель дознанья.</br>\nДостоверность – то мать его, ибо</br>\nНе рождённые опытом знанья</br>\nЕсть бесплодность и куча ошибок.</br>', NULL, NULL, NULL),
(574, 5, 1, 'О МАТЕРИИ И ДУХОВНОСТИ', 'Мысли из Ведических трактатов', 'В материи грубой, греховной –</br>\nСтрадание, несовершенство,</br>\nА качества жизни духовной –</br>\nПознание, Вечность, Блаженство.</br>', NULL, NULL, NULL),
(575, 5, 1, 'РЕЗЬБА НА КАМНЕ', 'По изречению из Талмуда', 'Исчерпни знания житейской канны,</br>\nПривей к зелёному растущему суку:</br>\nУченье в младости – резьба на камне,</br>\nУченье в старости – черченье по песку.</br>', NULL, NULL, NULL),
(576, 5, 1, 'О ЧЁМ МЫ ДУМАЕМ', NULL, 'О чём мы думаем сейчас – то есть наш путь.</br>\nО, сколько за день мы путей перебираем!</br>\nВ любую сторону пытаемся шагнуть</br>\nИ в суете сует кружа, порой сгораем.</br>\n</br>\nНо людям всем вокруг один положен путь.</br>\nПуть – к Богу, к Истине, к Духовному прозренью.</br>\nА суету сует нам нужно б зачеркнуть,</br>\nПредставив мыслям главный путь наш к рассмотренью.</br>', NULL, NULL, NULL),
(577, 5, 1, 'ЧТО ТОЛКУ', NULL, 'Что толку знать так много правил,</br>\nПо их бесплодности скорбя.</br>\nМудрец то дело б так исправил:</br>\nУж лучше знать не много правил,</br>\nНо те – полезны для тебя.</br>', NULL, NULL, NULL),
(578, 5, 1, 'СЛУЧАЙНОСТЬ – СОВОКУПНОСТЬ СЛЕДСТВИЙ', 'По изречению Клода Гельвеция', 'Причины не поймём тревог и бедствий,</br>\nЗакономерностей не видим, соответствий.</br>\nНаш разум то не в силах усмотреть:</br>\nСлучайность – схема, совокупность следствий.</br>', NULL, NULL, NULL),
(579, 5, 1, 'НИЧТО НЕ НЕВОЗМОЖНО', 'По изречению Ф. де Ларошфуко', 'Ничто не невозможно.</br>\nВсегда пути найдутся,</br>\nВедущие к тому.</br>\nИ с волей то не сложно.</br>\nС ней средства обретутся,</br>\nЧто разгоняют тьму.</br>', NULL, NULL, NULL),
(580, 5, 1, 'СВОЙСТВЕННОСТЬ НАШЕЙ ПРИРОДЫ', 'По изречению Мориса Метерлинка', 'Судьба твоя, как решето</br>\nПросеет успех и невзгоды,</br>\nНо, падает сверху лишь то,</br>\nЧто свойственно нашей природе.</br>', NULL, NULL, NULL),
(581, 5, 1, 'ТРИ ЧАСТИ', 'По изречению Д. Аддисона', 'Три части в жизни составляют счастье</br>\nИ в каждой наше главное участье:</br>\n---------------------------------------------------</br>\n1. Стараться в жизни от души трудиться.</br>\n2. Уметь любить и от любви светиться.</br>\n3. Надеяться на что-то, в это верить.</br>\n--------------------------------------------------</br>\nТри части счастья в силах мы отмерить.</br>', NULL, NULL, NULL),
(582, 5, 1, 'В НАС ПОЛОВИНА ТВОРЦА', 'По изречению Демокрита', 'В нас – половина удела,</br>\nВ нас – половина Творца:</br>\nСмелость – начало дела,</br>\nСлучай – хозяин конца.</br>', NULL, NULL, NULL),
(583, 5, 1, 'ПРАЗДНАЯ ЛЕНЬ', NULL, 'Всадников ратная мощь сгущена</br>\nЕле понятно, где солнце, где тень.</br>\nПраздников праздная жизнь лишена,</br>\nЕсли приятна нам праздная лень.</br>', NULL, NULL, NULL),
(584, 5, 1, 'НРАВСТВЕННОСТЬ', 'По изречению йога Рамачарака', 'Не интеллект владеет миром,</br>\nНе сила, не богатства сласть,</br>\nНе правит миром даже власть.</br>\nК нам нравственность взойдет кумиром.</br>', NULL, NULL, NULL),
(585, 5, 1, 'СОРОК ДНЕЙ БЕЗ ТРЕВОГ', 'По изречению из Талмуда\n', 'Рай посетить из нас бы каждый мог,</br>\nВзглянув на мир душою не хворая.</br>\nКто сорок дней прожил жизнь без тревог,</br>\nТот удостоился земного рая.</br>', NULL, NULL, NULL),
(586, 5, 1, 'ХВАЛЬБА', NULL, 'Кто демонстрирует другим, хвалясь:</br>\nСвой ум, богатство, прочие явления,</br>\nСкорей всего, тот не живет молясь,</br>\nИ, в сущности, далёк от просветления.</br>', NULL, NULL, NULL),
(587, 5, 1, 'КОЛОДЕЦ', NULL, 'Порой не видим звезд с вершин высоких гор,</br>\nХотя близки мы к ним, они ж ярки, как солнце.</br>\nМы обольщаем чаще ими нежный взор,</br>\nКогда находимся во глубине колодца.</br>', NULL, NULL, NULL),
(588, 5, 1, 'ТРИ СОСТОЯНИЯ', 'По изречению Хун Цзычен', 'В покое – мысли чистые</br>\nи видишь сердца истинную суть.</br>\nНо в праздности – податлив дух</br>\nи сердца истинные познаёшь истоки.</br>\nАв безмятежности – глубоких помыслов проложен путь,</br>\nОснову истинную сердца постигаешь, его токи.</br>\n</br>\nЧтоб сердца красотою овладеть,</br>\nЛучи впитать его златых сияний,</br>\nЧтоб подлинное сердце разглядеть,</br>\nНет ничего трёх лучше состояний.</br>', NULL, NULL, NULL),
(589, 5, 1, 'УТОМИТЕЛЬНАЯ БОРЬБА', NULL, 'Нет в лени радости и в скуке нет почёта,</br>\nНенадобность твоя раздавит мукой.</br>\nК тому ж лентяи не дают себе отчёта,</br>\nКак утомительна борьба со скукой.</br>', NULL, NULL, NULL),
(590, 5, 1, 'О ПРОТИВОПОЛОЖНОСТИ И ПРОТИВОРЕЧИЕ', 'Из учения Якова Беме', 'И что бы про зло не кумекали,</br>\nЕсть в нём замысел, а не вина…</br>\nНичто не появится в зеркале,</br>\nНе будь тёмной одна сторона.</br>', NULL, NULL, NULL),
(591, 5, 1, 'ТАЛАНТ', NULL, 'Талант – ответственная божеская ссуда.</br>\nТалант – энергия, что двигает сердца.</br>\nТалант – умение увидеть в мире чудо.</br>\nТалант – способность понимать творца.</br>', NULL, NULL, NULL),
(592, 5, 1, 'ЛУНА', NULL, 'С красавицей-луной знакомы все.</br>\nИ не изменят вид ее жара и стужа.</br>\nДостойно будет плыть в ночной красе.</br>\nВ каких она б не отражалась грязных лужах.</br>', NULL, NULL, NULL),
(593, 5, 1, 'НЕ СТАНЬ, КАК ПРИЗРАК', NULL, 'Бездарность – грех, сонливое зеванье.</br>\nНе стань как призрак, что без места своего.</br>\nУ каждого из нас своё призванье,</br>\nТалант наш в том, чтоб распознать его.</br>', NULL, NULL, NULL),
(594, 5, 1, 'СУДЬБА ПОРОЮ НЕГОДУЕТ', 'Мысли Джеймса Лоуэлла', 'Судьба порою негодует</br>\nИ не поможет вам никто,</br>\nКогда холодный ветер дует,</br>\nНаденьте тёплое пальто.</br>', NULL, NULL, NULL),
(595, 5, 1, 'ХУДОЖНИК', NULL, 'С бесстрастием к венцу и жгучим розгам,</br>\nХудожник сердцем осязает, а не мозгом.</br>\nС улыбкой думною, со страстными слезами,</br>\nХудожник образами мыслит, не словами.</br>\nВ свободе выбора души, в горячем рвенье,</br>\nХудожник в вечности живёт, живя в мгновенье.</br>', NULL, NULL, NULL),
(596, 5, 1, 'НЕ ОСУЖДАЙ ПИСАТЕЛЯ', NULL, 'Не осуждай в писателе, что жизнь его дурна,</br>\nЕго бунтарство, грешность, романтизм, небрежность.</br>\nТворенью своему он подарил всю нежность</br>\nИ всю любовь, что Господом дана.</br>', NULL, NULL, NULL),
(597, 5, 1, 'ПРОСЛАВИВШИ ВИЗИТЫ', NULL, 'Повсюду фокусы бесплодного фиглярства,</br>\nИм паразиты развлекают паразитов.</br>\nТак вкусим лучше чистой мысли яства,</br>\nДуховных рыцарей прославивши визиты.</br>', NULL, NULL, NULL),
(598, 5, 1, 'ЛЮБИТЬ В СЕБЕ ИСКУССТВО', 'По изречению К. Станиславского', 'Как снова гордость будет в буйстве,</br>\nНе растворяйся в этом чувстве.</br>\nУмей любить в себе искусство,</br>\nА не себя любить в искусстве.</br>', NULL, NULL, NULL),
(599, 5, 1, 'В ЖЕРТВУ', 'По изречению Ж. Кокто', 'Поэзией себя не услащают,</br>\nПоэзии себя не посвящают,</br>\nСебя ей преподносят в жертву</br>\nИ этим актом мир тот освещают.</br>', NULL, NULL, NULL),
(600, 5, 1, 'КРЫЛАТОЕ СЛОВО', 'По изречению В. Шойхера', 'Раз оставишь крылатое слово,</br>\nТо не будет душа одинока.</br>\nПовторят его снова и снова</br>\nИ восторг долетит до истока.</br>', NULL, NULL, NULL),
(601, 5, 1, 'КРАСОТА', NULL, 'Извечно миром красота воспета,</br>\nТа не изменится в любые времена,</br>\nВедь красота же не во внешности предмета,</br>\nА в чувстве, что внушает нам она.</br>', NULL, NULL, NULL),
(602, 5, 1, 'СЛОВО', 'По изречению из Библии Мф. 19.11.', 'Не всё слово вмещает сие,</br>\nНо его попадёт острие</br>\nВ сердца тех, раз кому то дано,</br>\nТам и выразит суть всю оно.</br>', NULL, NULL, NULL),
(603, 5, 1, 'ДОЙТИ ДО СМЫСЛА', NULL, 'Глубокой мысли видится окрас</br>\nЧрез опыт, что имеем во плоти.</br>\nНо если смысл вдруг не дошёл до нас,</br>\nСтараться нужно до него дойти.</br>', NULL, NULL, NULL),
(604, 5, 1, 'ПРИСЛУГА – КРИТИКА', 'По изречению Г. Матюшова', 'Вот снова гений занял свой престол,</br>\nНо где талант, там рядом и холуйство.</br>\nПрислуга – критика, мечтает сесть за стол,</br>\nГде царствует и пирствует искусство.</br>', NULL, NULL, NULL),
(605, 5, 1, 'УПРОСТИТ ВСЁ ТО, ЧТО СЛОЖНО', NULL, 'Усилье с опытом суть жизни прояснит.</br>\nПонятно станет всё и всё возможно.</br>\nПосредствен кто, простое усложнит,</br>\nУмён кто, упростит всё то, что сложно.</br>', NULL, NULL, NULL),
(606, 5, 1, 'РЕЧИ', NULL, 'Речи мудрых людей просты,</br>\nРечи умных людей – ясны.</br>\nЗаурядных людей – многословны,</br>\nЗлых людей – суетны и греховны.</br>', NULL, NULL, NULL),
(607, 5, 1, 'СЛОВО – МЁД И ЯД', NULL, 'Слово стоит и мёда, и яда.</br>\nЕго действо свершить всё готово.</br>\nКак влюбляемся с первого взгляда,</br>\nТак презреть можем с первого слова.</br>', NULL, NULL, NULL),
(608, 5, 1, 'ГЛУХ', NULL, 'Когда в голове много пагубной кисли,</br>\nТакой человек к вразумлению глух.</br>\nОн даже не знает, о чём его мысли,</br>\nПока не услышит, что выскажет вслух.</br>', NULL, NULL, NULL),
(609, 5, 1, 'В ДЕНЬ СУДА', 'По изречению из Библии Мф. 12.36', 'Доставила много вреда</br>\nКоварных речей борозда.</br>\nПусть знает душа без стыда:</br>\nЗа всякое праздное слово,</br>\nДадут ей ответ в день суда.</br>\n', NULL, NULL, NULL),
(610, 5, 1, 'ГУСТЫЕ КАМЫШИ', 'По изречению из Библии Пр. 18.7', 'Речь глупца – опасны кудряши,</br>\nЗаплетаются в губительном изгибе.</br>\nГлупого язык – его погибель.</br>\nГлупого уста – сеть для души.</br>', NULL, NULL, NULL),
(611, 5, 1, 'РЕЧЬЮ ДЛИННОЮ', 'По изречению отца А. Дюма', 'Кто бы как бы хорошо ни говорил,</br>\nЕсли речи очень долгие развил,</br>\nВсё равно, в конце концов, он глупость скажет,</br>\nРечью длинною ума он не покажет.</br>', NULL, NULL, NULL),
(612, 5, 1, 'ДВА СЛУЧАЯ ГОВОРИТЬ', 'По изречению Исократа', 'В двух случаях есть повод говорить:</br>\n1. Предмет речей обдуман, чтоб творить.</br>\n2. Сказать о том, что так необходимо.</br>\nВ других же случаях, вся болтовня стыдима,</br>\nМолчите лучше и идите мимо.</br>', NULL, NULL, NULL),
(613, 5, 1, 'ТИХО УЛЫБНЁТСЯ', 'По изречению И. В. Гёте и Библии Сир. 21.23.', 'Умник говорит, что мир весь – смех сплошной,</br>\nУмный от таких слов увернётся.</br>\nГлупый в смехе возвышает голос свой,</br>\nБлагородный муж лишь тихо улыбнётся.</br>', NULL, NULL, NULL),
(614, 5, 1, 'САТИРА', 'По изречению В. Жемчужникова', 'Ах, сатира, то ль в ней правда, то ль курьёз,</br>\nТо ль тепло в себе несёт, то ли – морозы.</br>\nЮмор, то понятно – смех до слёз,</br>\nА сатира означает – смех сквозь слёзы.</br>', NULL, NULL, NULL),
(615, 5, 1, 'КРЫЛЬЯ ИЛИ ГОРБ', NULL, 'Семья твоя – твои есть крылья,</br>\nНе делай так, чтоб был то горб.</br>\nС ней можешь покрываться пылью</br>\nИ можешь счастлив быть и горд.</br>', NULL, NULL, NULL),
(616, 5, 1, 'ЛУЧШЕ ЛЮБИТЬ', NULL, 'Старайся ту мысль в себя впитывать,</br>\nСтарайся её углубить:</br>\nЧто нужно детей не воспитывать,</br>\nИх нужно всем сердцем любить.</br>', NULL, NULL, NULL),
(617, 5, 1, 'ОПРОТИВЕЕТ МНОГОРЕЧИВЫЙ', 'По изречению из Библии Сир. 20.8', 'Как неприятен рот болтливый,</br>\nС речами из одних пустот,</br>\nЧто может запросто любого уморить</br>\nИ опротивеет многоречивый,</br>\nВозненавиден будет тот,</br>\nКто восхищает себе право говорить.</br>', NULL, NULL, NULL),
(618, 5, 1, 'В СОЮЗЕ С ЛЮБИМЫМ', NULL, 'В союзе с любимым благое верши,</br>\nТвоя то земная доля.</br>\nУ добрых супругов – две добрых души,</br>\nЕдиной должна быть воля.</br>', NULL, NULL, NULL),
(619, 5, 1, 'ПОСПЕШНЫЙ ШАГ', NULL, 'Не создавайте брак с испугу,</br>\nНе делайте поспешный шаг.</br>\nРаз сильной тяги нет друг к другу,</br>\nСемейный зачадит очаг.</br>', NULL, NULL, NULL),
(620, 5, 1, 'РАЗ НЕ ВЗЯЛ', NULL, 'Пусть вдвоём, но ты во мраке.</br>\nПусть дуэт, но вразнобой.</br>\nНе найдёшь ты счастья в браке,</br>\nРаз не взял его с собой.</br>', NULL, NULL, NULL),
(621, 5, 1, 'ЖЕНА', 'По древнеиндийскому изречению', 'Как слуга заботится жена,</br>\nКак гетера знает все запреты,</br>\nКак богиня красоты видна,</br>\nКак наставник подаёт советы,</br>\nКак и мать стремится, чтоб был сыт,</br>\nКак земля, вынослива, спокойна.</br>\n---------------------------------------------</br>\nС той женой хороший будет быт,</br>\nВ кой шесть лиц справляются достойно.</br>', NULL, NULL, NULL),
(622, 5, 1, 'ПРИПРАВА', NULL, 'Ох! Шутка пошлая – приправа в виде соуса,</br>\nНам подаёт её к столу развратный бес.</br>\nЕда роскошная – мышленье ниже пояса,</br>\nТа очень многих окрыляет до небес.</br>', NULL, NULL, NULL),
(623, 5, 1, 'ЧРЕЗ ДУШУ НЕЖНУЮ', NULL, 'Простой акт близости телесной монотонен</br>\nИ поиск счастья в том окажется напрасным.</br>\nЧрез чувства сильные любой момент бездонен.</br>\nЧрез душу нежную всё явится прекрасным.</br>\n', NULL, NULL, NULL),
(624, 5, 1, 'ЖЕНЩИНЫ', 'По изречению В. Сумбатова', 'Женщины – странные всё ж существа,</br>\nЛюбят чудесные звуки.</br>\nЕсли не тронут их ваши слова,</br>\nЗначит, не тронут и руки.</br>', NULL, NULL, NULL),
(625, 5, 1, 'КОСМЕТИКА', 'По изречению А. Мердос', 'Без печальных личико трещин,</br>\nКак лепестки семицветика.</br>\nВсё это чудо – косметика –</br>\nБоевая раскраска женщин.</br>', NULL, NULL, NULL),
(626, 5, 1, 'МОЖЕТ И НЕ СКЛЕИТСЯ', NULL, 'Если к тебе кто-то клеится,</br>\nНапрасно внутри чувство тлеется.</br>\nВдруг не захочет прилипнуть,</br>\nЗовя его, можешь охрипнуть.</br>', NULL, NULL, NULL),
(627, 5, 1, 'В ГЛАЗА', NULL, 'Раз мы любим, то смотрим друг другу в глаза.</br>\nВосторгаемся, если в них счастья слеза.</br>\nИ бывает, что смотрим мы также любя,</br>\nНо в глазах, что напротив, лишь видим себя.</br>', NULL, NULL, NULL),
(628, 5, 1, 'РАЗЛУКА', NULL, 'Кто жизнь изменил, наполняется новью,</br>\nКто очень стремится, всё сможет пройти.</br>\nРазлука – есть способ покончить с любовью.</br>\nРазлука – есть средство любовь ту спасти.</br>\n', NULL, NULL, NULL),
(629, 5, 1, 'ЧЕЛОВЕЧЕСКИЙ РАЗУМ', 'По изречению К. Стоун', 'Может сделать наш разум из всех нас Богов.</br>\nКуда хочешь, сумеет доставить.</br>\nЧеловеческий разум добьется всего,</br>\nЧто способен себе Он представить.</br>\n', NULL, NULL, NULL),
(630, 5, 1, 'ШКАЛА УДАЧИ', NULL, 'Поднимется жизни удачной шкала,</br>\nДружить только с разумом нужно.</br>\nРаз мысль вдохновенная в разум пришла,</br>\nДоверившись, действуй с ней дружно.</br>', NULL, NULL, NULL),
(631, 5, 1, 'ЧЕРСТВЫ ИЛИ СДОБНЫ', 'По изречению Ф. Честерфилда', 'Надейся больше на глаза и меньше всё ж на уши.</br>\nГотовят речь, чтоб показать суть правды или чуши.</br>\nИ выбирают те слова, что будут в том удобны,</br>\nА, видя, знает голова, черствы слова иль сдобны.</br>', NULL, NULL, NULL),
(632, 5, 1, 'ТОЧИЛЬНЫЙ КАМЕНЬ', NULL, 'Слова с дурным зарядом,</br>\nЛетят порой, как пламень,</br>\nТы должен знать, кто рядом</br>\nЕсть твой точильный камень.</br>', NULL, NULL, NULL),
(633, 5, 1, 'СВЯТАЯ БОЛЬ', NULL, 'Я благодарен боли, что сейчас дана,</br>\nЧерез неё добьюсь заветной цели,</br>\nПускай терплю её я еле-еле,</br>\nНо только так дорога истинно верна.</br>', NULL, NULL, NULL),
(634, 5, 1, 'МИР ЗАБОТИТСЯ ОБО МНЕ', NULL, 'Мой мир меня оберегает,</br>\nОстерегает от проблем.</br>\nВсё, что хочу, он предлагает</br>\nИ восторгает бытием.</br>\n</br>\nКогда иду ему навстречу,</br>\nМир катится навстречу мне,</br>\nКогда добром ему отвечу,</br>\nМир благодарен мне вдвойне.</br>', NULL, NULL, NULL),
(635, 5, 1, 'НАКОПЛЕНИЕ ЧЕЛОВЕЧЕСТВА', NULL, 'Накопило наше светлое величество</br>\nВсяких знаний бесконечное количество,</br>\nСтолько ж вредных накопило утверждений,</br>\nБесполезных миру глупых заблуждений.</br>', NULL, NULL, NULL),
(636, 5, 1, 'СТАРАЙСЯ', NULL, 'Старайся обиду унять,</br>\nСтарайся всех просто понять.</br>', NULL, NULL, NULL),
(637, 5, 1, 'ПОГОДА И СВОБОДА', NULL, 'В любое время года,</br>\nВ душе – моя погода!</br>\nНе лгу – а это право,</br>\nВручает мне свобода!</br>', NULL, NULL, NULL),
(638, 5, 1, 'А ЛУЧШЕ', NULL, 'А лучше всё же думать больше о себе.</br>\nА лучше то иметь, что хочется тебе:</br>\nРаз нет стремленья, нет и возвышенья,</br>\nРаз нет задач, то нет и их решенья,</br>\nРаз нет желанья, нет его свершенья.</br>', NULL, NULL, NULL),
(639, 5, 1, 'ОТЗОВИ', NULL, 'От грешного зуда себя отзови,</br>\nЧем больше мы судим, тем меньше любви.</br>', NULL, NULL, NULL),
(640, 5, 1, 'СЕРДЦЕ И ХАРАКТЕР', 'По изречению Ильи Шевелёва', 'Судя порой по всему,</br>\nСердце диктует уму,</br>\nХарактер командует сердцем,</br>\nТак разгони полутьму.</br>', NULL, NULL, NULL),
(641, 5, 1, 'МЫСЛЬ', NULL, 'Если мысль твоя пошла на самотёк,</br>\nМчатся счастье и удача наутёк.</br>\nЕсли строгое у мысли направление,</br>\nБудут рядом и успех и вдохновение.</br>', NULL, NULL, NULL),
(642, 5, 1, 'СТРАДАНИЕ', NULL, 'Есть боль и её оправдание.</br>\nНе нужно роптать на страдание.</br>\nПройди сквозь болото на сушу,</br>\nОчисти страданием душу.</br>', NULL, NULL, NULL),
(643, 5, 1, 'ДА СТЫДНО МНЕ БУДЕТ', 'По французскому изречению', 'От злой суматохи Господь пусть разбудит.</br>\nРаз, думаю плохо, да стыдно мне будет.</br>', NULL, NULL, NULL),
(644, 5, 1, 'НЕ СОТВОРИ', NULL, 'Как все!</br>\nТы – искра очага.</br>\nКак все!</br>\nТы – уникальность мира.</br>\nНе сотвори себе врага.</br>\nНе возведи себе кумира.</br>', NULL, NULL, NULL),
(645, 5, 1, 'МИР – ЗЕРКАЛО', NULL, 'Как зеркало мир отражает</br>\nТвоё отношенье к нему:</br>\nВзвалил недовольства суму</br>\nИ мир недовольство ссужает.</br>\n</br>\nТяжелую строим судьбу,</br>\nНевзрачными красим тонами:</br>\nИ мир этот борется с нами,</br>\nРаз мы выбираем борьбу.</br>\n</br>\nПоверь, ты – судьбы господин.</br>\nОставь напряженную сечу</br>\nИ мир улыбнётся навстречу,</br>\nВ желанье с тобой он един!</br>', NULL, NULL, NULL),
(646, 5, 1, 'ИСПЫТАНИЕ', NULL, 'Гуляет по земле в веках предание,</br>\nГосподь Всевышний шлет нам испытания,</br>\nЧто с нашей силой соразмерены точь-в-точь.</br>\nЗнать, став мудрее, мы должны их превозмочь!</br>', NULL, NULL, NULL),
(647, 5, 1, 'ОТШЛИФУЕМ ХАРАКТЕР', NULL, 'Так как с кем-то постоянно мы в контакте,</br>\nОтшлифуем друг о друга свой характер.</br>', NULL, NULL, NULL),
(648, 5, 1, 'ЗАКОН', NULL, 'Мы неизбежно ждём</br>\nНаграды или бедствий.</br>\n«Что сеем, то и жнём!» –</br>\nЗакон причин и следствий.</br>', NULL, NULL, NULL),
(649, 5, 1, 'СКОРО ОН ПРИДЁТ', NULL, 'Раз добра в нас мало – в гости злость бредёт.</br>\nРаз мы ждём скандала – скоро он придёт.</br>', NULL, NULL, NULL),
(650, 5, 1, 'УПРЁКАМ', NULL, 'Я больше никого не упрекну!</br>\nОбидам, злости вход в себя замкну.</br>\nЯ душу чистым светом озарю.</br>\nЛюблю. Прощаю. Всех благодарю.</br>', NULL, NULL, NULL),
(651, 5, 1, 'НЕ БЛАГОДАРНОЕ ДЕЛО', NULL, 'Часто мы используем умело</br>\nСправедливой критики пальбу,</br>\nНо не благодарное то дело.</br>\nНаложи на осуждение табу.</br>', NULL, NULL, NULL),
(652, 5, 1, 'МЕРЗОСТЬ ДУШ', 'По изречению из Библии Сир. 27. 33', 'Злоба с гневом – мерзость мелких душ.</br>\nОвладеть тем сможет грешный муж.</br>', NULL, NULL, NULL),
(653, 5, 1, 'ВЕРА, НАДЕЖДА, ЛЮБОВЬ', NULL, 'В счастье надо поверить</br>\nИ когда очень туго,</br>\nИ когда очень плохо,</br>\nВсюду тьма, нет добра.</br>\nЗло не надобно мерить,</br>\nЗдесь любовь вам – подруга,</br>\nВера – с каждого вздоха,</br>\nИ надежда – с утра!</br>', NULL, NULL, NULL),
(654, 5, 1, 'УПРАВЛЕНИЕ РЕАЛЬНОСТЬЮ', NULL, 'Хватит вращать карусель,</br>\nХватит нам жить машинальностью.</br>\nСмысл нашей жизни и цель –</br>\nЕсть управленье реальностью.</br>', NULL, NULL, NULL),
(655, 5, 1, 'КОНЧИТСЯ СТЫДОМ', 'По изречению Б. Франклина', 'Началось засевом, кончится плодом.</br>\nНачалось что гневом, кончится стыдом.</br>', NULL, NULL, NULL),
(656, 5, 1, 'В ТЕБЕ ПРИЧИНА', NULL, 'Мир идеален, добр, прекрасен!</br>\nВо всём гармония и красота!</br>\nРаз видишь грязь: мир тёмен и опасен,</br>\nВ тебе причина и нечистота.</br>', NULL, NULL, NULL),
(657, 5, 1, 'ПОЗВОЛЬ', NULL, 'Как жить, спорим зря в разнобой,</br>\nСвой вывод считая благим.</br>\nПозволь же себе быть собой,</br>\nДругому позволь быть другим.</br>', NULL, NULL, NULL),
(658, 5, 1, 'ДВЕРЬ К СЧАСТЬЮ', NULL, 'Ты к счастью можешь дверь открыть</br>\nУмением счастливым быть!</br>', NULL, NULL, NULL),
(659, 5, 1, 'СТРАХ', NULL, 'Не допускайте в душу страх.</br>\nВсе двери на засов!</br>\nЕго крыла опасен взмах,</br>\nЕго навязчив зов.</br>\n</br>\nА если с верою живешь,</br>\nИ с целью впереди,</br>\nТо долгий, славный путь пройдёшь</br>\nС бесстрашием в груди!</br>', NULL, NULL, NULL),
(660, 5, 1, 'Я ВЕРЮ', NULL, 'Я верю!</br>\nЖизнь прекрасна и добра!</br>\nНе мерю,</br>\nВыбираю, что угодно!</br>\nСегодня</br>\nДелаю всё лучше,</br>\nЧем вчера!</br>\nА завтра</br>\nСделаю всё лучше,</br>\nЧем сегодня!</br>', NULL, NULL, NULL),
(661, 5, 1, 'ПОБУДИТ К ИЗМЕНЕ', NULL, 'Бывает, что безверие ведёт жизнь к перемене.</br>\nБывает, недоверие побудит и к измене.</br>', NULL, NULL, NULL),
(662, 5, 1, 'МЫ САМИ СОЗДАЕМ СТРАДАНИЯ', NULL, 'Мы сами создаем себе страдания,</br>\nКогда за истину свои же принимаем мысли,</br>\nИ верим, что – несчастные избрания,</br>\nКогда накрутим, вдруг, истории из кисли.</br>\n</br>\nНам должно знать, земные обстоятельства</br>\nТоскливой, бедной жизнь не сделают нисколько!</br>\nКогда, лишь, в тело явные вмешательства,</br>\nФизическая боль проникнет в нас и только!</br>\n</br>\nВ терзаньях суть от тех интерпретаций</br>\nНесут, что пагубность, печаль, разлад, тревогу.</br>\nОт множества плачевных аннотаций,</br>\nКоторым также расчищаем мы дорогу.</br>\n</br>\nПоймите, горести приносят только мысли!</br>\nЧто в нашей голове сейчас повисли,</br>\nНет! Не условности, не жизненные нормы!</br>\nМы обретаем сами негатива формы.</br>', NULL, NULL, NULL),
(663, 5, 1, 'ГОРЕ – НЕ МОРЕ', NULL, 'Грусть в твоём взоре,</br>\nВнешность бледна…</br>\nГоре – не море,</br>\nВыпьешь до дна.</br>', NULL, NULL, NULL),
(664, 5, 1, 'НЕ ПРИУЧАЙ УСТ', 'По изречению из Библии Сир. 23.16', 'Не приучай к невежливости своих уст,</br>\nГреховных слов в них разрастётся колкий куст.</br>', NULL, NULL, NULL),
(665, 5, 1, 'ЗЛУ НЕ ПРОТИВЬСЯ', NULL, 'У человечества есть истина высокая:</br>\nЗлу не противься! – мудрость в том глубокая:</br>\nЕсть истинное чудо в неприятности,</br>\nСокрыто благо в жизненной превратности.</br>\nВнутри тебя, вовне – она тот факт являет:</br>\nПринятие того, к добру всё изменяет!</br>', NULL, NULL, NULL),
(666, 5, 1, 'ПЕРЕНЕСИ С ДОСТОИНСТВОМ', NULL, 'Бывают дни, когда всё в жизни сложно,</br>\nНет облегченья, сколько не проси:</br>\nРаз изменить такое невозможно,</br>\nС достоинством ту боль перенеси.</br>', NULL, NULL, NULL),
(667, 5, 1, 'МЫ ДУМАЕМ', NULL, 'Мы думаем, что счастье – в наслажденье.</br>\nНет. Счастье – от тревог освобожденье.</br>', NULL, NULL, NULL),
(668, 5, 1, 'НАУЧИСЬ СТРАДАТЬ', NULL, 'Не всё есть – благодать,</br>\nНо стань-ка терпеливым</br>\nИ научись страдать,</br>\nРаз хочешь быть счастливым.</br>', NULL, NULL, NULL),
(669, 5, 1, 'БЛЕФ ЭГОИЗМА', NULL, 'Есть много тонких форм страдания,</br>\nКоторым мы находим оправдания:</br>\nНетерпеливость, ревность, возмущенье, гнев…</br>\nТе формы – эгоизма блеф!</br>\n</br>\nНо есть у них</br>\nНачало проявления.</br>\nТа весть и миг,</br>\nНамёк на избавление.</br>\n</br>\nСтрадания плодят</br>\nБоль нам и боль другим,</br>\nЧтоб не сразил их яд,</br>\nЗакрой дорогу им!</br>', NULL, NULL, NULL),
(670, 5, 1, 'ПРОГЛОТИВ ОБИДУ', NULL, 'Не поддавайся ядовитому флюиду,</br>\nСебя сгрызёшь ты, проглотив обиду.</br>', NULL, NULL, NULL),
(671, 5, 1, 'ПОЗВОЛЬ ТАК БЫТЬ', NULL, 'Не порождай эмоционального стеснения.</br>\nПрими таковость каждого мгновения.</br>\nМентальности не вешай на событий вилы.</br>\nОткрой в себе немыслимый источник силы.</br>\n</br>\nК примеру: в грязь внезапно поскользнулся,</br>\nПролил вдруг чай, на поезд не проснулся.</br>\nНикак не именуй ты то событие.</br>\nПозволь так быть! В том мудрое открытие.</br>', NULL, NULL, NULL),
(672, 5, 1, 'УЧИТЬСЯ ЖИТЬ', NULL, 'Уметь страдать – от Бога нам заданье;</br>\nУчиться жить и в выси и на дне.</br>\nРаз тяжело перенести страданья,</br>\nОт этого страдаешь ты вдвойне.</br>', NULL, NULL, NULL),
(673, 5, 1, 'ПОТЕРЯ', NULL, 'Какая бы потеря в жизни не стряслась,</br>\nНе отрицай!</br>\nНе игнорируй боли!</br>\nТебя так не щадящей.</br>\nДа! Пустота за этим горем разрослась.</br>\nПриняв ее,</br>\nТы ощутишь:</br>\nПокой и мир оттуда исходящий.</br>', NULL, NULL, NULL);
INSERT INTO `content` (`id`, `id_cat1`, `id_cat2`, `title`, `comment`, `text`, `keywords`, `description`, `lang`) VALUES
(674, 5, 1, 'КОНТРОЛИРУЙ РЕЧЬ', 'Артха–Веды', 'Хочешь счастье уберечь – контролируй свою речь.</br>', NULL, NULL, NULL),
(675, 5, 1, 'ЖИЗНЬ БЕЗ РАДОСТИ', NULL, 'Не предавайтесь истощающей печали.</br>\nНе отдавайтесь горестной тоске.</br>\nОставьте мнительность в затерянном леске.</br>\nСердца у многих без веселья одичали</br>\nИ жизнь без радости висит на волоске.</br>', NULL, NULL, NULL),
(676, 5, 1, 'ВРЕМЯ И ДЕЛО', NULL, 'Если вдруг сильно жизнь искалечит,</br>\nПомни, время и дело нас лечит.</br>', NULL, NULL, NULL),
(677, 5, 1, 'УМЕНИЕ ДЕРЖАТЬСЯ', NULL, 'Мы не должны по жизни раздражаться:</br>\n«Спокойствие, умение держвться!»</br>\nВ момент раздора слейся с небесами.</br>\nИх чистота и высь придут к вам сами.</br>', NULL, NULL, NULL),
(678, 5, 1, 'ГРУСТЬ', NULL, 'Невидимы границы…</br>\nПечалей вереницы…</br>\nДушевные дробницы…</br>\nСердечные темницы.</br>\n</br>\nСредь жизненных долин,</br>\nГрусть – жесткий властелин.</br>', NULL, NULL, NULL),
(679, 5, 1, 'УПРАВЛЯЙ НАСТРОЕНИЕМ', NULL, 'Раз не можешь настроеньем управлять,</br>\nНастроение начнёт повелевать.</br>', NULL, NULL, NULL),
(680, 5, 1, 'ЗОЛОТОЙ ВОДОПАД', NULL, 'Справлюсь в мыслях с зелёной тревогой,</br>\nРазрастусь в сердце розовым садом,</br>\nОбернусь серебристой дорогой,</br>\nПонесусь золотым водопадом.</br>', NULL, NULL, NULL),
(681, 5, 1, 'ЗАНЯТАЯ ПЧЕЛА', 'По изречению В. Блейка', 'Полна жизнь кабалы,</br>\nПолны забот котлы,</br>\nПолны печалей торбы.</br>\n-------------------------------</br>\nНо чьи-то дни светлы:</br>\nУ занятой пчелы</br>\nНет времени для скорби.</br>', NULL, NULL, NULL),
(682, 5, 1, 'ОТ МАЛЕНЬКИХ ОБИД', 'По изречению Максима Горького', 'От разных маленьких обид-печалей.</br>\nПоберегите душу изначально.</br>', NULL, NULL, NULL),
(683, 5, 1, 'МАНТРА НАСТРОЕНИЯ', NULL, 'Не впитывай, что портит настроение!</br>\nНе обращай на глупости внимание!</br>\nЦени добро и мудрое суждение:</br>\nОни пусть озарят твое сознание!</br>', NULL, NULL, NULL),
(684, 5, 1, 'ПОСТОЙ', 'По изречению А. Давидовича', 'Не нужно унывать, постой!</br>\nСмени движением застой!</br>\nНесчастья вытесняют счастье,</br>\nНаполнив душу пустотой.</br>', NULL, NULL, NULL),
(685, 5, 1, 'ЛАСКОВЫЙ ТЕЛЁНОК', 'По русскому изречению', 'Кто всех добрей, того по жизни Бог спасёт.</br>\nДвух матерей телёнок ласковый сосёт.</br>', NULL, NULL, NULL),
(686, 5, 1, 'НЕ ПРОКЛИНАЙ', NULL, 'Обидят, но не надо проклинать:</br>\nОпасны и страшны тебе же эти вещи.</br>\nОстерегись злой силой пеленать,</br>\nОна назад порой ножа ударит резче.</br>', NULL, NULL, NULL),
(687, 5, 1, 'В ДОЛГ', NULL, 'Отчаянье послал нам Бог.</br>\nЧтоб новая проснулась жизнь.</br>\nТы с тем явлением дружи:</br>\nОтдаст оно, что взяло в долг.</br>', NULL, NULL, NULL),
(688, 5, 1, 'СВОБОДОЙ ВОЗЛЮБИМ', NULL, 'Свободой станет возлюбим и ею возносим,</br>\nТот, кто – не раб страстям своим и прихотям чужим.</br>', NULL, NULL, NULL),
(689, 5, 1, 'ПРОСНИСЬ', NULL, 'Зря думаешь, что – маленький зверек!</br>\nИ даже шороха бумажного боишься.</br>\nТебя ж слоном потенциал нарёк.</br>\nПроснись! Себе не тем ты снишься!</br>', NULL, NULL, NULL),
(690, 5, 1, 'В НЕСЧАСТЬЕ ПОЗНАЁМ СЕБЯ', NULL, 'Несчастье – жизненный турнир,</br>\nГде душу крепко теребя,</br>\nГде сердце чуткое дубя,</br>\nБичуют, больно и дробя.</br>\n</br>\nНо после, жизни эликсир</br>\nНесчастье дарит нам, любя.</br>\n</br>\nВ несчастье познаём мы мир.</br>\nВ несчастье познаём себя.</br>', NULL, NULL, NULL),
(691, 5, 1, 'НЕ ГРЯЗНИ', 'По изречению из Библии Сир. 30.27', 'Душу свою этим злом не черни;</br>\nРевность и гнев сократят твои дни.</br>', NULL, NULL, NULL),
(692, 5, 1, 'ОДОЛЕЙ СЕБЯ', NULL, 'Как же сможешь ты стать Господином всего?</br>\nРаз не в силах себя одолеть самого.</br>\nКак под прессом эмоций бурлящих</br>\nЖизнь создашь из успехов блестящих?</br>\n</br>\nКак же станешь добром озарять этот свет?</br>\nКогда власти твоей над собой даже нет.</br>\nКогда страсть в тебе греет невежду,</br>\nКак путь кажешь? Как явишь надежду?</br>', NULL, NULL, NULL),
(693, 5, 1, 'ГОРНИЛО БЕДСТВИЙ', NULL, 'Чтоб пыл твой в бедах не угас,</br>\nФилософ Адамс сообщает:</br>\nГорнило бедствий очищает,</br>\nОблагораживает нас.</br>\n', NULL, NULL, NULL),
(694, 5, 1, 'ЗРЯ', NULL, 'Зря болтать всегда привык, зря в речах так пылок,</br>\nТот, кто чешет свой язык, чешет и затылок.</br>', NULL, NULL, NULL),
(695, 5, 1, 'СЛУЧАЙНОСТИ', NULL, 'Даже вредные, ненужные случайности</br>\nПеред светлым преклоняются умом.</br>\nПогрозив, они меняют крайности,</br>\nПринося простор и радость в дом.</br>', NULL, NULL, NULL),
(696, 5, 1, 'ЛОЗУНГ НИЦШЕ', NULL, 'Пусть тяжело порой бывает.</br>\nВсё горестней и всё больней.</br>\nНо! Что меня не убивает,</br>\nТо делает в сто раз сильней!</br>', NULL, NULL, NULL),
(697, 5, 1, 'ЧРЕЗ БОЛЬ СТРАДАНЬЯ', NULL, 'Недаром говорят: Всё к лучшему, поверь!</br>\nЧрез боль страданья в новый мир откроешь дверь.</br>', NULL, NULL, NULL),
(698, 5, 1, 'ВПЕРЕД К ПРАВДИВОСТИ', NULL, 'От лицемерия, бесчестности устали все.</br>\nКак это крепко въелось в отношения!</br>\nОставим грубости пещерной приношения.</br>\nВперёд! К правдивости и внутренней красе.</br>', NULL, NULL, NULL),
(699, 5, 1, 'ЧАСЫ НУЖДЫ', NULL, 'Забудь часы нужды! Кому они нужны?</br>\nНо! Не забудь! Что всё ж они важны:</br>\nНе зря тот путь тебе с небес вручили,</br>\nЧасы нужды чему-то научили.</br>', NULL, NULL, NULL),
(700, 5, 1, 'ЧАСТЬ ЛЮБВИ', NULL, 'Дружбу ссорою не рви, ссора – это часть любви.</br>', NULL, NULL, NULL),
(701, 5, 1, 'ПОКОЙ', NULL, 'Взывая покой вместо суетных дум,</br>\nНаходишь в покое совет.</br>\nВливая покой в свое сердце и ум,</br>\nРождаешь Божественный свет!</br>', NULL, NULL, NULL),
(702, 5, 1, 'ПОЛЕЗНЫЕ ПЛОДЫ', NULL, 'Пусть всё проходит вскоре,</br>\nНо всё ж в минувшем горе</br>\nТы находи полезные плоды,</br>\nВсё верно истолкуй,</br>\nИ в нём оружье куй</br>\nПротив другой грядущей вновь беды.</br>', NULL, NULL, NULL),
(703, 5, 1, 'БОЯЗНЬ', NULL, 'Но раз бояться можно нам всего,</br>\nТо лучше не бояться ничего.</br>', NULL, NULL, NULL),
(704, 5, 1, 'ПРЕВЗОЙДИ СЕБЯ', NULL, 'Зачем завидовать, зачем же подражать,</br>\nЗачем других пытаться превзойти?</br>\nНужней свои таланты выражать.</br>\nВажней себя превосходя найти!</br>', NULL, NULL, NULL),
(705, 5, 1, 'ТУПИКОВОЕ МЫШЛЕНИЕ', 'По изречению О. Муравьевой', 'У каждого своё решение</br>\nИ каждый в жизни свой даёт обет.</br>\nЗнай, ситуаций тупиковых нет,</br>\nЕсть просто тупиковое мышление.</br>', NULL, NULL, NULL),
(706, 5, 1, 'ЛЕКАРСТВО ОТ ОБИДЫ', NULL, 'От темноты спасает освещение,</br>\nА от обиды вылечит прощение.</br>', NULL, NULL, NULL),
(707, 5, 1, 'ИЛИ СМЕРТЬ, ИЛИ ДЕЛО', NULL, 'Когда отчаянье к тебе заглянет в дом,</br>\nНамереньем его ты должен быть ведом.</br>\nСам выбирай, что с ним к тебе грядёт:</br>\nОно иль к краху, или к делу приведёт.</br>', NULL, NULL, NULL),
(708, 5, 1, 'НЕРВОЗНОСТЬ', NULL, 'Даже самый мудрейший по праву,</br>\nЗдравомыслящий, кроткий по нраву,</br>\nПрорастивши нервозности колос,</br>\nГлушит умного разума голос.</br>', NULL, NULL, NULL),
(709, 5, 1, 'ПЕЧАЛЬНЫЕ МЫСЛИ', 'По изречению Э. Хамингуэя', 'Печальные мысли – дурман,</br>\nВ сердце они зря лелеялись.</br>\nПечальные мысли – туман,</br>\nСолнце взошло – и рассеялись.</br>', NULL, NULL, NULL),
(710, 5, 1, 'ВРЕМЯ ЛЕЧИТ', NULL, 'Умчатся печали-тираны.</br>\nИсчезнут следы от плетей.</br>\nВерь, время залечит раны</br>\nЗаботами будущих дней.</br>', NULL, NULL, NULL),
(711, 5, 1, 'ПЧЕЛА САРИЦА', NULL, 'Сознание наше – пчела царица!</br>\nМысли – то улей, что следом мчится,</br>\nГде она сядет, прильнут мысли, нервы.</br>\nСознание правит, решая все первым.</br>\n</br>\nКуда нам лететь отнеситесь с вниманьем,</br>\nДля этого важен контроль над сознаньем.</br>', NULL, NULL, NULL),
(712, 5, 1, 'УКРОТИ', NULL, 'Наши буйства, как злые быки.</br>\nНаши нервы, как волчьи клыки.</br>\nНаши страсти, как буря в пути.</br>\nТы же можешь, возьми, укроти!</br>', NULL, NULL, NULL),
(713, 5, 1, 'СОУЧАСТНИК', NULL, 'Ну признайтесь же сердечно,</br>\nЧтобы жаловаться вечно,</br>\nТы готов искать несчастий,</br>\nВ тебе – доля соучастий.</br>', NULL, NULL, NULL),
(714, 5, 1, 'МЕДИТАТИВНОСТЬ', NULL, 'Блокируй в храм души всю негативность.</br>\nОт злых корней ее старайся защищаться.</br>\nПрими покой, зажги медитативность.</br>\nВ огне спокойствия ты будешь очищаться.</br>', NULL, NULL, NULL),
(715, 5, 1, 'ПРЕКРАСНОЕ ТАК ПРОСТО НЕ ДАЁТСЯ', NULL, 'Кому-то легче всё ж несчастным быть,</br>\nИ даже нравится, что реже он смеётся.</br>\nПорой для счастья нужно землю рыть.</br>\nНичто прекрасное так просто не даётся.</br>', NULL, NULL, NULL),
(716, 5, 1, 'ЧТО ВЫБРАТЬ', NULL, 'Тревожные мысли – опасные силы,</br>\nНас точат и рушат внутри.</br>\nПронзают насквозь ядовитые вилы</br>\nС добром, побеждая пари.</br>\n</br>\nА мысли: о благе, о смысле, о счастье,</br>\nНапротив, нам радость дают.</br>\nИ право за нами, что выбрать, напастье?</br>\nИль светлый покой и уют?</br>', NULL, NULL, NULL),
(717, 5, 1, 'ЕСЛИ ПЛОХО БУДЕТ ТЕБЕ', NULL, 'Не селись у тоски в избе, –</br>\nЭто скажет тебе любой.</br>\nЕсли плохо будет тебе,</br>\nТо другим будет плохо с тобой.</br>', NULL, NULL, NULL),
(718, 5, 1, 'БОЛЕЗНЕННЫЕ ЛЮДИ', 'По изречению Ф. Ницше', 'Знай!</br>\nЖители мрачных берлог,</br>\nСкитальцы унылых дорог,</br>\nНесчастные, сбитые с ног,</br>\nНе верящие, что есть Бог,</br>\nРаздавленные и пустые,</br>\nНамного опасней, чем злые.</br>', NULL, NULL, NULL),
(719, 5, 1, 'ХОЗЯИН МНЕНИЯ', NULL, 'Ничто мне не испортит настроения!</br>\nМогу себе такое предрекать.</br>\nЕсть ситуация, но я – хозяин мнения:</br>\nРасстраиваться или обтекать.</br>', NULL, NULL, NULL),
(720, 5, 1, 'ИСПЫТАННЫЙ СОВЕТ', NULL, 'Мысли мрачные насильно не гони,</br>\nЭто будут жалкие усилия,</br>\nЧем применишь больше ты насилия,</br>\nТем скорей назад придут они.</br>\n</br>\nНо, воспользуйся испытанным советом:</br>\nНи к чему быть с чернотой в борьбе,</br>\nТо само исчезнет по себе.</br>\nНаполняйся просто Божьим светом.</br>', NULL, NULL, NULL),
(721, 5, 1, 'ДЫХАНИЕ ЖИЗНИ', NULL, 'Грудь от счастья расправится,</br>\nСожмётся сердечко от горя.</br>\nДыханию жизни вторя,</br>\nТы с этим обязан справиться.</br>', NULL, NULL, NULL),
(722, 5, 1, 'ХОРОШИЙ НАСТРОЙ', NULL, 'Хороший настрой тонизирует ум.</br>\nВ покое – духовный рассвет.</br>\nМудрейший союз дарит искренность дум</br>\nИ радужной Истины свет.</br>', NULL, NULL, NULL),
(723, 5, 1, 'ШПОРЫ И КРЫЛЬЯ', NULL, 'Бывает, участь кобылья,</br>\nПечали – наши шпоры.</br>\nБывает, ревут моторы,</br>\nРадости – наши крылья.</br>', NULL, NULL, NULL),
(724, 5, 1, 'ПУСКАЙ', NULL, 'Пускай глаза мои не знают горьких слез,</br>\nБесчувственным к обидам станет слух</br>\nИ голос зазвучит без жалоб и угроз,</br>\nА в сердце расцветёт здоровый дух.</br>', NULL, NULL, NULL),
(725, 5, 1, 'ЖИЗНЬ БЕЗ РАДОСТИ', NULL, 'Такова вот людская мораль:</br>\nЖизнь без радости пусто проходит,</br>\nТы без пользы живёшь, так выходит,</br>\nРаз вокруг тебя мрак и печаль.</br>', NULL, NULL, NULL),
(726, 5, 1, 'СОВЕРШЕНСТВО', NULL, 'Достигнет совершенства тот,</br>\nКто подчинит себе эмоции чувства,</br>\nСтрастей кипящих овладеет буйство.</br>\nТакой до всевозможного дойдёт.</br>', NULL, NULL, NULL),
(727, 5, 1, 'ИЩИ СВОЮ ОШИБКУ', NULL, 'Со счастьем каждый должен жить.</br>\nСчастливым каждый должен быть.</br>\nА раз несчастен шибко,</br>\nИщи свою ошибку.</br>', NULL, NULL, NULL),
(728, 5, 1, 'ДУМАЙ', NULL, 'Полагаешься на низших людей – деградируешь.</br>\nПолагаешься на равных себе – не меняешься.</br>\nПолагаешься на высших в борьбе – лидируешь,</br>\nТак думай, на кого полагаешься.</br>', NULL, NULL, NULL),
(729, 5, 1, 'НЕ ВИДИМ', 'По изречению И.Тургенева', 'Раз хороши все условья,</br>\nНам счастье являет честь.</br>\nКак мы не ценим здоровье,</br>\nТак счастье не видим, что есть.</br>', NULL, NULL, NULL),
(730, 5, 1, 'ДРУЖЕЛЮБНОСТЬ', NULL, 'Не надо в скуке заводить себе друзей,</br>\nНо дружелюбность воспитай почтенную:</br>\nВсем существам вокруг благополучье сей,</br>\nВ друзья ты обретёшь тем всю Вселенную.</br>', NULL, NULL, NULL),
(731, 5, 1, 'ГРЕХ', NULL, 'Раз захочешь, то сможешь!</br>\nЗабыть тяжкий грех,</br>\nСовершённый тобой во плоти.</br>\nТе последствия всё же</br>\nЗабрали твой смех,</br>\nОт того никуда не уйти.</br>\n</br>\nНе носись ты с грехами,</br>\nЗачем их внушать,</br>\nИстязаясь в душевной борьбе.</br>\nВсе помчится кругами,</br>\nНе смей лишь плошать!</br>\nПорождая тот грех вновь в себе.</br>', NULL, NULL, NULL),
(732, 5, 1, 'НЕНАВИСТЬ И СТРАХ', NULL, 'Держат нас в мрачной обители,</br>\nВ цепких и жгучих руках,</br>\nВсех мыслей дурных родители:</br>\nНенависть злая и страх!</br>', NULL, NULL, NULL),
(733, 5, 1, 'КАРМА', NULL, 'Не ждите чего-то, плывя по течению.</br>\nПусть карма – железный закон.</br>\nА лучше бы было отдаться влечению,</br>\nЧто Вами возводится Он!</br>', NULL, NULL, NULL),
(734, 5, 1, 'ПРИТВОРСТВО', NULL, 'Знай, суетливость – вовсе не проворство,</br>\nШумливость, – то не силы выраженье:</br>\nТо – не энергия, то – глупое притворство.</br>\nСпокойствие решит проблему достиженья.</br>', NULL, NULL, NULL),
(735, 5, 1, 'ДВА НАЧАЛА', NULL, 'Уравновешен мир. В нас тоже два начала:</br>\nМы можем быть и добрыми, и злыми.</br>\nНе испугайтесь, как коснётесь тьмы причала,</br>\nВедь, нам решать: Что выбрать, стать какими!</br>', NULL, NULL, NULL),
(736, 5, 1, 'НЕ ГЛЯДИТЕ НАЗАД', NULL, 'Заторможена жизнь, вы в ней маетесь,</br>\nРаз глядите назад с сожалением</br>\nИ на месте одной водворяетесь,</br>\nС облекающим душу старением.</br>\n</br>\nПосмотрите вперёд – тайна юности</br>\nПриглашает раскраситься творчеством</br>\nВосходящим к прекрасной безумности,</br>\nНазываемым вашим Высочеством!</br>', NULL, NULL, NULL),
(737, 5, 1, 'ОБМАН', NULL, 'Что толку, что других обманешь</br>\nИ пусть тебя не уличишь,</br>\nЗнай, ложью ты себя дурманишь,</br>\nГде правда? Сам не различишь.</br>\n', NULL, NULL, NULL),
(738, 5, 1, 'НЕДОСТАТКИ', NULL, 'Всегда найдутся недостатки,</br>\nИх с корнем рвёшь, а поле шире.</br>\nА раз считаешь, всё в порядке:</br>\nТебе не место в этом мире!</br>', NULL, NULL, NULL),
(739, 5, 1, 'ФИЛОСОФСКИЙ КАМЕНЬ', NULL, 'Найдите в себе философский камень:</br>\nЧто в золото выльет дешевый металл,</br>\nЧто грусть превратит в благородный пламень,</br>\nЧто страх переплавит в стальной пьедестал.</br>', NULL, NULL, NULL),
(740, 5, 1, 'ПАНИКА', NULL, 'Не забудьте, что паника греет!</br>\nТянет в узел тугой поясок.</br>\nРаз впадёте в нее, так быстрее</br>\nЗасосёт, чем зыбучий песок.</br>', NULL, NULL, NULL),
(741, 5, 1, 'НАДЕЖДА', NULL, 'Говорят: Умирает надежда последней!</br>\nНет, не правда! Она не умрёт никогда!</br>\nНужно знать, что ничто не уходит бесследно.</br>\nРаз в то веришь, с тобой не случится беда.</br>', NULL, NULL, NULL),
(742, 5, 1, 'ОБЕЗЬЯНА', NULL, 'Казаться хочешь важным и солидным?</br>\nТак корчишь рожицы, как в цирке обезьяна.</br>\nНо твой обман становится всем видным:</br>\nВ гримасе глупого типичного изъяна.</br>', NULL, NULL, NULL),
(743, 5, 1, 'ЛИЦО', NULL, 'Выраженье лица светлой мыслью разгладь,</br>\nМрачный образ внутри создавать не спеши,</br>\nПомни, наше лицо, как зеркальная гладь,</br>\nОтражает характер и чувства души.</br>', NULL, NULL, NULL),
(744, 5, 1, 'СИЛА СЛАБЫХ', NULL, 'Мелочность ума –</br>\nГлупая кума.</br>\nРазума смутьянство –</br>\nГлупое упрямство.</br>\nВсе упрямцы глупы:</br>\nНа глазищах лупы.</br>\nВсе глупцы упрямы:</br>\nЛезут прямо в яму.</br>\nТо – души ухабы,</br>\nТо есть – «сила» слабых.</br>', NULL, NULL, NULL),
(745, 5, 1, 'ФРАЗА', NULL, '«Я это не могу себе позволить» –</br>\nТакие строки часто говорим.</br>\nРаз так желаем, сердце не неволит,</br>\nОно смиряется с отказом сим.</br>\nА если скажем по-другому фразу:</br>\n«Как я могу себе позволить это?»</br>\nПридут идеи, даже может сразу,</br>\nВедь, сердце не оставит без ответа.</br>', NULL, NULL, NULL),
(746, 5, 1, 'НУЖНО БЫ ЗАБЫТЬ', NULL, 'Мир стал потусторонним</br>\nИ сердце хочет выть,</br>\nЗачем, мы – люди, помним?</br>\nЧто нужно бы забыть.</br>\n', NULL, NULL, NULL),
(747, 5, 1, 'ШЕПЧЕТ СТРАХ О МНОГОМ', NULL, 'В образе убогом</br>\nГорьким монологом</br>\nШепчет страх о многом:</br>\nО пугливой птахе,</br>\nТемпах черепахи,</br>\nНеизбежном крахе.</br>\nОн растит в нас нравственных калек.</br>\nТо тому итогом:</br>\nПод любым предлогом</br>\nЗа своим порогом</br>\nС ним в общенье строгом</br>\nСмелый и разумный человек</br>', NULL, NULL, NULL),
(748, 5, 1, 'КОЛЮЧКА ВЫНИМАЕТСЯ КОЛЮЧКОЙ', NULL, 'Раз боль и негатив пришли к тебе,</br>\nВыходит где-то в чём-то стал ты злючкой,</br>\nПойми, всегда, везде, в любой борьбе,</br>\nКолючка вынимается колючкой.</br>', NULL, NULL, NULL),
(749, 5, 1, 'НЕГАТИВ', NULL, 'Пессимистично не прими тот корректив,</br>\nНесёт он в жизнь баланс, а не удары.</br>\nК тебе приходит в гости негатив,</br>\nОдностороннего развития снять чары.</br>', NULL, NULL, NULL),
(750, 5, 1, 'УТРО ВЕЧЕРА МУДРЕНЕЕ', NULL, 'Если то не пожар, а нервозная ярость,</br>\nНе спеши предпринять, успокойся, хоть малость.</br>\nУтвердиться в решении нужно позднее,</br>\nУтро вечера, помни, всегда мудренее.</br>', NULL, NULL, NULL),
(751, 5, 1, 'НЕ МЕШАЙТЕ ПРИРОДЕ', NULL, 'Ведём себя порой, как сумасброды,</br>\nПытаясь улучшать гармонию природы.</br>\nА нужно той природе не мешать,</br>\nА нужно благо от неё вкушать,</br>\nХраня её, душою возвышать.</br>', NULL, NULL, NULL),
(752, 5, 1, 'ЛЮДСКОЙ НЕДОСТАТОК', NULL, 'Говорят, будто ревность второе уродство.</br>\nПриглядись и увидишь заметное сходство.</br>\nРевность рушит любовь, дружбу, счастье, покой.</br>\nТак гони из себя недостаток людской.</br>', NULL, NULL, NULL),
(753, 5, 1, 'К СОВЕРШЕНСТВУ ЧЕРЕЗ КУЛЬТУРУ', NULL, 'Раз нет культуры, нет и совершенства.</br>\nПора ошибки наши истреблять,</br>\nЗабыть критиковать и оскорблять</br>\nИ этим достигать внутри блаженства.</br>', NULL, NULL, NULL),
(754, 5, 1, 'ЗАКОН СОХРАНЕНИЯ ЭНЕРГИИ', NULL, 'Если грубости слышишь с лихвою,</br>\nЕсли часто ты видишь плохое,</br>\nОбъяснение стало б такое.</br>\n</br>\nСам добился такого удела.</br>\nВ отношениях шёл до предела,</br>\nГоворил что-то плохо иль делал.</br>\n</br>\nЗнать пора уж, обет себе дать,</br>\nНегатив свой внутри обуздать.</br>\nПопытаться собой совладать.</br>', NULL, NULL, NULL),
(755, 5, 1, 'НЕ БУДЕШЬ СЧАСТЬЕМ БРОШЕН', NULL, 'Знай, счастье не явление лихое.</br>\nНе нужно, чтобы был ты им взъерошен.</br>\nНе будешь счастьем никогда ты брошен</br>\nС уменьем забывать плохое</br>\nИ помнить о хорошем.</br>', NULL, NULL, NULL),
(756, 5, 1, 'ДУШЕВНЫЙ РАЗГОВОР', 'Карма – йога', 'Бьёт карма, опуская ноги, руки.</br>\nЖизнь заторможена, тоска вокруг…</br>\nВселяясь цепко в твой телесный круг,</br>\nДепрессия рождает эти муки.</br>\n</br>\nИ не пытайся вырваться наружу,</br>\nВо внутрь себя старайся посмотреть,</br>\nУвидь, что избегаешь лицезреть,</br>\nЛишь в том спасенье сможешь обнаружить.</br>\n</br>\nИ не кричи проблеме той: Исчезни!</br>\nЕй вовсе не нужна с тобою ссора.</br>\nДушевного добившись разговора,</br>\nОна прогонит все твои болезни.</br>', NULL, NULL, NULL),
(757, 5, 1, 'СЧАСТЬЕ В ТЕБЕ САМОМ', NULL, 'Будешь жить ты на небе седьмом,</br>\nСебе счастье в душе застолбя.</br>\nЕсли счастье в тебе самом,</br>\nТо и счастье вокруг тебя.</br>', NULL, NULL, NULL),
(758, 5, 1, 'ПОРОК', NULL, 'Порок – не в том, что явятся желания,</br>\nПорок – не в том, что в голове терзания.</br>\nНе в том, что слышишь тёмные признания.</br>\nПорок – есть не умение пресечь его влияние.</br>', NULL, NULL, NULL),
(759, 5, 1, 'ОШИБКИ', NULL, 'Когда ошибки мало,</br>\nВторая – следом ждёт.</br>\nНе стойте, где попало,</br>\nЕщё раз попадёт.</br>', NULL, NULL, NULL),
(760, 5, 1, 'С ТОБОЙ', NULL, 'В уме</br>\nВоцарилась трезвость.</br>\nИ пусть</br>\nЗаблудился в пустыне,</br>\nС тобой</br>\nБудут сила и резвость.</br>\nВ сердце</br>\nВлилось довольство.</br>\nПусть</br>\nОтруби ешь отныне,</br>\nС тобой</br>\nЖизни вкус, хлебосольства.</br>\nВ душе</br>\nЖивёт безмятежность.</br>\nИ пусть</br>\nТы дрожишь в холстине –</br>\nС тобой</br>\nЗемли, неба нежность.</br>', NULL, NULL, NULL),
(761, 5, 1, 'ПЕРЕЖДИ', NULL, 'Бывает полумрак,</br>\nБывает затуманится,</br>\nНо не увидишь как,</br>\nВсё вновь прозрачным станется.</br>\n</br>\nТы бурю пережди,</br>\nНе нужно мучаться.</br>\nНу сам же посуди,</br>\nВедь всё получиться.</br>', NULL, NULL, NULL),
(762, 5, 1, 'ССОРА', NULL, 'Хочешь воду помутить в пруду,</br>\nЧто ж, у ссоры будь на поводу.</br>\nВ миг поднимутся былые нечистоты.</br>\nЗлость и ненависть затеют чехарду.</br>', NULL, NULL, NULL),
(763, 5, 1, 'ЧУДОДЕЙ', NULL, 'Добрый человек и чудодей</br>\nСохранит культуры репутацию:</br>\nРазберёт любую ситуацию,</br>\nНе поранив тем сердца людей.</br>', NULL, NULL, NULL),
(764, 5, 1, 'НЕ ПРИНАДЛЕЖАТЬ СЕБЕ', NULL, 'Всем угождать – обычное батрачество.</br>\nИзлишество – всегда плохое качество,</br>\nОсобенно, в общении с людьми,</br>\nТо самого себя, топтать в пыли.</br>\nКогда принадлежишь всем без остатка,</br>\nВ тебе себя появится нехватка.</br>', NULL, NULL, NULL),
(765, 5, 1, 'С ГЛУПОСТЬЮ', NULL, 'С глупостью в готовности</br>\nУмей держать ушко.</br>\nОдин шаг смущённости –</br>\nИ тонет жизни судно.</br>\nВверх – к духовности</br>\nНам долго плыть и трудно,</br>\nВниз – к никчёмности</br>\nМгновенно и легко.</br>', NULL, NULL, NULL),
(766, 5, 1, 'ЧРЕЗМЕРНАЯ НАДЕЖДА', NULL, 'Оставь обычай свой не в меру восхваляться.</br>\nО чем твердишь, за этим можно не угнаться.</br>\nНе совпадет с мечтой реальная одежда.</br>\nНе оправдается чрезмерная надежда.</br>\nКогда на зависть всем сказал об очень многом,</br>\nТо от того порвётся нить златая с Богом.</br>', NULL, NULL, NULL),
(767, 5, 1, 'ПОБОЙСЯ ГОРДОСТИ', NULL, 'Для жизни риск –</br>\nС отравленным ростком.</br>\nПобольше твёрдости!</br>\nВначале писк,</br>\nПотом уже баском.</br>\nПобойся гордости!</br>', NULL, NULL, NULL),
(768, 5, 1, 'АДСКИЙ ОГОНЬ', NULL, 'Ударил камень, искра появилась:</br>\nВыходит, и в камнях огонь живёт.</br>\nУдарил человека, зло вздыбилось –</br>\nПусть добрый он, но гнев его зажжёт.</br>\n--------------------------------------------------</br>\nПокой в душе своей я не нарушу,</br>\nЧтоб пламень ярости не вырвался наружу.</br>', NULL, NULL, NULL),
(769, 5, 1, 'ГРЕБЯ К ПРОСВЕТЛЕНИЮ', NULL, 'Ты, к Просветлению гребя,</br>\nНе сможешь отдохнуть.</br>\nПуть изменения себя –</br>\nСложнейший в жизни путь.</br>\n', NULL, NULL, NULL),
(770, 5, 1, 'ЭТО НАШ МИР', NULL, 'Каким бы странным не казалось поведение,</br>\nКакой бы необычной не предстала речь,</br>\nНа все спокойное имей в себе суждение.</br>\nПрими, пойми и не пытайся пренебречь.</br>', NULL, NULL, NULL),
(771, 5, 1, 'БЛАГОДАТЬ', NULL, 'Раз не голодный ты и не холодный,</br>\nИ в будущем такое же видать –</br>\nИ если ты ещё свободный,</br>\nТо счастье тебе дарит благодать.</br>\n', NULL, NULL, NULL),
(772, 5, 1, 'О ФРАНЦУЗСКОМ ФИЛОСОФЕМОРАЛИСТЕ', NULL, 'Представ пред всеми эталоном честности,</br>\nШаррон взошел на пьедестал известности,</br>\nОставив в памяти секрет того успеха.</br>\n-----------------------------------------------------</br>\nЧтоб Пьера слушали, он не бежал к доспехам</br>\nИ не крикливо раздирал свой рот,</br>\nА уши раскрывал наоборот.</br>', NULL, NULL, NULL),
(773, 5, 1, 'ПОДКОВА И ХОМУТ', 'По изречению В. Сумбатова', 'Подкову повесим и ждём,</br>\nЧто лучики счастья падут.</br>\nА счастье польётся дождём,</br>\nКогда сбросим тяжкий хомут.</br>', NULL, NULL, NULL),
(774, 5, 1, 'ПРИЧИНА', NULL, 'И в крике есть участие:</br>\nОбрушит лаву эхо.</br>\nТак сильному, несчастие –</br>\nПричина для успеха.</br>', NULL, NULL, NULL),
(775, 5, 1, 'ПРЕДСТАВЬ СЕБЕ', NULL, 'Ну зачем ссоры омут,</br>\nНеприятность в судьбе.</br>\nЧем плести зло другому,</br>\nЧернь представь ту себе.</br>\n</br>\nОн, как ты, то же слышит,</br>\nТу же чувствует боль,</br>\nТак же ходит и дышит.</br>\nТы ж его не неволь.</br>', NULL, NULL, NULL),
(776, 5, 1, 'ТАК МАЛО И ТАК МНОГО', NULL, 'С миром этим не всегда всё дружно,</br>\nМир мы любим в бедах обвинять.</br>\nНо так мало нам для счастья нужно</br>\nИ так много, чтобы то понять.</br>\n', NULL, NULL, NULL),
(777, 5, 1, 'ДОЛЮ СЛАДОСТИ', NULL, 'Сохраняйте душу в младости</br>\nПри любых страданиях людских.</br>\nНаходите долю сладости</br>\nВ мыслях опечаленных своих.</br>', NULL, NULL, NULL),
(778, 5, 1, 'БЕДА В НАШЕЙ УТРОБЕ', NULL, 'Не по чьей-то чужой вине</br>\nПоддаётся душа хворобе.</br>\nЗнай, беда не приходит извне.</br>\nОна прячется в нашей утробе.</br>', NULL, NULL, NULL),
(779, 5, 1, 'ПОЙМИ, ТО ГАДОСТИ', NULL, 'Пойми, то гадости,</br>\nЗря спину ты на них не горбь.</br>\nЛишайся радости,</br>\nС которой исчезает скорбь.</br>', NULL, NULL, NULL),
(780, 5, 1, 'ПОДСКАЗКА ИЛИ ЗЛОСЧАСТЬЕ', NULL, 'У человека нет несчастий кроме тех,</br>\nКоторые он сам зовёт несчастьем.</br>\nМы можем выбрать слёзы или смех,</br>\nРешить, подсказка это иль злосчастье.</br>', NULL, NULL, NULL),
(781, 5, 1, 'ТВЁРДЫЕ ДУШИ', NULL, 'Души твёрдые горной породы</br>\nИзбегают развратных гиней,</br>\nКак отважные мореходы</br>\nИзбегают подводных камней.</br>', NULL, NULL, NULL),
(782, 5, 1, 'МЫ – ХУДОЖНИКИ', NULL, 'Художник краски выбрал для холста.</br>\nКартина выйдет светлой или мрачной.</br>\nЕсть выбор мыслей и эмоций, не спроста:</br>\nРисуем жизнь мы яркой иль невзрачной.</br>', NULL, NULL, NULL),
(783, 5, 1, 'ИНТЕЛЛЕКТ', 'По изречению О. Хаксли', 'Когда объект</br>\nНаходит секса интересней дело,</br>\nВ нём – интеллект!</br>\nВ его уме – ясно, светло и зрело.</br>', NULL, NULL, NULL),
(784, 5, 1, 'МЫСЛИ О ДРУГИХ', NULL, 'Что подумаем мы о других,</br>\nВолновать должно лишь нас самих.</br>\nНу а тем зачем о том тужить:</br>\nНаши мысли, с ними нам и жить.</br>', NULL, NULL, NULL),
(785, 5, 1, 'ЛИШИ ВООБРАЖЕНЬЯ', NULL, 'Когда печален с кем-нибудь момент,</br>\nКогда не чувствуешь душой сближенья,</br>\nКогда является внутри уничиженье:</br>\nСвиданья с ним лиши воображенья,</br>\nТем самым чувства ты сведёшь на нет.</br>', NULL, NULL, NULL),
(786, 5, 1, 'БЕЗДЕЙСТВИЕ', NULL, 'Мечтаем жизнь свою к успеху изменить.</br>\nНа это все надежды возлагаем.</br>\nТак почему ж бездействия не рвём мы нить</br>\nИ сами измениться, не желаем.</br>', NULL, NULL, NULL),
(787, 5, 1, 'МАНТРА ПОРЯДКА', NULL, 'Каждый день разбираю заторы</br>\nВ своем доме и в мыслях своих.</br>\nГде нет солнца, открою я шторы,</br>\nОсветив им свой быт и свой миг.</br>', NULL, NULL, NULL),
(788, 5, 1, 'ЗВОНАРИ РАССУДКА', NULL, 'Гнева беспощадны упыри.</br>\nНервов непреклонно злых вторженье.</br>\nПозабыв душевные сраженья,</br>\nПрогоняй коварство до зари.</br>\n</br>\nПусть с утра рассудка звонари</br>\nНачинают верное служенье.</br>\nПобеждая робкие движенья,</br>\nПробуждай осознанность внутри.</br>', NULL, NULL, NULL),
(789, 5, 1, 'ВЛАДЕЙ СТРАСТЬЮ', NULL, 'Нещадно жизнь круша,</br>\nСадится страсть на шею.</br>\nТогда страсть хороша,</br>\nКогда владеешь ею.</br>', NULL, NULL, NULL),
(790, 5, 1, 'МНЕНИЕ ДРУГИХ', NULL, 'О нём говорят, ну и пусть говорят.</br>\nПо мненью других, не суди!</br>\nПослушай, что он о других говорит.</br>\nНа это вот и гляди!</br>', NULL, NULL, NULL),
(791, 5, 1, 'ВРАЧЕВАТЬ', NULL, 'Ни к чему пылкой страсти</br>\nБольно нас бичевать.</br>\nНужно силе той власти</br>\nНежно нас врачевать.</br>', NULL, NULL, NULL),
(792, 5, 1, 'ТВОЕ ЗЕРКАЛО', NULL, 'Твоё зеркало – другие люди.</br>\nУчись на этом каждый раз.</br>\nТак было и так вечно будет:</br>\nОни к тебе, как ты к себе сейчас!</br>\n', NULL, NULL, NULL),
(793, 5, 1, 'СТАНЕТ СТРАСТЬ ПОРОКОМ', NULL, 'Стать должно твоим уроком:</br>\nЕсли страсть с немалым сроком,</br>\nПревратится та в привычку,</br>\nСтанет страсть – пороком.</br>', NULL, NULL, NULL),
(794, 5, 1, 'ВЛАСТЕЛИН ИЗБРАНИЙ', NULL, 'Свобода и конец страданий</br>\nНаступят в тот момент в тебе,</br>\nКогда решишь, что властелин избраний</br>\nТы сам всех, что в твоей судьбе.</br>', NULL, NULL, NULL),
(795, 5, 1, 'ПЛОХО И ЗДОРОВО', 'По изречению Драгана М. Ерёмича', 'Плохо, раз страсть покрывает ум пылью,</br>\nПлохо, раз чувства твои – тормоза.</br>\nЗдорово! Чувства дают уму крылья.</br>\nЗдорово! Ум даёт чувствам глаза.</br>', NULL, NULL, NULL),
(796, 5, 1, 'ОТЧЕГО БЕДНЕЕМОТЧЕГО БЕДНЕЕМ', NULL, 'Как редко думаем о том, что мы имеем.</br>\nНо помним вот всегда: чего не достаёт!</br>\nНе замечая первого, мы сами же беднеем,</br>\nЗато второе нам покоя не даёт!</br>', NULL, NULL, NULL),
(797, 5, 1, 'ДУРАК СПОСОБЕН', NULL, 'Дурак способен только надурить,</br>\nПусть представляется, как нужно коему.</br>\nДурак прилично может говорить,</br>\nНо поступает всё же он по-своему.</br>', NULL, NULL, NULL),
(798, 5, 1, 'ДУМАЙ ОБ УДАЧЕ', NULL, 'Не думай, что чего-то мало,</br>\nА думай, что ты всех богаче!</br>\nНе думай о своих провалах,</br>\nА думай! О своей удаче!</br>', NULL, NULL, NULL),
(799, 5, 1, 'ДЕЙСТВИЯ', NULL, 'Не принимай так близко к сердцу то,</br>\nЧто действия твои всех привлекут внимание,</br>\nИль будь фарфоровой фигурочкой в пальто.</br>\nЗастынь на полке в позе ожидания.</br>', NULL, NULL, NULL),
(800, 5, 1, 'СОВЕСТЬ – ЭТО ПАЛКА', 'По изречению О. де Бальзака', 'Вновь в руках скалка,</br>\nВновь злости оковы,</br>\nВновь совестью начал судить.</br>\nСовесть – как палка,</br>\nЕй бьёшь ты любого,</br>\nСебя ей не хочется бить.</br>', NULL, NULL, NULL),
(801, 5, 1, 'СЕКРЕТ УЛЫБКИ', NULL, 'Ты к грубости взметнись</br>\nИ грубость в миг в ответ,</br>\nА просто, улыбнись!</br>\n(И в этом весь секрет)</br>', NULL, NULL, NULL),
(802, 5, 1, 'ТИХАЯ ВЛАСТЬ', 'По изречению Вильяма Чэннинга', 'Как ни громко кричат твои страсти,</br>\nСтоит совести голос подать,</br>\nЗаробеют те, станут спадать,</br>\nИспугавшись её тихой власти.</br>', NULL, NULL, NULL),
(803, 5, 1, 'ГЛАВНОЕ ДОСТОИНСТВО', NULL, 'Наше достоинство главное,</br>\nЧто мы к размышленью способные,</br>\nТак пусть будут мысли славные,</br>\nПолезные и беззлобные.</br>', NULL, NULL, NULL),
(804, 5, 1, 'ПРОСТИ', NULL, 'Злиться, судить и мстить</br>\nМожет любой дурак.</br>\nМудрый сумеет простить</br>\nИ это действительно так!</br>', NULL, NULL, NULL),
(805, 5, 1, 'БРОСЬ СЕГОДНЯ', NULL, 'Поддержка будет в том Господня:</br>\nДурны привычки брось сегодня,</br>\nВедь завтра станут те дурнее,</br>\nИх завтра побороть труднее.</br>', NULL, NULL, NULL),
(806, 5, 1, 'ЧТОБЫ ВСЕ ПОЗНАТЬ', NULL, 'Пробуй всех понять,</br>\nНе спеши судить.</br>\nЧтобы всё познать,</br>\nНужно всё простить.</br>', NULL, NULL, NULL),
(807, 5, 1, 'ЧТОБ СПРАВИТЬСЯ С ПРИВЫЧКОЙ', NULL, 'То выдели в кавычках,</br>\nТо назови отмычкой:</br>\n«Тебе нужна привычка,</br>\nЧтоб справиться с привычкой»</br>', NULL, NULL, NULL),
(808, 5, 1, 'ГОРДОСТЬ ЧРЕЗМЕРНАЯ', 'По изречению И. Тургенева', 'Гордость прескверную</br>\nЗдравостью сердца туши.</br>\nГордость чрезмерная –</br>\nВывеска жалкой души.</br>', NULL, NULL, NULL),
(809, 5, 1, 'ЧТОБ, НЕ ИДТИ ДВАЖДЫ', NULL, 'Доброе дело! Доброе слово! Сейчас!</br>\nПройди этот путь однажды!</br>\nПомни, что Бог это ждёт от нас,</br>\nЧтоб не идти путём тем же дважды!</br>', NULL, NULL, NULL),
(810, 5, 1, 'ДОБРЫЕ СЛОВА', NULL, 'Не скупись на добрые слова.</br>\nИм признательна любая голова.</br>\nТы забудешь, с кем приветлив был,</br>\nА тому ты будешь долго мил.</br>', NULL, NULL, NULL),
(811, 5, 1, 'КАК ТОЛЬКО МИР', NULL, 'Как только мир представится скучливым,</br>\nПечаль зеленую не нужно умножать,</br>\nС тоскою мрачной лучше быть кичливым,</br>\nОт грусти серой надобно бежать.</br>\n------------------------------------------------</br>\nТебя никто не должен обижать,</br>\nТебя никто не должен ублажать,</br>\nТебя никто не сделает счастливым,</br>\nПока ты сам себя не станешь уважать.</br>\n', NULL, NULL, NULL),
(812, 5, 1, 'ВОСТОЧНАЯ МУДРОСТЬ', NULL, 'Восточная мудрость считает,</br>\nЧто делать все надо легко.</br>\nКто мягко по жизни ступает,</br>\nТот может уйти далеко.</br>', NULL, NULL, NULL),
(813, 5, 1, 'КОГДА ТАК ЕСТЬ', NULL, 'Попал впросак – обмяк,</br>\nВпитался мрак – иссяк,</br>\nВ аду клоак – двояк;</br>\nНе человек – червяк.</br>\n--------------------------------</br>\nКогда так есть, во прах</br>\nРаздавят хлесть и страх.</br>', NULL, NULL, NULL),
(814, 5, 1, 'ДРУЖЕЛЮБНОСТЬ', NULL, 'Распри с кем-то, это – безрассудность.</br>\nПусть нам люди отвечают – да!</br>\nСпор рождает пагубность и нудность.</br>\nДружелюбность лучше, Господа!</br>', NULL, NULL, NULL),
(815, 5, 1, 'ДЛЯ КОГО ТЫ ДОБР', 'Библия Сир 14.5', 'Внутри тебя озлобленный чертог,</br>\nРаз самого себя не любишь, значит плох.</br>\nВнутри тебя есть ядовитость кобр,</br>\nРаз зол ты на себя, то для кого ты добр?</br>', NULL, NULL, NULL),
(816, 5, 1, 'МУДРЫЕ ЛЮДИ', NULL, 'Почему стекается вся вода</br>\nВ большие-большие моря?</br>\nИ море находится ниже всегда,</br>\nНо, значит, выходит не зря!</br>\n</br>\nИ самые-самые мудрые люди</br>\nНе ставят себя выше всех.</br>\nДаже немного отстать не забудут</br>\nИ в этом их выси успех!</br>', NULL, NULL, NULL),
(817, 5, 1, 'ВСЕ СЧАСТЛИВЫЕ ВОКРУГ', NULL, 'Образуем счастья крепкий круг,</br>\nЧеловек пусть человеку станет друг,</br>\nЕсли каждый одному подарит счастье,</br>\nБудут в мире все счастливые вокруг.</br>', NULL, NULL, NULL),
(818, 5, 1, 'БУДЬ ЕСТЕСТВЕННЫМ', NULL, 'Мы часто любим подражать кому-то:</br>\nОни – особые! А, мы – пародия на них.</br>\nНо, в естестве, мы в каждую минуту,</br>\nВдруг, можем стать особеннее их!</br>', NULL, NULL, NULL),
(819, 5, 1, 'СЛЁЗЫ И СМЕХ', NULL, 'Грусть и радость из нас понимает любой.</br>\nТолько слёзы и смех – это разный заряд,</br>\nКогда плачешь, никто ведь такому не рад,</br>\nРассмеёшься, весь мир рассмеётся с тобой.</br>', NULL, NULL, NULL),
(820, 5, 1, 'ПРИЯТНОЕ С ПОЛЕЗНЫМ', NULL, 'Не может ситуаций быть плохих.</br>\nСуть такова: Забыть о смысле сих.</br>\nЕсть ситуация приятная, когда,</br>\nСобытий добрых длится череда.</br>\n</br>\nЕсть ситуация полезная и с ней,</br>\nТы можешь стать мудрее и сильней.</br>\nДругих же ситуаций просто нет!</br>\nПриятное с полезным – весь ответ!</br>', NULL, NULL, NULL),
(821, 5, 1, 'НЕТ ПРАВА', NULL, 'Все мы узами Божьими кровны</br>\nИ под знаком единых планет.</br>\nВсе мы где-то и в чём-то виновны,</br>\nИ наказывать права в нас нет.</br>', NULL, NULL, NULL),
(822, 5, 1, 'СОМНЕНИЕ', NULL, 'Спокойно относись всегда к сомнению.</br>\nОно, как фильтр – твоей грядущей цели.</br>\nОно приводит просто к размышлению:</br>\nИдти вперёд, иль посидеть на мели.</br>\n</br>\nСомнение наполнено заботой.</br>\nНа том спасибо, хватит многословность:</br>\nДополни веру сильной, звонкой нотой.</br>\nОтбрось сомнение, как некую условность.</br>', NULL, NULL, NULL),
(823, 5, 1, 'НЕРВОЗ', NULL, 'Болезнь заразная – нервоз</br>\nРождает пагубный прогноз.</br>\nВ ней страстность бурь и буйство гроз,</br>\nИстерик гром и град угроз.</br>', NULL, NULL, NULL),
(824, 5, 1, 'ГРУСТНО И СТЫДНО', NULL, 'Мы в ссоре порой языкасты,</br>\nСердиты, грубы и горласты,</br>\nКоварны, гневны и зубасты.</br>\nНо после, как стихнут контрасты,</br>\nКак сбросятся злости балласты,</br>\nЗа речь, что была так ехидна,</br>\nЗа тон, что звучал так обидно,</br>\nНе стало б нам грустно и стыдно.</br>', NULL, NULL, NULL),
(825, 5, 1, 'ИЩИТЕ ДОБРО', NULL, 'Один мудрец сказал о том:</br>\n«Зачем искать нам зло в святом?</br>\nУслышьте душу, не нутро!</br>\nИщите в грешнике добро!»</br>', NULL, NULL, NULL),
(826, 5, 1, 'БУДЬ ПРОЩЕ', NULL, 'Детям малым по жизни нужно молоко.</br>\nВзрослым людям – тверже пища.</br>\nБлизорукий не видит, что там, далеко.</br>\nС ограниченным – стены и днища.</br>\n</br>\nНе терзай неуместно сердечко свое,</br>\nРаскрывая в не нужном месте.</br>\nПросит жизнь поиграть порой в игры её,</br>\nПошутить, посмеявшись вместе.</br>', NULL, NULL, NULL),
(827, 5, 1, 'ЛУЧШЕ ЛАЮТ, ЧЕМ КУСАЮТСЯ', NULL, 'Пусть мщения мысли желают,</br>\nНо незачем в это бросаться.</br>\nПусть лучше собаки вслед лают,</br>\nЧем ты разрешишь им кусаться.</br>', NULL, NULL, NULL),
(828, 5, 1, 'БРАННЫЕ СЛОВА', NULL, 'Отбросим! То обведшая одежда!</br>\nВсе бранные и колкие слова.</br>\nВсем неприятны оскорбления невежды.</br>\nНесут опасность злости жернова.</br>\n</br>\nЗабудем осужденья, поквитанья</br>\nПриветливо сумеем говорить.</br>\nНет – даже правде горькой оправданья.</br>\nПолезней поддержать и ободрить.</br>', NULL, NULL, NULL),
(829, 5, 1, 'НЕ УЧАЩАЙ', 'По изречению из Библии Пр. 25. 17', 'Вход в дом не учащай друзей своих.</br>\nПойдёт та дружба ложною тропою,</br>\nВедь ты возненавидеть можешь их</br>\nИ могут те наскучиться тобою.</br>', NULL, NULL, NULL),
(830, 5, 1, 'ОТВЕТСТВЕННОСТЬ', NULL, 'Каждый знает душою, есть горе и зло.</br>\nКаждый чувствует сердцем любовь и добро.</br>\nКаждый видит глазами где мрак, где светло.</br>\nВыбирает он сам, что б его привлекло:</br>\nНегодяйства услугу позвать ли в нутро,</br>\nДобродетели качество, то ли б взросло:</br>\nСам решает под чьё же податься крыло.</br>\n', NULL, NULL, NULL),
(831, 5, 1, 'ОПАСНО', 'По изречению А. Круглова', 'Выясненье отношений так опасно.</br>\nСвои нервы вы мотаете напрасно.</br>\nНи к чему упреки, крики и пиленье,</br>\nНе леченье дружбы то, а воспаленье.</br>', NULL, NULL, NULL),
(832, 5, 1, 'НАПРАСНО', NULL, 'Мы напрасно бываем капризны,</br>\nМожет лучше забыть укоризны,</br>\nНеудачники зрят катаклизмы,</br>\nУ счастливцев мгновенья сюрпризны,</br>\nЛишь несчастные слабые слизни,</br>\nВерят в то, что нет счастья по жизни.</br>', NULL, NULL, NULL),
(833, 5, 1, 'НЕИЗБЕЖНОЕ МОЖНО ПОБЕДИТЬ', 'По изречению Сенеки', 'Может нужно всё ж предупредить,</br>\nЧто у каждого в судьбе своя стезя,</br>\nИзбежать нам неизбежного нельзя,</br>\nЕго можно только победить.</br>', NULL, NULL, NULL),
(834, 5, 1, 'ОТСТАТЬ ОТ ССОРЫ', 'По изречению из Библии Пр. 20. 3', 'К гневу свой гнев ни к чему нам взметать.</br>\nЧесть человеку – от ссоры отстать.</br>\nВсякий глупец задорен.</br>\nПлох человек тот, что спорен.</br>\n', NULL, NULL, NULL),
(835, 5, 1, 'КТО УДАЧЛИВ, ТОТ ВЕРИТ В СЕБЯ', 'По изречению А. Даниэль-Брюне', 'Зря ты жизнь посвящаешь плачу,</br>\nЗря ты жизнь проживаешь скорбя.</br>\nНеудачник, знай, верит в удачу,</br>\nКто, удачлив, тот верит в себя!</br>', NULL, NULL, NULL),
(836, 5, 1, 'ТАК ДОЛГО', NULL, 'Когда ссора утомительно длинна,</br>\nПонимание должно стать нашим долгом.</br>\nНе тянулась эта ссора бы так долго,</br>\nЕсли б стороны одной была вина.</br>', NULL, NULL, NULL),
(837, 5, 1, 'НЕ БЫВАЕТ ОПАСНЕЙ БОЛЕЗНИ', 'По изречению Михаила М. Сперанского', 'Если мысли твои дали сбой,</br>\nБудут жизни шаги бесполезны.</br>\nНе бывает опасней болезни:</br>\n«Недовольство своею судьбой.»</br>', NULL, NULL, NULL),
(838, 5, 1, 'КАМЕНЬ, ЧТО ЗА ПАЗУХОЙ', NULL, 'Злобы вредоносность – факт извечен.</br>\nЗлоба размножается, ханжит.</br>\nНе заметишь, как проникнет в печень</br>\nКамень, что за пазухой лежит.</br>', NULL, NULL, NULL),
(839, 5, 1, 'СНИМИТЕ ТЁМНЫЕ ОЧКИ', 'По изречению Чарльза Эллиотта', 'Бывает, что становимся всё злее,</br>\nИ ширятся от ужаса зрачки.</br>\nНо станет этот мир светлее,</br>\nКогда мы снимем тёмные очки.</br>', NULL, NULL, NULL),
(840, 5, 1, 'ДРОВ НЕ КИДАЙ В ОГОНЬ', 'По изречению из Библии Сир. 8.4', 'Когда это нужно, себя ты пришпорь,</br>\nТого, кто есть рядом, не тронь.</br>\nРаз дерзок язык человека, не спорь.</br>\nИ дров не кидай в тот огонь.</br>', NULL, NULL, NULL),
(841, 5, 1, 'ПЛОХОЕ НАСТРОЕНИЕ', NULL, 'Бытует в свете мнение;</br>\nПлохое настроение</br>\nТвоей коварной может стать привычкой,</br>\nК дурным делам и глупостям отмычкой.</br>', NULL, NULL, NULL),
(842, 5, 1, 'КОРЕНЬ ТОГО СЛОВА – РАЗДЕЛЕНЬЕ', NULL, 'Ни к чему распущенный обманный секс,</br>\nКорень того слова – разделенье,</br>\nВредный фактор в форме вожделенья.</br>\nПримитивный и животный наш рефлекс.</br>\n</br>\nВзлёт, падение, мучение и стресс,</br>\nЭти чувства временны и чужды.</br>\nМы телесные справляем нужды.</br>\nВерность Истине – вот что имеет вес.</br>', NULL, NULL, NULL),
(843, 5, 1, 'А ДУМАЙ ЛУЧШЕ.', 'По изречению Эмиля Кроткого', 'Все люди иль винят, иль хвалят,</br>\nПривычка в каждом к этому живёт.</br>\nНо всё ж, не думай, что все пчёлы жалят,</br>\nА думай лучше, что дают те мёд.</br>', NULL, NULL, NULL),
(844, 5, 1, 'ВСПОМНИ', NULL, 'Стены четыре, пол и потолок,</br>\nМрак заточенья в каменных оковах.</br>\nЗа дверью мрачной на стальных засовах</br>\nЖизнь преподносит непростой урок.</br>\n----------------------------------------------</br>\nКогда так жестко внешне ограничен,</br>\nОт тяготы облегчи скорбный срок:</br>\nТы вспомни, как душой своей глубок,</br>\nЧто добродетелью внутри быть можешь возвеличен.</br>', NULL, NULL, NULL),
(845, 5, 1, 'КОГДА ТЫ ВЕСЕЛ', NULL, 'Когда ты весел, радуется море,</br>\nКогда ты весел, дивно на просторе,</br>\nКогда ты весел, розовые зори,</br>\nКогда ты весел, солнце в твоём взоре.</br>', NULL, NULL, NULL),
(846, 5, 1, 'ОСТАВЬ ЕГО В ПОКОЕ', 'По народному изречению', 'Вниманье раз скупое</br>\nИ тщетно ласку будишь.</br>\nОставь того в покое –</br>\nНасильно мил не будешь.</br>', NULL, NULL, NULL),
(847, 5, 1, 'РАЗ ХОЧЕШЬ ПРЕБЫВАТЬ В БЛАГОПОЛУЧИИ', NULL, 'То солнце, то закроет тучами;</br>\nРаз хочешь пребывать в благополучии,</br>\nУмей быть сильным в счастье и в беде,</br>\nУмей прожить в богатстве и в нужде.</br>', NULL, NULL, NULL),
(848, 5, 1, 'ВУЛЬГАРНЫЙ ПРИЗНАК', NULL, 'Во всём быть правым не стремись,</br>\nЗнай, гордость – мрачный призрак.</br>\nТы фактом этим не срамись,</br>\nВульгарный это признак.</br>', NULL, NULL, NULL),
(849, 5, 1, 'О ЗЛОСЛОВИИ', NULL, 'Обильное сквернословие</br>\nСужает аспекты мышления.</br>\nЮмор с оттенком злословия</br>\nПриносит душе огрубление.</br>\nЯзвительные наставления</br>\nСодержат мотив оскорбления.</br>', NULL, NULL, NULL),
(850, 5, 1, 'О СЕРДЦЕ', 'По изречению из Талмуда', 'Окаменеют сердца дверцы,</br>\nРаз правоте своей верны.</br>\nА размягчаться дверцы сердца –</br>\nСознанием своей вины.</br>', NULL, NULL, NULL),
(851, 5, 1, 'ЛОЖНАЯ ЛЮБОВЬ', NULL, 'Раз любовь твоя у ближнего сворована,</br>\nВ цепи ревности колючие закована,</br>\nНа телесном вожделении основана,</br>\nИли выгодой корыстной продиктована,</br>\nЭто ложная любовь и будет сломана,</br>\nТак как Богом это чувство не даровано.</br>', NULL, NULL, NULL),
(852, 5, 1, 'ПРИВЫКНЕТ СЕРДЦЕ', NULL, 'Слова, подсобные ярму,</br>\nСлова, что вроде хомута,</br>\nСлова, подобие хлыста</br>\nНе нужно говорить спроста.</br>\n------------------------------------</br>\nПривыкнет сердце ведь к тому,</br>\nЧто часто говорят уста.</br>\n', NULL, NULL, NULL),
(853, 5, 1, 'ПЯТЬ НЕОБХОДИМЫХ УСЛОВИЙ', NULL, '1. Раз силы нет, так дальше продолжаться,</br>\nНе стоит в это глубже погружаться.</br>\n</br>\n2. Возможно, скажешь ты, что некуда уйти?</br>\nНо то – внушение, что держит нас в клети.</br>\n</br>\n3. Пусть с этим дней прошло не мало и годин,</br>\nЗнай, выход есть всегда – и не один.</br>\n</br>\n4. Твой гнёт страданий будет обезличен,</br>\nКогда к нему ты станешь безразличен:</br>\nНе думай лишь о том, не говори!</br>\nИ то исчезнет и умрёт внутри.</br>\n</br>\n5. Не делай резких необдуманных движений.</br>\nУйди спокойно от ненужных напряжений.</br>', NULL, NULL, NULL),
(854, 5, 1, 'МЫСЛИ И ДЕЙСТВИЯ', NULL, 'Тех мыслей возводи строенье,</br>\nЧто поднимают настроенье.</br>\nТех действий делай вознесенье,</br>\nВ которых видишь путь спасенья.</br>', NULL, NULL, NULL),
(855, 5, 1, 'ВАЖНОСТЬ', NULL, 'Наша внешняя и внутренняя важность</br>\nРовным счётом в мире ничего не значит.</br>\nКаждый сам себя искусственно дурачит,</br>\nА последствия, конечно – неудачи.</br>\nНи к чему тот перегиб и эпатажность,</br>\nОтноситесь к жизни проще и спокойней.</br>\nБез избыточных эмоций всем удобней.</br>\nЖизнь без важности полезней и достойней.</br>', NULL, NULL, NULL),
(856, 5, 1, 'ИЗ-ПОД ВЛАСТИ НЕГАТИВА', NULL, 'Эта жизнь – качанье коромысла,</br>\nНо мы в силах избежать тревожных пут.</br>\nНепрерывная игра и бодрость мысли,</br>\nИз-под власти негатива уведут.</br>', NULL, NULL, NULL),
(857, 5, 1, 'СЛОВА НАПОЛНЕНЫ', 'По изречению из Библии Прем. 1.11, Сир. 5.15', 'От ропота пустого сохранись.</br>\nОт злого языка поберегись.</br>\nЧто тайно сказано, то не проходит даром,</br>\nУста клевещущие смертным бьют ударом.</br>\nСлова наполнены и ядом, и нектаром.</br>\n----------------------------------------------------</br>\nВ речах есть славы верх, бесчестья низ,</br>\nПаденья глубь и достиженья высь.</br>', NULL, NULL, NULL),
(858, 5, 1, 'КОВАРНАЯ РЕВНОСТЬ', NULL, 'Любовь, раз ей веришь, вернее.</br>\nКоварную ревность дави,</br>\nВедь чем твоя ревность сильнее,</br>\nТем глубже могила любви.</br>', NULL, NULL, NULL),
(859, 5, 1, 'НЕУДАЧЕ РАДУЙТЕСЬ НАЗЛО', NULL, 'Находите пользу в неудаче,</br>\nРаз хотите разрешить задачу.</br>\nНеудаче радуйтесь назло,</br>\nРаз хотите, чтобы вам везло.</br>', NULL, NULL, NULL),
(860, 5, 1, 'ТАИНСТВО ДУШИ', NULL, 'И где б ты во Вселенной не был,</br>\nКаким глубоким не казалось б небо,</br>\nСказать: «То тайна» – не спеши!</br>\nПревыше таинство души.</br>', NULL, NULL, NULL),
(861, 5, 1, 'БОЛЬШЕ РАЗУМА И ДУХА', NULL, 'Богатство с радостью дарить жизнь не устанет,</br>\nКогда в нас Разума и Духа больше станет.</br>', NULL, NULL, NULL),
(862, 5, 1, 'ДУША ЧЕЛОВЕКА', NULL, 'Я – миров и времён госпожа!</br>\nЯ живу чистотой дорожа.</br>\nЯ решаю, меняя день, ночь,</br>\nКак мне лучше делам их помочь.</br>\nДаже время утратит значенье,</br>\nМне не сразу придет облегченье.</br>\nЯ вернусь, жить от века да века,</br>\nТак как есть я – душа человека!</br>', NULL, NULL, NULL),
(863, 5, 1, 'ДУША И РАЗУМ', NULL, 'Блуждают в темноте, в заснеженной глуши</br>\nДуша без Разума и Разум без Души.</br>\nВсё в жизни к лучшему сумеет измениться,</br>\nКогда те встретятся и смогут породниться.</br>', NULL, NULL, NULL),
(864, 5, 1, 'ЗНАЙ ПУТЬ ДОМОЙ', NULL, 'Ты есть – не чувства и эмоции твои.</br>\nЗнай путь домой!</br>\nЗри истинные дали!</br>\nПрими!</br>\nСвою укорененность в бытии.</br>\nЦени!</br>\nСвою божественность в реале.</br>', NULL, NULL, NULL),
(865, 5, 1, 'ТАМОЖНЯ', NULL, 'Смерть – таможня,</br>\nДеньги нам не пронести через неё.</br>\nДушу – можно,</br>\nТак тогда обогатите вы её.</br>', NULL, NULL, NULL),
(866, 5, 1, 'ПРИЛИВ ДУХОВНОЙ ЭМОЦИИ', NULL, 'Прилив духовной эмоции</br>\nМеняет в сознанье пропорции;</br>\nВстречаясь с прекрасным, бесценным,</br>\nНет смысла уж в прошлом разменном.</br>\nТы молишь у чистой любви:</br>\nО, Господи, благослови!</br>\nСвоё повеленье яви!</br>\nДай шанс и на путь вдохнови!</br>', NULL, NULL, NULL),
(867, 5, 1, 'ПРИСТАНЬ ОСМЫСЛЕНИЯ', NULL, 'Доверьтесь Духу, что у нас внутри,</br>\nОн ниспошлет вам проблеск просветленья.</br>\nОн есть – душа души!</br>\nЗвезда зари!</br>\nВ нем тайна истин!</br>\nПристань осмысленья!</br>', NULL, NULL, NULL),
(868, 5, 1, 'ЦАРСТВО БОЖИЕ', 'По изречению из Библии Лк. 16.16, 17.21', 'Душу сбереги, не обесчесть,</br>\nЭто всё потом тебе зачтётся.</br>\nЦарство Божие внутри нас есть</br>\nИ оно усилием берётся.</br>', NULL, NULL, NULL),
(869, 5, 1, 'ЗЕРКАЛЬНОЕ СВОЙСТВО', NULL, 'Кто обретает высшее сознание,</br>\nЗеркальное с ним получает свойство:</br>\nСвоей души реально видеть здание</br>\nИ трезво оценить его устройство.</br>', NULL, NULL, NULL),
(870, 5, 1, 'ЧИСТЫЙ БРИЛЛИАНТ', NULL, 'Если вынуть вдруг душу из тела</br>\nИ очистить от скверны насевшей,</br>\nКак бриллиант чистый та б заблестела,</br>\nОслепив красотою воззревшей.</br>', NULL, NULL, NULL),
(871, 5, 1, 'ЖИЗНЬ – ИСПЫТАНИЕ ДУШИ', NULL, 'Пришли мы в мир, того желая сами,</br>\nНо бьёмся, не поняв, зачем мы здесь.</br>\nЗабыли, жаль, прощаясь с небесами,</br>\nЧто испытание души та жизнь и есть.</br>', NULL, NULL, NULL),
(872, 5, 1, 'ЖИЗНЬ – НЕ ИГРА', NULL, 'Мы взрослые – не детвора,</br>\nТак кто сказал, что жизнь – игра?</br>\nДавным-давно понять пора,</br>\nЧто жизнь таинственна, мудра.</br>\n-----------------------------------------------------</br>\nЖизнь – не игра, не мишура и не курьёз…</br>\nОна – всерьез!</br>', NULL, NULL, NULL),
(873, 5, 1, 'ВЕЧНА', NULL, 'Не думай напрасно, что жизнь быстротечна,</br>\nЗнай, жизнь, что прекрасна, та в памяти вечна.</br>', NULL, NULL, NULL),
(874, 5, 1, 'ГЛАЗАМИ ДУХА', NULL, 'Почувствовать Её должны мы сами,</br>\nЧрез сердце песнь Её летит до слуха.</br>\nНе разглядеть Её телесными глазами,</br>\nМы Истину воззрим глазами Духа.</br>', NULL, NULL, NULL),
(875, 5, 1, 'ТЫ – ДУХ', NULL, 'Ты – дух! То пойми!</br>\nИ войди в свое царство!</br>\nПрижми настроений капризных мытарство.</br>\nТы стань господином ума своего</br>\nИ мысль бесконтрольную выбрось его!</br>', NULL, NULL, NULL),
(876, 5, 1, 'КРАСОТА', NULL, 'Духовна сущность красоты:</br>\nВ душе ее находишь ты.</br>', NULL, NULL, NULL),
(877, 5, 1, 'ЭКОЛОГИЯ', NULL, 'Мы знаем нынче страны и народы</br>\nВолнует экология природы.</br>\nА голос мудрый мне сказал в тиши:</br>\n«Начните с экологии души.»</br>', NULL, NULL, NULL),
(878, 5, 1, 'ОДИН ЗАКОН', NULL, 'Один закон души и тела:</br>\nПитать свой дух есть часть удела.</br>', NULL, NULL, NULL),
(879, 5, 1, 'И ПАДАЯ ДУХОМ МОЖНО УШИБИТЬСЯ', 'По изречению Михаила Генина', 'Сложна жизнь по слухам –</br>\nВ ней можно не раз ошибиться,</br>\nИ падая духом,</br>\nПридётся тебе ушибиться.</br>', NULL, NULL, NULL),
(880, 5, 1, 'ИСТИННОЕ ЗНАНИЕ', NULL, 'Духовное самосознание –</br>\nВот истинное знание!</br>', NULL, NULL, NULL),
(881, 5, 1, 'ВЫСШИЙ ПЛАН', NULL, 'У маленького «я»</br>\nНачертан высший план:</br>\nМы капелькой живя,</br>\nВольёмся в океан.</br>\n</br>\nВсеобщим станем «Я»,</br>\nЕдиным и большим,</br>\nЧтоб в те попасть края</br>\nМы жизнь свою вершим.</br>', NULL, NULL, NULL),
(882, 5, 1, 'ЕДИНАЯ СУЩНОСТЬ', NULL, 'Нет, по сути, вокруг других!</br>\nВезде ты встречаешь себя самого!</br>\nСкрыта под формой и именем в них</br>\nЕдиная сущность твоя и его!</br>\n', NULL, NULL, NULL),
(883, 5, 1, 'КОМПЛЕКС НЕДОСТАТОЧНОСТИ', NULL, 'Комплекс недостаточности часто</br>\nВ нашем сердце крепко заседает.</br>\nОщущение, что нам отчасти</br>\nВ этой жизни что-то не хватает.</br>\n</br>\nЖизнь богата, радостна, жемчужна,</br>\nЧто желаем, будем получать,</br>\nНо на деле ничего не нужно,</br>\nЧтоб духовное развитие начать.</br>', NULL, NULL, NULL),
(884, 5, 1, 'ОТНОШЕНИЯ', NULL, 'Двух человек, двух людей отношения</br>\nМогут являть собой Ад,</br>\nНо если поймёшь, что одно вы вершение!</br>\nПолучишь духовный заряд.</br>', NULL, NULL, NULL);
INSERT INTO `content` (`id`, `id_cat1`, `id_cat2`, `title`, `comment`, `text`, `keywords`, `description`, `lang`) VALUES
(885, 5, 1, 'НАМЕРЕНЬЕ ДУШИ', NULL, 'Содержат энергетики пластины</br>\nВ нас светлое намеренье души,</br>\nПлюс визуализация картины –</br>\nСвершить своё желанье поспеши.</br>', NULL, NULL, NULL),
(886, 5, 1, 'МОСТ', NULL, 'Построй между этим красивый мост:</br>\nРеальная жизнь и духовный рост.</br>', NULL, NULL, NULL),
(887, 5, 1, 'Я – МАЛАЯ ФОРМА', NULL, 'Незнанье себя порождает напасти,</br>\nВсех бед на планете: страданья и страсти.</br>\nТы в рамках ума создаешь суррогат,</br>\nЧто лучше всех «я», «я» – так значим, богат.</br>\n</br>\nЦепляешь к нему цепь всех страхов и нужд</br>\nИ сам для себя, вдруг, становишься чужд.</br>\n«Я» – малая форма, а губит незнанье,</br>\nЧто жизнь вся едина! Едино сознанье!</br>', NULL, NULL, NULL),
(888, 5, 1, 'КТО ТЫ?', NULL, 'Многое в жизни твоей так важно,</br>\nНо, значимо, лишь, абсолютно одно!</br>\nВажно, конечно, что есть в твоём теле,</br>\nНо, кто ты? Важнее на самом деле.</br>\nПоняв скоротечность и временность «Я»,</br>\nПостигнет гармонию сущность твоя!</br>', NULL, NULL, NULL),
(889, 5, 1, 'НЕ ПУТАЙ', NULL, 'Я есмь! – Едино с настоящим!</br>\nОно наполнено незыблемым пространством.</br>\nНе путай его с «Я» сулящим.</br>\nНе путай, что проходит, с постоянством!</br>', NULL, NULL, NULL),
(890, 5, 1, 'РАБСТВО ДУХА', NULL, 'Пусть нравственный завет</br>\nДойдет до слуха.</br>\nПозорней рабства нет,</br>\nЧем рабство духа.</br>', NULL, NULL, NULL),
(891, 5, 1, 'ПРИЗРАЧНОСТЬ', NULL, 'Разтождествить сознание и форму</br>\nВнезапность смерти может неожиданно</br>\nИ унести в исчезновенья даль.</br>\nПокой и тишина являют норму.</br>\nТы видишь: жизнь и тело иллюзорно выдано</br>\nИ эту призрачность и эту нереаль.</br>', NULL, NULL, NULL),
(892, 5, 1, 'СМЕРТЬ – ГОСЭКЗАМЕН', NULL, 'Наш госэкзамен – это смерть,</br>\nЧтоб вновь не пасть нам в круговерть,</br>\nКопите мудрость в каждом дне,</br>\nНе упадёт она в цене.</br>\n-----------------------------------------</br>\nПускай оставим память тут,</br>\nНо мудрости цветы взрастут.</br>\nМы, ставши счастливы в летах,</br>\nУмрём с улыбкой на устах.</br>', NULL, NULL, NULL),
(893, 5, 1, 'БЕССМЕРТИЕ', NULL, 'Как много людей старых, очень больных</br>\nПред смертию свет излучают из глаз.</br>\nСмирились! Хотя не настал смертный миг,</br>\nУзрев тишины и бессмертия час.</br>', NULL, NULL, NULL),
(894, 5, 1, 'ЦЕНИТЕ ЖИЗНЬ', NULL, 'Если бы в мире один был цвет</br>\nИ не было б цвета другого,</br>\nРаз с чем сравнить, его даже нет,</br>\nТо нет цвета как такового!</br>\nНам смерть дана, чтобы знать,</br>\nЧто есть жизнь! Ценить ее быстротечность.</br>\nУметь стать мудрее, в чем суть осознать.</br>\nУзреть в преходящести вечность!</br>', NULL, NULL, NULL),
(895, 5, 1, 'ВЕЧНОСТЬ', NULL, 'Смерти нет! Жизнь бессмертна!</br>\nЕсть метаморфозы форм существований.</br>\nИ нет антитезы и жизнь не инертна,</br>\nА вечность – основа всех оснований.</br>', NULL, NULL, NULL),
(896, 5, 1, 'ЗАМЕТИМ ВДРУГ', NULL, 'Жаль, смерть пока не обманул никто.</br>\nЖеланье к этому всегда нас грело.</br>\nПусть нашей жизни канет вглубь плато,</br>\nНе бойтесь смерти и живите смело.</br>\n--------------------------------------------------</br>\nС годами счастье копится зато.</br>\nСчастливей всех мы станем лет под сто.</br>\nСлучится раз, услышав, зашумело,</br>\nИ обернувшись просто так на то,</br>\nЗаметим вдруг, упало наше тело.</br>', NULL, NULL, NULL),
(897, 5, 1, 'ВЕЧНОСТЬ ДУХА', NULL, 'Умирать – естественно, как и рождаться.</br>\nСмертью, как и жизнью, можно наслаждаться.</br>\nЧувство страха смерти вы уймите.</br>\nВечность жизни духа есть, поймите!</br>', NULL, NULL, NULL),
(898, 5, 1, 'ТРУДНЕЕ РОЖДАТЬСЯ', NULL, 'Смерть не так уж страшна…</br>\nПереход в мир астральный;</br>\nТы прозрачный, невидимый, телом бледнее,</br>\nЖизнь, все также видна,</br>\nТо есть миг тривиальный,</br>\nВот рождаться по-новому это труднее.</br>\n', NULL, NULL, NULL),
(899, 5, 1, 'ДВА СПОСОБА', NULL, 'Нам от Ведов дошла любопытная весть,</br>\nУмереть людям только два способа есть:</br>\nСпособ первый: принять смерть от тяжкого горя,</br>\nДоставляя несчастье, и с миром повздорив,</br>\nИ второй способ, смерть от безмерного счастья.</br>\nПринося всем успех и благое участье.</br>', NULL, NULL, NULL),
(900, 5, 1, 'НЕОБЫЧНЫЙ КОМПЛЕКС', NULL, 'Наш комплекс, порою, совсем необычен,</br>\nК примеру, старенья момент горемычен.</br>\nНо нужно понять, что лишь старится тело,</br>\nДуша молода навсегда и всецело.</br>\nЗря смерти боимся, как злостной чумы,</br>\nУмрёт наше тело, но только не мы.</br>', NULL, NULL, NULL),
(901, 5, 1, 'О СМЕРТИ', NULL, 'Боимся смерти тьмы.</br>\nЖивет к ней страх в народе.</br>\nНе умираем мы,</br>\nА прячемся в природе.</br>', NULL, NULL, NULL),
(902, 5, 1, 'НЕ БОЙТЕСЬ!', NULL, 'Не бойтесь, ни жизни, ни смерти явления:</br>\nВ том и в другом есть жизнь в проявлении.</br>', NULL, NULL, NULL),
(903, 5, 1, 'РАССУЖДЕНИЯ ФИЛОСОФА', NULL, 'Не бойтесь смерти, это всё пустяк!</br>\nЕй не достать при жизни нас никак.</br>\nА если вдруг наступит смертный час,</br>\nКого ей доставать? Не будет нас.</br>', NULL, NULL, NULL),
(904, 5, 1, 'НУЖНО ПРИРОДЕ ЛЮДСКОЙ ПОКОРИТЬСЯ', NULL, 'Знаем,</br>\nЧто смерть после жизни приходит,</br>\nНужно</br>\nПрироде людской покориться.</br>\nПлохо не то,</br>\nЧто всё в жизни проходит,</br>\nПлохо, лишь то!</br>\nС тем не можем мириться.</br>', NULL, NULL, NULL),
(905, 5, 1, 'НЕ НАЙТИ СЛОВ', NULL, 'Жизнь прекрасна, светла и вечна.</br>\nВпереди свет невообразимого.</br>\nИ слов не найти, конечно,</br>\nДля выражения невыразимого!</br>', NULL, NULL, NULL),
(906, 5, 1, 'КЛЮЧИК К СЧАСТЬЮ – ДОБРЫЕ ДЕЛА', NULL, '– В этом мире всё закрыто на замок.</br>\nКак зайти нам в светлый теремок?</br>\n– Поверните в том замочке ключ</br>\nИ забрезжит счастья тёплый луч.</br>\n</br>\n– Где же взять ключи от теремка?</br>\nКак добраться к свету тайника?</br>\n– Ключ к замочку – добрые дела,</br>\nБлагодарность и людская похвала.</br>', NULL, NULL, NULL),
(907, 5, 1, 'МИР ПРИМЕРНЫЙ И БЕЗУМНЫЙ', 'По изречению Периандра', 'Бывает мир примерен,</br>\nБывает мир безумен.</br>\nВ счастье будь умерен,</br>\nВ несчастье будь разумен.</br>', NULL, NULL, NULL),
(908, 5, 1, 'ЖИЗНЬ ПРЕКРАСНА', NULL, 'Жизнь прекрасна и будет еще прекрасней!</br>\nВ сердце удача!</br>\nВ душе позитив и успех!</br>\nВ мыслях легко и я становлюсь богаче!</br>\nВсе во мне радостно!</br>\nМир мой счастливей всех!</br>', NULL, NULL, NULL),
(909, 5, 1, 'МАНТРА СЧАСТЬЯ', NULL, 'Я счастлив!</br>\nСейчас я очень счастлив!</br>\nИ счастьем наполняется душа!</br>\nЯ счастлив!</br>\nЯ бесконечно счастлив!</br>\nИ жизнь моя светла и хороша!</br>', NULL, NULL, NULL),
(910, 5, 1, 'НОВЫЕ ГЛАЗА', NULL, 'Смотрю на жизнь влюблёнными глазами</br>\nИ это начинается с утра.</br>\nОт счастья улыбаюсь со слезами,</br>\nМир полон чуда, света и добра.</br>', NULL, NULL, NULL),
(911, 5, 1, 'ЭНЕРГИЯ ТВОРЕНЬЯ', NULL, 'Есть в нас Великая Энергия Творенья.</br>\nДоверьтесь жизни, единитесь с сущим.</br>\nВливайте в душу умиротворенье.</br>\nГлядите взором понимание несущим.</br>', NULL, NULL, NULL),
(912, 5, 1, 'СМЫСЛ И ЦЕЛЬ', 'По изречению Г. Матюшова', 'Пускай Вселенной небольшая часть я,</br>\nНо смысл и цель есть также у неё:</br>\nСмысл жизни этой – счастье, счастье, счастье!</br>\nЦель жизни – дольше продлевать её.</br>', NULL, NULL, NULL),
(913, 5, 1, 'ВСЁ УЖЕ ЕСТЬ', 'По изречению из Библии. Ек. 3. 14', 'Пребывает вовек то, что делает Бог.</br>\nНе прибавит никто, не убавит.</br>\nИ что было, что есть и что будет, он смог</br>\nСотворить это всё и тем правит.</br>', NULL, NULL, NULL),
(914, 5, 1, 'ДУХОВНОСТЬ', NULL, 'Господь Верховный – это безусловность.</br>\nДух в Бытии – откроет нам Духовность.</br>', NULL, NULL, NULL),
(915, 5, 1, 'СМЫСЛ – БОЖЬЯ РУКА', 'Веды', 'Жизни смысл – это Божья рука.</br>\nТы знакомство с руки начинаешь,</br>\nЕй берёшь, ей даёшь, пеленаешь,</br>\nТо – эмоций и знаний река.</br>', NULL, NULL, NULL),
(916, 5, 1, 'ЗНАЧИТ ЕСТЬ', NULL, 'Разговоров всяких много,</br>\nМыслей разных хороводь.</br>\nЕсли есть потребность Бога,</br>\nЗначит, есть и сам Господь.</br>', NULL, NULL, NULL),
(917, 5, 1, 'ИСТОЧНИК', 'Библия Пр. 16.22', 'Разум – есть особая от Господа опека:</br>\nРазум – есть источник жизни человека.</br>', NULL, NULL, NULL),
(918, 5, 1, 'МЫ – ГОСТИ', NULL, 'Не забывай,</br>\nЧто все мы в этом мире гости.</br>\nНе допускай,</br>\nВ себя грех осужденья, злости.</br>\nУ нас нет права, друг,</br>\nНи мыслью, ни словами</br>\nСудить всё то вокруг,</br>\nЧто создано не нами.</br>', NULL, NULL, NULL),
(919, 5, 1, 'БОЖЕСТВЕННАЯ ПЕСНЬ', NULL, 'Во всём, что есть – прекрасная созвучность,</br>\nУслышать Бога стоит только захотеть.</br>\nПусть не дано понять Его нам Сущность,</br>\nБожественную песнь Его мы можем петь.</br>', NULL, NULL, NULL),
(920, 5, 1, 'ЕДИНСТВО ВО МНОЖЕСТВЕ', NULL, 'Мир – единый дух в своём вельможестве,</br>\nТо единство утверждается во множестве.</br>', NULL, NULL, NULL),
(921, 5, 1, 'ПЛОТСКИЕ ЛЮДИ', 'По изречению П. Гольбаха', 'Плотские люди глупы:</br>\nНе ценят духовные блага,</br>\nНе видят Божьей стопы,</br>\nНе делают к Богу ни шага.</br>', NULL, NULL, NULL),
(922, 5, 1, 'СВЕТИЛЬНИК ГОСПОДЕНЬ', 'По изречению из Библии Пр. 20.27', 'Светильник Господень от Бога опека</br>\nИ этим мы светом едины.</br>\nСветильник Господень, то – дух человека,</br>\nЧто сердца пытает глубины.</br>', NULL, NULL, NULL),
(923, 5, 1, 'ГОСПОДЬ ДУМАЕТ О НАС, НО НЕ ЗА НАС', 'По изречению Жана Кокто', 'Господь всех любит, принимает без прикрас,</br>\nНо всё ж он думает о нас, но не за нас.</br>', NULL, NULL, NULL),
(924, 5, 1, 'РАЙ ИЛИ АД', NULL, 'Есть Понятие Рая и Ада.</br>\nНас сейчас окружает Ад:</br>\nЭто значит сердец прохлада,</br>\nЭто зависть, жестокость, разлад.</br>\n</br>\nВ Рай попасть нам тоже по силам:</br>\nЭто значит сердец тепло,</br>\nБлагодарность, терпимость по жилам,</br>\nВзгляд, с которым всем станет светло.</br>', NULL, NULL, NULL),
(925, 5, 1, 'ОДИНАКОВО', NULL, 'Всего начало Им дано указно,</br>\nИ все богатства нам даются от Него.</br>\nПускай про Бога говорят и разно,</br>\nНо одинаково все чувствуют Его.</br>', NULL, NULL, NULL),
(926, 5, 1, 'СУДЬБА – СУЖДЕНИЕ БОГА О НАС', 'По изречению Андрея Лаврухина', 'Прошептал мне однажды мой внутренний глас:</br>\nПерст судьбы – есть суждение Бога о нас.</br>', NULL, NULL, NULL),
(927, 5, 1, 'ДУША – ТО КРЫЛЬЯ', NULL, 'Духовную меняешь жизнь зря на телесную.</br>\nДуша – то крылья, что поднимут в высь небесную,</br>\nА на ногах одних сорвёшься в глубь отвесную.</br>', NULL, NULL, NULL),
(928, 5, 1, 'ОН СПРАВЕДЛИВ', 'По изречению из Библии Прем. 1.2,3', 'Он сердца простотою призывается.</br>\nНе искушающим его Он открывается,</br>\nКто верует, является тому.</br>\nВ ком ум неправый, с теми разделяется,</br>\nС безумством испытанием справляется,</br>\nОн справедлив во всём и ко всему.</br>', NULL, NULL, NULL),
(929, 5, 1, 'НЕЛЬЗЯ ПРИКУРИТЬ', NULL, 'Невозможно таланта шедевр повторить –</br>\nОт божественной искры нельзя прикурить.</br>', NULL, NULL, NULL),
(930, 5, 1, 'ОПЕКА', NULL, 'Не лишишься божеской опеки,</br>\nК океану Блага попадёшь.</br>\nПусть! Хоть сотню раз ты упадёшь,</br>\nНо не сможешь осуждённым быть навеки.</br>', NULL, NULL, NULL),
(931, 5, 1, 'БОЖЕСТВЕННЫЙ ТОК', NULL, 'Перестали слова восклицать –</br>\nНачинайте тот мир созерцать.</br>\nПерекрылся всех мыслей поток –</br>\nТак внимайте Божественный ток.</br>', NULL, NULL, NULL),
(932, 5, 1, 'ЕСТЬ', NULL, 'Если встал у порога, пригласят и присесть.</br>\nКто в искании Бога, значит, Бог в том уж есть.</br>', NULL, NULL, NULL),
(933, 5, 1, 'ОТРИЦАНИЕ ДУХА', NULL, 'Жизнь – разруха,</br>\nВпустую растрачены годы –</br>\nС отрицанием Духа,</br>\nС отрицанием света Свободы.</br>', NULL, NULL, NULL),
(934, 5, 1, 'В ЧЕЛОВЕЧЬЕМ ОБЛИЧЬЕ', NULL, 'Мы – свидетели Бога величья,</br>\nМы – творений Его есть отличье.</br>\nОн нам дал свою власть, свою силу,</br>\nЧтоб творить в человечьем обличье.</br>', NULL, NULL, NULL),
(935, 5, 1, 'ЛЕННО ДУМАТЬ', 'Из онтологии мудрости', 'Нам ленно думать, этим Бога огорчаем</br>\nИ чтоб не думать: смотрим, слушаем, читаем.</br>', NULL, NULL, NULL),
(936, 5, 1, 'БЛАГОДАРИ', NULL, 'Желаешь позитивное мышление,</br>\nБлагодари во взгляде и в моление.</br>\n</br>\nЖелаешь позитивное влияние,</br>\nБлагодари и сменишь состояние.</br>\n</br>\nЖелаешь получить от жизни много,</br>\nБлагодари людей, судьбу и Бога!</br>', NULL, NULL, NULL),
(937, 5, 1, 'СПАСИ, БОГ', NULL, 'Ведь каждый всей душой,</br>\nСказать б спасибо смог…</br>\nА смысл есть в том большой,</br>\nТо значит: Спаси Бог!</br>', NULL, NULL, NULL),
(938, 5, 1, 'ТРИ ВАЖНЫХ ВОПРОСА', NULL, 'Чтоб не вязнуть в суете хаоса,</br>\nЧтоб познать Божественную суть:</br>\nПомни, в жизни три важны вопроса,</br>\nЧто есть Истина? Кто Я? Мой путь?</br>', NULL, NULL, NULL),
(939, 5, 1, 'НЕ НАНОСЯ УДАР', NULL, 'Смиренным будь, подставь другую щеку…</br>\nРаз так сумеешь, придет Божий дар!</br>\nНе поддавайся гневностному шоку,</br>\nНайди в себе ответ, не нанося удар.</br>', NULL, NULL, NULL),
(940, 5, 1, 'РАЗУМ – ОСВЕЩЁННЫЙ УМ', NULL, 'Ум и разум имеют различные свойства.</br>\nУм – для жизни мирской и природной устройство.</br>\nРазум – солнечный ум, Богом Ра освещенный,</br>\nС сутью Божьей души в глубь его воплощённой.</br>', NULL, NULL, NULL),
(941, 5, 1, 'ТЫ И ОН', NULL, 'Зовут его: Бог… и Аллах… кто, как, где.</br>\nЗовут: Отче наш… чистый-чистый эфир.</br>\nНо должен ты знать, что всегда и везде!</br>\nТы входишь в его, а он в твой входит мир!</br>', NULL, NULL, NULL),
(942, 5, 1, 'НЕ ОСКУДЕЕТ РУКА ДАЮЩЕГО', NULL, '«Да не оскудеет рука дающего»</br>\nТак должны мы, отдавая, говорить.</br>\nДелай это ради дня грядущего,</br>\nЧтобы в добром завтра нам щедрее быть.</br>', NULL, NULL, NULL),
(943, 5, 1, 'ЦАРСТВО ГОСПОДНЕ', NULL, 'Почаще себе говори,</br>\nЧто Царство Господне внутри!</br>\nВ тебе: и свобода, и радость!</br>\nБессмертья прекрасная сладость!</br>', NULL, NULL, NULL),
(944, 5, 1, 'ЗАПОВЕДИ', NULL, 'Помни эти строки наизусть:</br>\nНе грусти,</br>\nТебя оставит грусть,</br>\nНе пугай</br>\nИ будешь не напуган,</br>\nНе ругай</br>\nИ будешь не обруган,</br>\nНе суди</br>\nИ будешь не судим,</br>\nНе вреди</br>\nИ будешь невредим.</br>', NULL, NULL, NULL),
(945, 5, 1, 'ПО ВЕРЕ ВАМ', NULL, 'Библейским поклонюсь словам:</br>\n«По вере вашей да будет вам!»</br>\nЛишь с верой суть моя сильна,</br>\nДа будет истинна она!</br>', NULL, NULL, NULL),
(946, 5, 1, 'ИСПОЛНИ', NULL, 'Веру, Силу, Бог послал!</br>\nТы об этом помни!</br>\nВсё, что сердцем пожелал</br>\nДля себя – исполни!</br>', NULL, NULL, NULL),
(947, 5, 1, 'ВЫШЕ ГОЛОВУ', NULL, 'Ты носишь в себе частичку Бога</br>\nИ смысл твоей жизни: К нему дорога!</br>\nЗемной – ты, но есть в тебе то, что свыше,</br>\nПоэтому голову – выше!</br>', NULL, NULL, NULL),
(948, 5, 1, 'СИЛЫ ВСЕЛЕННОЙ', NULL, 'Мы – живущие – спящие Боги!</br>\nВ нас есть силы Вселенной всей.</br>\nВ век духовности к храму дороги</br>\nС каждым шагом ясней и ясней.</br>', NULL, NULL, NULL),
(949, 5, 1, 'СИЛА ДОКАЗАТЕЛЬСТВ', NULL, 'Дурную отрицаю власть:</br>\nЛюдей, вещей и обстоятельств.</br>\nИм на себя не дам напасть!</br>\nЯ – есмь! Вот сила доказательств!</br>', NULL, NULL, NULL),
(950, 5, 1, 'ДОБРО', 'Завещание Ванги', 'Добро имеет огромную силу,</br>\nОно побеждает всё зло.</br>\nРаз выбрал добро священным Святилом,</br>\nТы прав и тебе повезло.</br>', NULL, NULL, NULL),
(951, 5, 1, 'ВОЗДАСТСЯ ПО ВЕРЕ', NULL, 'Просите! И вам ниспошлют с небес.</br>\nСтучите! Для вас распахнутся двери.</br>\nМысли граничьте с пределом чудес.</br>\nТвердым в желаньи, воздастся по вере!</br>', NULL, NULL, NULL),
(952, 5, 1, 'РАЙ ВСЕГДА', NULL, 'Попасть что б в рай, взлететь на небеса,</br>\nУсердие всей жизни нужно свесть.</br>\nНе тратьте время, создавайте чудеса</br>\nВ том месте, где сейчас вы есть.</br>', NULL, NULL, NULL),
(953, 5, 1, 'УЧЕНИЕ БУДДЫ', NULL, 'Учит Будда: «Роди в душе мудреца.</br>\nНа земле работай в поте лица.</br>\nНо, когда ты один, то должно быть легко,</br>\nВозносись над землёй высоко-высоко»</br>', NULL, NULL, NULL),
(954, 5, 1, 'АБСОЛЮТНАЯ ВЕРА', NULL, 'Абсолютной должна быть вера.</br>\nЭто та неизменная мера,</br>\nЧтобы взять всё, что хочешь от Бога.</br>\nЭто мало и это много!</br>', NULL, NULL, NULL),
(955, 5, 1, 'МЫСЛИ ХРИСТА', NULL, 'Христос по Божьи учит:</br>\nКто просит, тот получит,</br>\nКто постучит, войдёт,</br>\nКто ищет, тот найдёт.</br>', NULL, NULL, NULL),
(956, 5, 1, 'ФИЧИНО О БОГЕ', NULL, 'Неся философские гири,</br>\nДуша прозревает в дороге:</br>\nНе Бог простирается в мире,</br>\nА мир простирается в Боге.</br>', NULL, NULL, NULL),
(957, 5, 1, 'МОЩЬ', NULL, 'Божественная мощь у человека есть, по сути.</br>\nТот гений царствовать рожден, как некий Бог.</br>\nКакие бы история не одевала путы,</br>\nСмириться с рабством он не может и не мог.</br>', NULL, NULL, NULL),
(958, 5, 1, 'НЕВЫРАЗИМАЯ СИЛА', NULL, 'Не выказать нам Божество, не выразить,</br>\nНе описать большому каталогу.</br>\nНе вылепить, не вырубить, не вырезать,</br>\nНи храм, ни монумент подобный Богу.</br>', NULL, NULL, NULL),
(959, 5, 1, 'ПРЕДЛОГ ДЛЯ ЖИЗНИ', NULL, 'Зачем те загадки, что создали мы,</br>\nОт них пыль летит кутерьмы.</br>\nНет лучше для жизни предлога:</br>\nРазгадывать таинство Бога.</br>', NULL, NULL, NULL),
(960, 5, 1, 'ПЯТАЯ МАНТРА', 'Восточного ведического трактата Шри Ишопанишад', 'Господь Верховный ходит и не ходит,</br>\nОн далеко и в тоже время близко очень.</br>\nВо внутрь всего, что есть, он также входит.</br>\nИ всё же, прибывает вне всего он в прочем.</br>', NULL, NULL, NULL),
(961, 5, 1, 'ПУСТЬ БОГ УВИДИТ НАС', 'По восточному ведическому трактату', 'Не стремитесь по жизни лик Бога</br>\nПовстречать, иль увидеть хоть раз.</br>\nНе об этом должна быть тревога;</br>\nУстремитесь, чтоб Бог видел вас.</br>', NULL, NULL, NULL),
(962, 5, 1, 'МАНТРА 6', 'Восточного ведического трактата', 'Кто видит, с Господом всё связано вокруг,</br>\nКто понимает, существа – Его частицы,</br>\nВсё сущее есть плод Его заслуг,</br>\nЛюбовь того, все перейдёт границы.</br>', NULL, NULL, NULL),
(963, 5, 1, 'ПУТЬ К ИСТИНЕ', NULL, 'Путь к истине трудный,</br>\nНо благочестивый.</br>\nПуть к истине чудный,</br>\nОн светлый, счастливый.</br>\nНо, сложный, безмерный,</br>\nИ нужно дерзнуть,</br>\nПройти самый верный</br>\nИ праведный путь!</br>', NULL, NULL, NULL),
(964, 5, 1, 'ВСЁ ЗАВИСИТ ОТ НАС', NULL, 'Постарайтесь слышать Божий глас:</br>\n«Всё зависит только лишь от нас!»</br>\nА кто этого не понимает,</br>\nТот других в своих проблемах обвиняет,</br>\nВсех, кто под руку попался, обличает</br>\nИ свою вину не замечает.</br>', NULL, NULL, NULL),
(965, 5, 1, 'ДУША – ТО БОГ', 'По изречению Сенеки', 'Душа – то Бог,</br>\nПриют нашедший в теле человека.</br>\nА жизнь – предлог,</br>\nЧтобы над всем вокруг была опека.</br>', NULL, NULL, NULL),
(966, 5, 1, 'ПРЕЗИРАЮЩИЙ МУДРОСТЬ', 'Мысли из Библии Прем. 3.11.12', 'Кто мудрость презирает</br>\nИ наставленья хает –</br>\nНесчастный и голодный,</br>\nС женой своей холодный,</br>\nС ним дети злы утробны,</br>\nНикто ему не родный,</br>\nДела его не годны,</br>\nТруды пусты, бесплодны.</br>', NULL, NULL, NULL),
(967, 5, 1, 'ЕДИНСТВО', NULL, 'Не важно под названием каким</br>\nМы молимся Разумному Началу.</br>\nА важно Духом быть единым с Ним</br>\nИ плыть вперед к Великому Причалу.</br>', NULL, NULL, NULL),
(968, 5, 1, 'НЕ БУДЬ СЕБЕ ВОПРЕКИ', NULL, 'Вселенская книга, а в ней ты – страница!</br>\nТы – капля энергии мудрой реки!</br>\nТы – светлая, чистая Бога частица!</br>\nСебе – значит Богу – не будь вопреки!</br>', NULL, NULL, NULL),
(969, 5, 1, 'ПОДУМАЙ И ВОЗБЛАГОДАРИ', 'Старинная надпись на храме', 'У каждого должны</br>\nЗажить слова внутри:</br>\n«Подумай и возблагодари!»</br>\nПодумайте, что нужно было, нам судьба дала.</br>\nВозблагодарите Господа за добрые дела!</br>', NULL, NULL, NULL),
(970, 5, 1, 'ВСЕ ВНУТРИ', NULL, 'Я – искра Божья! Бог со мной!</br>\nТворю всё то, что мило.</br>\nГлубокий внутренний покой</br>\nИ внутренняя сила.</br>', NULL, NULL, NULL),
(971, 5, 1, 'НЕТ НИЧЕГО СЛУЧАЙНОГО НА СВЕТЕ', 'По изречению А. Ельчанинова', 'Нет ничего случайного на свете,</br>\nВсё обсуждается в Божественном совете.</br>\nКто верит, что случайна путь-дорога,</br>\nНе верит, значит, тот по жизни в Бога.</br>', NULL, NULL, NULL),
(972, 5, 1, 'БОГ НАПРАВИТ СТОПЫ ЕГО', 'Библия ПР. 16.9', 'Не только зависит суть</br>\nОт решения твоего:</br>\nЧеловек обдумал путь,</br>\nБог направил стопы его.</br>', NULL, NULL, NULL),
(973, 5, 1, 'ВО ДНИ БЛАГОПОЛУЧИЯ И НЕСЧАСТИЯ', 'По Библии Ек. 7.14', 'Небесное созвучие</br>\nВо дни благополучия.</br>\nПользуйся Божьим благом.</br>\nДушевное участие</br>\nВо дни несчастия,</br>\nПомысли над каждым шагом.</br>', NULL, NULL, NULL),
(974, 5, 1, 'НЕ ЗАБУДЬ', NULL, 'Не в наших этот Мир властях.</br>\nНе нужно жить ему грубя.</br>\nЖиви, как дома у себя,</br>\nНо не забудь, что ты в гостях.</br>', NULL, NULL, NULL),
(975, 5, 1, 'БЕСКОРЫСТНАЯ И ВЕЧНАЯ ЛЮБОВЬ', NULL, 'Утеряно, не помним как давно,</br>\nБесценное и очень дорогое.</br>\nХоть что взамен нам не было б дано,</br>\nМы – нищие, а значит мы – изгои.</br>\n</br>\nМы – нищие, вокруг нас пустота,</br>\nНо о потере той порой доходит молвь,</br>\nБогатство это – жизни полнота –</br>\nЕсть бескорыстная и вечная любовь.</br>', NULL, NULL, NULL),
(976, 5, 1, 'В ЛЮБОВЬ БРОСАЙСЯ С ГОЛОВОЙ', 'По изречению В. О. Рузова', 'Любовь – есть путь важнейший твой.</br>\nВсего себя к любви готовь.</br>\nВ любовь бросайся с головой,</br>\nИначе, это – не любовь!</br>', NULL, NULL, NULL),
(977, 5, 1, 'КОГДА ЖЕЛАЕТЕ ЛЮБВИ И МИРА', NULL, 'С поэтом рядом вдохновительная лира,</br>\nКогда желает превосходное творить,</br>\nИ также, Истина захочет рядом быть,</br>\nКогда желаете Душой любви и мира.</br>', NULL, NULL, NULL),
(978, 5, 1, 'ЦВЕТОК ЛЮБВИ', NULL, 'Анализируем напрасно</br>\nЗа что кого-то любим страстно.</br>\nЗря теребим любви росток,</br>\nНебесных ищем чувств исток.</br>\n-------------------------------------</br>\nБог в тех делах один знаток.</br>\nБог дарит нам любви цветок.</br>', NULL, NULL, NULL),
(979, 5, 1, 'ПЕРЕПЛЫТЬ С ЛЮБОВЬЮ', NULL, 'Найди любви неиссякаемый фонтан,</br>\nИспей в нём Истину и будь готов к условью,</br>\nЧто реку жизни, даже жизней океан,</br>\nТы должен будешь переплыть с любовью.</br>', NULL, NULL, NULL),
(980, 5, 1, 'НАЧИНАЙ МИЛОСЕРДИЕ С ДОМА', NULL, 'Благодарность твоя так весома,</br>\nТерпеливость твоя всех важней.</br>\nНачинай милосердие с дома,</br>\nС самых близких тебе людей.</br>', NULL, NULL, NULL),
(981, 5, 1, 'УДИВИТЕЛЬНЫЕ ПЕРЕМЕНЫ', NULL, 'Когда позволишь каждому вокруг</br>\nВдруг стать таким, как есть.</br>\nПредставишь, что он близкий друг,</br>\nНесущий добру весть.</br>\n</br>\nТо удивительные перемены</br>\nВзволнуют твою кровь:</br>\nГде разделенья нет, разрушены где стены –</br>\nЕстественно присутствует любовь!</br>', NULL, NULL, NULL),
(982, 5, 1, 'ЛЮБОВЬ К СЕБЕ', NULL, 'Мысль красною нитью сквозь Библию тянется:</br>\n«Всем разумом, сердцем Бога любите!»</br>\nИисусом Христом нам не раз упомянется:</br>\n«К себе, в свою душу любовь ту пошлите.»</br>', NULL, NULL, NULL),
(983, 5, 1, 'УВАЖАЙ СЕБЯ', NULL, 'К себе уваженье создай,</br>\nПоддерживай и развивай.</br>\nВсе лучше к себе относись,</br>\nЛюби себя и светись.</br>\n</br>\nЗатем, помни, только затем,</br>\nПо жизни ты встретишься с тем:</br>\nКто мудр, идеален, велик,</br>\nВ ком видишь Божественный лик.</br>', NULL, NULL, NULL),
(984, 5, 1, 'С ЧЕМ ПРОЖИТЬ', 'По изречению из Библии 1 Кор. 13.4', 'Разные у чувств есть коридоры,</br>\nС чем прожить себя предуготовь.</br>\nВозбуждает ненависть раздоры,</br>\nПокрывает все грехи любовь.</br>', NULL, NULL, NULL),
(985, 5, 1, 'С ЛЮБОВЬЮ К БЛИЖНЕМУ', NULL, 'Ты любовью к ближнему</br>\nБога не сласти.</br>\nБудь с любовью к ближнему,</br>\nЧтоб Бога обрести.</br>', NULL, NULL, NULL),
(986, 5, 1, 'ВЫСОКИЕ ЧАСТОТЫ', NULL, 'Когда Любовь – высокие частоты!</br>\nВас ждут, как воздух, как глоток воды.</br>\nНа низких – зависть, злобные охоты.</br>\nКто любит, не доступен для мрачной суеты.</br>', NULL, NULL, NULL),
(987, 5, 1, 'КТО ЛЮБИТ ВСЕХ', 'По изречению из Корана', 'Кто любит всех,</br>\nТому всегда везде любовь опора.</br>\nВозвысьте тех,</br>\nКто делает добро всем без разбора.</br>', NULL, NULL, NULL),
(988, 5, 1, 'ПОБЕДА', NULL, 'Когда всерьёз к себе</br>\nЛюбовной облачишься формой,</br>\nТо навсегда в тебе</br>\nПобеда – станет нормой!</br>', NULL, NULL, NULL),
(989, 5, 1, 'КТО ЛЮБИТ', NULL, 'Кто любит, любимым</br>\nПуть к счастью упрочит.</br>\nОн даст быть счастливым,</br>\nКак тот того хочет.</br>', NULL, NULL, NULL),
(990, 5, 1, 'Я – ПРОСТО ПРЕЛЕСТЬ', NULL, 'Когда тебе, иль про тебя</br>\nСказали ересь,</br>\nОтветь им, не грубя:</br>\n«Я – просто прелесть!»</br>\n</br>\nЛюби себя!</br>\nЛюби всегда и много!</br>\nЛюбя себя!</br>\nВ себе ты любишь Бога!</br>', NULL, NULL, NULL),
(991, 5, 1, 'ЧТОБ ЛЮБОВЬ ПРИШЛА', NULL, 'Раз хочешь, чтоб любовь пришла,</br>\nРаз хочешь, чтоб она взросла,</br>\nЕсть средство верное одно –</br>\nВ себе посей любви зерно.</br>', NULL, NULL, NULL),
(992, 5, 1, 'ПРИЕМЛЮ КАЖДОГО', NULL, 'Приемлю каждого таким, каков он есть!</br>\nНе возведу, а также не обижу.</br>\nВ своей душе добру позволю только цвесть.</br>\nЯ всех люблю и свет я в каждом вижу!</br>', NULL, NULL, NULL),
(993, 5, 1, 'НЕ ВИНИ', NULL, 'Зачем винить кого-то за тщеславие,</br>\nОни, как все, свернут на путь прозрения.</br>\nА чтобы нам самим не пасть в бесславие,</br>\nЗови любовь в свое мировоззрение.</br>', NULL, NULL, NULL),
(994, 5, 1, 'ПРИНЦИП ИСТИНЫ', NULL, 'Принцип Истины – любовь, а не насилие</br>\nИ нельзя ей агрессивно предлагать</br>\nЖизнь другую забирать, иль низвергать,</br>\nТолько к благу, к радости усилие.</br>', NULL, NULL, NULL),
(995, 5, 1, 'СТАНЕТ ОДИНОЧЕСТВО ТЮРЬМОЮ', 'По изречению Ф. Ницше', 'Чтоб не стоять у мрачного причала,</br>\nГде лето перепуталось с зимою,</br>\nГде день лучистый обвенчался с тьмою,</br>\nСебя любить начните для начала.</br>\nВедь если любите себя вы мало,</br>\nТо станет одиночество тюрьмою.</br>', NULL, NULL, NULL),
(996, 5, 1, 'НАШЕ СЕРДЦЕ', NULL, 'Что любовь без эротики, лаже,</br>\nЗря твердим мы у сердца на страже.</br>\nНаше сердце не так уж мало,</br>\nЧтоб его только с этим сплело.</br>\nВ нашем сердце свободно, светло,</br>\nВ нашем сердце весь Мир, всё тепло</br>\nИ любовь ко Вселенной всей даже.</br>', NULL, NULL, NULL),
(997, 5, 1, 'ЧЕРЕЗ ЛЮБОВЬ К ИСТИНЕ', 'По восточному ведическому трактату', 'К мудрой истине путь</br>\nСам себе уготовь.</br>\nПознаёшь её суть,</br>\nРаскрывая любовь.</br>', NULL, NULL, NULL),
(998, 5, 1, 'ИЗО ДНЯ В ДЕНЬ', NULL, 'К любви относиться нам нужно священно,</br>\nНо быть терпеливым становится лень.</br>\nГотовы любить мы всю жизнь, несомненно.</br>\nЛюбить не готовы лишь изо дня в день.</br>', NULL, NULL, NULL),
(999, 5, 1, 'ДВЕ МУДРОСТИ', NULL, 'Две мудрости живут под солнышком светящим,</br>\nНесущие основы человечности:</br>\n1. Себя любите и живите настоящим.</br>\n2. Любите всё и думайте о вечности.</br>', NULL, NULL, NULL),
(1000, 5, 1, 'КОГО ХОТИМ', NULL, 'Бывает так, что душу всю изгложем,</br>\nНо жизнь свою мы сами же мостим:</br>\nЗачем любить того, кого мы можем,</br>\nДавай любить того, кого хотим.</br>', NULL, NULL, NULL),
(1001, 5, 1, 'УЗЫ МИРА', NULL, 'Любовь разумное и мудрое движение,</br>\nПрироды части вяжет ее нить.</br>\nОна от Бога и Его есть выражение</br>\nЖелания весь мир соединить.</br>', NULL, NULL, NULL),
(1002, 5, 1, 'ЛЮБОВЬ, КАК УТРО', NULL, 'Любовь, как утро, любит пробуждать,</br>\nЛюбовь, как вечер, любит поблуждать,</br>\nРаз ты имеешь мужество влюбиться,</br>\nИмей тогда и мужество страдать.</br>', NULL, NULL, NULL),
(1003, 5, 1, 'ПОТРЕБНОСТЬ И ЛЮБОВЬ', NULL, 'Любовь – души вибрация,</br>\nТо – опыт, сердце, ум,</br>\nТо – счастья коронация,</br>\nТо – свет прекрасных дум.</br>\nКак жаль, когда в объятиях</br>\nБурлит желаньем кровь,</br>\nМы путаем понятия:</br>\nПотребность и любовь.</br>', NULL, NULL, NULL),
(1004, 5, 1, 'БОЖЬЮ ТАЙНУ', NULL, 'Раз вам любовь дана сейчас, берите.</br>\nДругого вам не будет вскоре займу.</br>\nНо всем подряд о ней не говорите,</br>\nТем разглашаете вы Божью тайну.</br>', NULL, NULL, NULL),
(1005, 5, 1, 'КРАСОТА', NULL, 'Во всем старайся видеть красоту.</br>\nВ ней воскресишь надежду и мечту.</br>\nВ ней обретёшь восторг и силу вновь.</br>\nВ божественной энергии – любовь.</br>', NULL, NULL, NULL),
(1006, 5, 1, 'СЕЙЧАС', NULL, 'В неизвестный и будущий час</br>\nВсе мечтают счастливыми стать,</br>\nНо зачем жизнь, как книгу листать,</br>\nБыть со счастьем мы можем сейчас!</br>', NULL, NULL, NULL),
(1007, 5, 1, 'НИ ЗДЕСЬ, НИ ТАМ', NULL, 'Мы будущему очень часто</br>\nСвои все мысли отдаем.</br>\nНе видим настоящего напрасно:</br>\nНи здесь! Ни там! В итоге: не живём.</br>', NULL, NULL, NULL),
(1008, 5, 1, 'БЕЗУСЛОВНАЯ ЛЮБОВЬ', NULL, 'Безусловная любовь –</br>\nБез горечи страдания.</br>\nБезусловная любовь –</br>\nБез права обладания.</br>\nБезусловная любовь –</br>\nВосторг без поклонения.</br>\nБезусловная любовь –</br>\nСвободный выбор мнения.</br>\nБезусловная любовь –</br>\nПрироды назидание.</br>\nБезусловная любовь –</br>\nОснова мироздания.</br>', NULL, NULL, NULL),
(1009, 5, 1, 'ВОЙДИ В НАСТОЯЩЕЕ', NULL, 'Когда со всем вниманием ты входишь в настоящее,</br>\nКак жизнь священна понимаешь!</br>\nИзвечность простоты и радость бытия манящая,</br>\nКак свято всё! Ты то в себя внимаешь.</br>', NULL, NULL, NULL),
(1010, 5, 1, 'ШАГ В НАСТОЯЩЕЕ', NULL, 'Как в настоящее ты сделал шаг,</br>\nУма пределы ты покинул…</br>\nПоток мышления затих и схлынул…</br>\nПространства… Тишины… Ты чувствуешь размах.</br>', NULL, NULL, NULL),
(1011, 5, 1, 'ТЕКУЩЕЕ МГНОВЕНИЕ', 'По изречению Э. Толле', 'Не путай содержание сейчас,</br>\nНа то, что в нем в суетах происходит.</br>\nТекущее мгновенье глубже в нас:</br>\nВ пространство проявления восходит.</br>', NULL, NULL, NULL),
(1012, 5, 1, 'СКАЖИ: «ДА»', NULL, 'Тому, что есть сейчас, скажите: да!</br>\nИ к мощи жизни, разуму подключитесь,</br>\nИ в мире быть проводником тогда</br>\nБлагих всех перемен научитесь.</br>', NULL, NULL, NULL),
(1013, 5, 1, 'ИЗ МИГА В МИГ', NULL, 'Когда сейчас становится основой,</br>\nТо ты воспримешь эту жизнь по новой.</br>\nЖиви из мига в миг! Никак иначе!</br>\nИ станешь, вдруг, сильней, мудрей, богаче!</br>', NULL, NULL, NULL),
(1014, 5, 1, 'ЦЕНИТЕ ТОЛЬКО НАСТОЯЩЕЕ', NULL, 'Нет! Не цените будущего больше настоящего.</br>\nВажнее цель? Иль к этой цели путь?</br>\nПридет то будущее также в настоящее.</br>\nЕсть жизнь сейчас – и в этом смысл и суть!</br>', NULL, NULL, NULL),
(1015, 5, 1, 'ПРИЗНАЙ И ОБЛАЧИСЬ', NULL, 'Не отрицай в себе насущные надежды.</br>\nНо ты пойми, они вторичны!</br>\nА в настоящем мы всегда первичны!</br>\nПризнай его и облачись в его одежды!</br>', NULL, NULL, NULL),
(1016, 5, 1, 'МЫСЛЕФОРМЫ', NULL, 'Есть настоящее!</br>\nЕсть только лишь сейчас!</br>\nА будущее с прошлым – мыслеформы.</br>\nНаш ум придумал эти нормы.</br>\nЖиви сейчас! Оно поддержит нас!</br>', NULL, NULL, NULL),
(1017, 5, 1, 'ВРЕМЯ – УДАЧА', NULL, 'Надо понять, что время – удача!</br>\nРаз рядом оно – береги!</br>\nВся радость ему и никак иначе,</br>\nВперёд его не беги!</br>', NULL, NULL, NULL),
(1018, 5, 1, 'ВОЛНУЮТ ВОСПОМИНАНИЯ', NULL, 'Волнует всё ж не тот, кого любили,</br>\nРеальный образ мы давно забыли.</br>\nПоймите правду и примите без прикрас,</br>\nВоспоминания о том волнуют нас.</br>', NULL, NULL, NULL),
(1019, 5, 1, 'ЖАЛОСТЛИВО СМОТРИТСЯ', 'По высказыванию Константина Мелихана', 'Как-то всё ж не спорится: нужно жить, он удит.</br>\nЖалостливо смотрится: что ловил, уплыло.</br>\nВ юности нам кажется то, что счастье будет.</br>\nВ старости покажется то, что счастье было.</br>', NULL, NULL, NULL),
(1020, 5, 1, 'НО ПОКА ЧТО МНЕ', NULL, 'Всё внутри пьяней, не закрыть ставней.</br>\nМысль полна минувших видений, теней.</br>\nСтать бы поумней, но пока что мне</br>\nБудущее лучше, прошлое родней.</br>', NULL, NULL, NULL),
(1021, 5, 1, 'ЩЕМЯЩЕЕ ЯВЛЕНИЕ', 'По изречению Вильгельма Фишера', 'Явление щемящее,</br>\nВозможно, это станется.</br>\nКто к будущему тянется</br>\nУронит настоящее.</br>', NULL, NULL, NULL),
(1022, 5, 1, 'НАСТОЯЩЕМУ ТЕСНО', 'По изречению Витаутаса Каралюса', 'Где взять от жизни толка</br>\nВ мечтах своих гостящему.</br>\nВ нас будущего столько,</br>\nЧто тесно настоящему.</br>', NULL, NULL, NULL),
(1023, 5, 1, 'ЖИТЬ НАСТОЯЩИМ', NULL, 'Нас прошлое чем-то печалит,</br>\nНас в будущем что-то страшит,</br>\nИ только лишь тот жизнь похвалит,</br>\nКто жить настоящим решит.</br>', NULL, NULL, NULL),
(1024, 5, 1, 'РАЗМЫШЛЕНИЯ ФИЛОСОФОВ', NULL, 'Изменить как жизни путь?</br>\nПрошлого, ведь не вернуть.</br>\nБеды как нам отогнать?</br>\nБудущего не узнать.</br>\nРадость жизни как пожать?</br>\nЭтот миг не удержать.</br>', NULL, NULL, NULL),
(1025, 5, 1, 'ГОРИЗОНТ – ЭТО НАДЕЖДА', NULL, 'Жизнь – мечты,</br>\nМысли зонт укрывает нас нежно.</br>\nЖизнь – версты,</br>\nГоризонт овевает надежда.</br>', NULL, NULL, NULL),
(1026, 5, 1, 'ЗРЯ СПЕШИМ', NULL, 'Пролетят года,</br>\nРитм не сокрушим.</br>\nЗнаем все, куда…</br>\nВсё равно, спешим.</br>', NULL, NULL, NULL),
(1027, 5, 1, 'МИГ, ВЕЧНОСТЬ И ВРЕМЯ', NULL, 'Философия гласит, что миг и вечность,</br>\nПредставляют в сущности одно и то же.</br>\nНу, а время, что являет нам конечность,</br>\nНи на то, ни на другое, не похоже.</br>', NULL, NULL, NULL),
(1028, 5, 1, 'ВРЕМЯ', NULL, 'Временем всё учтено:</br>\nКем то время сплочено,</br>\nКем то время продлено.</br>\nКем оно сохранено.</br>\nВ того время влюблено,</br>\nДля кого оно ценно.</br>\nРаз работаешь на время.</br>\nВместе с ним всё свершено.</br>', NULL, NULL, NULL),
(1029, 5, 1, 'РАЗНИЦА', NULL, 'О прошлом знаем всё.</br>\nКак жаль, его переплести не можем снова нить.</br>\nО будущем не знаем ничего,</br>\nНо то, что будет, в силах изменить.</br>', NULL, NULL, NULL),
(1030, 5, 1, 'НОСТАЛЬГИЯ', 'По изречению Стаса Янковского', 'В душе драматургия.</br>\nЗаныла ностальгия:</br>\nИ памяти тревожно,</br>\nА может, осторожно,</br>\nПопасть назад не сложно?</br>\nНо нужно бы очнуться,</br>\nВедь некуда вернуться.</br>', NULL, NULL, NULL),
(1031, 5, 1, 'ЗАБЫТЬ ВЧЕРАШНЕЕ ПОРА', 'По изречению Лиона Фейхтвангера', 'Что было в жизни уж однажды,</br>\nДавно развеяли ветра.</br>\nМиг не вернёшь, то знает каждый.</br>\nЗабыть вчерашнее пора.</br>\nВода, что выпита вчера,</br>\nНе утолит сегодня жажды.</br>', NULL, NULL, NULL),
(1032, 5, 1, 'СПОСОБНОСТЬ ЗАБЫВАТЬ', 'По изречению Оноре де Бальзака', 'Способность забывать – великий дар;</br>\nИм отличаются натуры сильные</br>\nИ к творчеству любвеобильные,</br>\nТе знают, что несёт кошмар</br>\nНе забывания пожар.</br>', NULL, NULL, NULL),
(1033, 5, 1, 'КТО НЕ ВЕРИТ В БУДУЩЕЕ', 'По изречению Александра Любищева', 'Эта мысль пусть ширится,</br>\nПомолчите судящие.</br>\nС настоящим мирится,</br>\nКто не верит в будущее.</br>', NULL, NULL, NULL),
(1034, 5, 1, 'ЛЕКАРСТВО ДЛЯ ЗДОРОВЬЯ', NULL, 'Лучшее лекарство для нашего здоровья –</br>\nДушу поместить в приятные условья.</br>', NULL, NULL, NULL),
(1035, 5, 1, 'МОЛОДОСТЬ', NULL, 'Не пугайтесь годов долгих бремени,</br>\nС нами молодость в вечном слиянии,</br>\nОна в сердце живёт, не во времени,</br>\nЭто – нашей души состояние.</br>', NULL, NULL, NULL),
(1036, 5, 1, 'СЕРДЦЕ', NULL, 'Спит весь дом, а мое сердце ропщет,</br>\nЗнаю, часто его я гублю.</br>\nИль за это судьба меня топчет,</br>\nИль за то, что я мало люблю.</br>', NULL, NULL, NULL),
(1037, 5, 1, 'ЧИСТОТА ПОМЫСЛОВ', NULL, 'От всех болезней создано давно лекарство:</br>\nИ исцеленье – не в таблетках, в нас самих!</br>\nИсточник вечной силы в нас! Здоровья царство!</br>\nРецепт тот: Чистота всех помыслов твоих!</br>', NULL, NULL, NULL),
(1038, 5, 1, 'УСПОКОЙ НЕРВЫ', 'По изречению Наполеона', 'Для леченья своего найдёшь резервы,</br>\nУспокоив расшалившиеся нервы.</br>', NULL, NULL, NULL),
(1039, 5, 1, 'АРИТМИЯ', NULL, 'Аритмия сердца, аритмия…</br>\nВьётся сердце, плачет и кричит…</br>\nНе обидь еще! О! Мама мия!</br>\nА не то навеки замолчит.</br>', NULL, NULL, NULL),
(1040, 5, 1, 'НЕ ВАЖНО, СКОЛЬКО ЛЕТ', NULL, 'Кто смотрит на года, тем надобен совет:</br>\nНе важно, сколько человеку лет!</br>\nНе важно! Тяжела… легка… годов сума.</br>\nА важно, набрался ли человек ума.</br>', NULL, NULL, NULL),
(1041, 5, 1, 'МОЩНЫЙ ДУХ', 'По изречению Гиппократа', 'Здоровью повезёт, раз сердце захотело.</br>\nМощный дух спасёт расслабленное тело.</br>', NULL, NULL, NULL),
(1042, 5, 1, 'ХИРУРГ', NULL, 'Сибирь... Хирург… Ему сто лет.</br>\nНо не дрожит рука.</br>\nОн никогда не знал диет,</br>\nА вот живет века.</br>\n</br>\nИ не до спорта, много дел,</br>\nНо в жизнь он принцип ввел:</br>\n«За стол совсем голодным сел,</br>\nЧуть голоден ушёл».</br>', NULL, NULL, NULL),
(1043, 5, 1, 'ПРИЧИНА КУШАТЬ', NULL, 'Одна причина кушать есть,</br>\nКогда ты очень хочешь есть.</br>\nЗапомни благостный совет:</br>\nДругой причины кушать нет!</br>', NULL, NULL, NULL),
(1044, 5, 1, 'НЕВОЗДЕРЖАННОСТЬ', 'По изречению О. Де Бальзака', 'Избегай безудержных страстей,</br>\nНевоздержанность – царица всех смертей.</br>', NULL, NULL, NULL),
(1045, 5, 1, 'А ЧТО ЖЕ ПОЛЕЗНЕЙ', NULL, 'С вином станет жизнь к нам намного любезней,</br>\nВино есть забвенье от наших мытарств.</br>\nНо нужно подумать, а что всё ж полезней;</br>\nВино может лучше и многих лекарств,</br>\nНо всё же не лучше премногих болезней.</br>', NULL, NULL, NULL),
(1046, 5, 1, 'ЛЮБЫЕ ГОДЫ', NULL, 'Хороши любые годы –</br>\nТаково должно быть чувство:</br>\nЮность – это дар природы,</br>\nЗрелость – это дар искусства.</br>', NULL, NULL, NULL),
(1047, 5, 1, 'ТОГО, КТО УПУСТИЛ', NULL, 'Старость того беспощадно изгложет,</br>\nКто упустил всё, что молодость может.</br>', NULL, NULL, NULL),
(1048, 5, 1, 'МАНТРА ЗДОРОВЬЯ', NULL, 'Я – стройный и спортивный!</br>\nЯ – каждой мышцей сильный!</br>\nЗдоровое питание.</br>\nЕм мало, со вниманием.</br>\nЖелудка сытость слушаю.</br>\nЯ перед сном не кушаю.</br>\nЯ радуюсь зарядке</br>\nИ всё во мне в порядке!</br>', NULL, NULL, NULL),
(1049, 5, 1, 'ЭЛЕКСИР МОЛОДОСТИ', NULL, 'Пока к новым мыслям готов приловчиться,</br>\nПока всем заботам готов ополчиться,</br>\nПока в нежных чувствах готов утончиться,</br>\nПока ты готов от всего излечиться,</br>\nПока ты способен чему-то учиться –</br>\nТы молод! И можешь за то поручиться.</br>', NULL, NULL, NULL),
(1050, 5, 1, 'О БОДРОСТИ', NULL, 'Если хочешь бодрость сохранить в пути,</br>\nТо немного не доешь и не доспи.</br>', NULL, NULL, NULL),
(1051, 5, 1, 'ЦВЕТЫ ЮНОСТИ', NULL, 'Кто говорит: Старею я!</br>\nДурна того привычка,</br>\nТо как опасная змея,</br>\nТо к старости отмычка.</br>\n</br>\nКаким себя представишь ты,</br>\nТаким затем и станешь,</br>\nА юности храня цветы</br>\nТы вечно не завянешь.</br>', NULL, NULL, NULL),
(1052, 5, 1, 'ВОЗРАСТ', NULL, 'Возраст – то форм превращенья звено.</br>\nВ возрасте каждому быть суждено.</br>\nС возрастом ярче свет мудрости будет.</br>\nС возрастом круче коньяк и вино.</br>\nС возрастом лучше хорошие люди.</br>', NULL, NULL, NULL),
(1053, 5, 1, 'НУЖНО ДОГАДАТЬСЯ', NULL, 'Постарел и перестал влюбляться –</br>\nИ чему тут можно удивляться?</br>\nА тому, что нужно догадаться:</br>\nМы стареем, перестав влюбляться!</br>', NULL, NULL, NULL),
(1054, 5, 1, 'ГОДЫ – ЖИЗНИ АТРИБУТ', NULL, 'Года – это жизненный наш атрибут:</br>\nС годами бывает, тихонечко тают.</br>\nС годами бывает, себя исчерпают.</br>\nС годами бывает, себя обретут.</br>', NULL, NULL, NULL),
(1055, 5, 1, 'ЧЕМ ЖИВ ЧЕЛОВЕК', NULL, 'Не хлебом единым жив человек!</br>\nМеньше еды – становишься чище.</br>\nУсердно работай в жизненный век:</br>\nТвой разум и Дух получат пищу.</br>', NULL, NULL, NULL),
(1056, 5, 1, 'ЮНОСТЬ', NULL, 'Юность – это счастье, когда мы все удалы.</br>\nЮность – это сила, когда кипят накалы.</br>\nЮность – это смелость, в ней не страшны провалы.</br>\nЮность – это место, где не нужны привалы.</br>\nЮность – это время, чтоб взяться за штурвалы.</br>\nЮность – это буря, шторм, ветер, волны, скалы.</br>', NULL, NULL, NULL),
(1057, 5, 1, 'ДВОРЕЦ', NULL, 'Богом тело дано, как дворец для души,</br>\nПусть и прочно оно, не пытай, не круши.</br>\nПомни: пьянство, обжорство, ленивость, разврат,</br>\nГонят душу нещадно из собственных врат.</br>', NULL, NULL, NULL),
(1058, 5, 1, 'НЕ ОТ МУДРОСТИ', 'По изречению из Библии Ек. 7.10', 'Недовольство слышится признавших</br>\nТо, что прежние деньки получше наших.</br>\nНе от мудрости твердит об этом кто-то,</br>\nВремя разное и разные красоты.</br>', NULL, NULL, NULL),
(1059, 5, 1, 'НЕ ТРАТЬ ВПУСТУЮ', NULL, 'Не трать впустую ни свой дух, ни тело.</br>\nДа, в них – родник, он может иссякать.</br>\nУвидишь, жизнь и сущность обеднела!</br>\nА где же ключ бьёт новый, не сыскать.</br>', NULL, NULL, NULL),
(1060, 5, 1, 'СТАРОСТИ, ОПЫТУ, СЕДИНАМ', NULL, 'Мы ценим очень:</br>\nЕсли лечит старый врач,</br>\nСюиту играет опытный скрипач,</br>\nПрофессор мудрый лекции читает,</br>\nСедой астролог путь судьбы чертает.</br>\n----------------------------------------------</br>\nМы верим в опыт, опыт – жизни ткач.</br>\nМы знаем точно, мудрость – суть удач.</br>', NULL, NULL, NULL),
(1061, 5, 1, 'ЧУДНЫЙ САМОЦВЕТ', 'По изречению Л. Э. Варлена', 'Меняется сиянье с каждым днём.</br>\nАх, очи жизни – чудный самоцвет!</br>\nГлаза у юношей горят огнём.</br>\nГлаза у старцев излучают свет.</br>', NULL, NULL, NULL),
(1062, 5, 1, 'ИСТОЧНИК ЭНЕРГИИ', NULL, 'Энергию жизни сыщешь везде:</br>\nВ воздухе, в пище, в воде.</br>\nНо Бог дал главнее, ценнее клад:</br>\nВ тебе мощнейший заряд.</br>\n</br>\nУмей обратиться к свету души,</br>\nС душою дела верши!</br>\nВольёт в тебя силу, радость, простор</br>\nДуши лучезарный взор!</br>', NULL, NULL, NULL),
(1063, 5, 1, 'СОВЕРШЕНСТВО', NULL, 'Ты не можешь быть красивым иль не очень.</br>\nУспокойся, породи блаженство.</br>\nИстина вся в том, что, между прочим,</br>\nТвоя внешность – это совершенство.</br>', NULL, NULL, NULL),
(1064, 5, 1, 'СТАРЫХ УВАЖАЙ', NULL, 'Старость тянет каждому ладонь.</br>\nКаждому придёт в объятья сонь.</br>\nНикому не скрыться от погонь.</br>\nНикакая не поможет бронь.</br>\nПоглотит нас всех её огонь.</br>\n-----------------------------------</br>\nТы не вечно млад, то урезонь.</br>\nСтарых уважай и их не тронь!</br>', NULL, NULL, NULL),
(1065, 5, 1, 'МАНТРА ПЕРЕД СНОМ', NULL, 'Мой сон – природы повеление.</br>\nМой сон приносит исцеление.</br>\nНабрав энергию и силу к пробуждению,</br>\nЯ подготовлюсь к новому рождению.</br>', NULL, NULL, NULL),
(1066, 5, 1, 'ДОЛГО ЖИТЬ', 'По американскому изречению', 'Хочет мудрость доложить,</br>\nЧто ты должен долго жить.</br>\nЧто ты должен не забыть,</br>\nЧто не должен старым быть.</br>', NULL, NULL, NULL),
(1067, 5, 1, 'ПРИЧИНА БОЛЕЗНЕЙ', NULL, 'Раз на тело болезней нападки,</br>\nВ чем причина нам нужно найти:</br>\nВидно в жизни опять неполадки,</br>\nЗначит, где-то мы сбились с пути.</br>', NULL, NULL, NULL),
(1068, 5, 1, 'ИСПЫТАНЬЕ СИЛ ДУХОВНЫХ', NULL, 'Старость зря нам видится с тоской,</br>\nЕсть в ней промысел властей верховных.</br>\nИ не отдых это, не покой.</br>\nСтарость – испытанье сил духовных.</br>', NULL, NULL, NULL),
(1069, 5, 1, 'ПЕРЕЕДАНИЕ', NULL, 'Переедание, то жалкий горький недуг.</br>\nТы с ним растление от жизни получаешь;</br>\nГрешишь, влачишь, толстеешь и скучаешь.</br>\nТак уважай себя, садясь к обеду.</br>', NULL, NULL, NULL),
(1070, 5, 1, 'ПОДГОТОВКА К СТАРОСТИ', 'По изречению Сенеки', 'Не нужен в делах твоих жизненных спех.</br>\nТы жить постарайся, на фетиш не зарясь.</br>\nИз всех своих благ позаботься о тех,</br>\nКоторые лучше становятся, старясь.</br>', NULL, NULL, NULL),
(1071, 5, 1, 'СПАСЕНИЕ И ЯД', NULL, 'Имеешь за здоровье опасение?</br>\nПрими тогда на то восточный взгляд:</br>\nКогда ты голоден, еда – спасение,</br>\nКогда не голоден, еда есть яд.</br>', NULL, NULL, NULL),
(1072, 5, 1, 'КТО ПЬЁТ ДО ДНА', 'По изречению Е. Кащеева', 'Душа пьяна, вино вливается,</br>\nКто пьёт до дна, до дна спивается.</br>', NULL, NULL, NULL),
(1073, 5, 1, 'СКУКА', NULL, 'Кто лишь чувственное понял наслажденье,</br>\nКто духовное не начал возрожденье,</br>\nКто Божественного не услышал звука,</br>\nВ зрелом возрасте сопутствует тем скука.</br>', NULL, NULL, NULL),
(1074, 5, 1, 'ЕСЛИ БЫ МОЛОДОСТЬ ЗНАЛА', 'По изречению Д. Рудого', 'Если бы молодость знала,</br>\nТо старость бы это могла,</br>\nИ не было б грусти финала,</br>\nИ в старости б жизнь не брела.</br>', NULL, NULL, NULL),
(1075, 5, 1, 'ОГРАНКА', 'По восточному трактату', 'Тело леновато по природе,</br>\nНо ему послушно угождаем.</br>\nВсё, что хочет, то ему в угоду</br>\nБезрассудно, слепо предлагаем.</br>\n</br>\nДобровольно те желанья ограничив,</br>\nМы душе своей даём огранку.</br>\nК кучке атомов, как станет взгляд придирчив,</br>\nТак, развитие души повысит планку.</br>', NULL, NULL, NULL),
(1076, 5, 1, 'АБСОЛЮТНОЕ РАССЛАБЛЕНИЕ', NULL, 'Сумел расслабиться вполне и абсолютно,</br>\nСдружились сразу мозговые половинки,</br>\nИ мыслям в микрокосмосе твоём уютно,</br>\nИ логики, и интуиции слились тропинки.</br>', NULL, NULL, NULL),
(1077, 5, 1, 'СТАРОСТЬ – ЦВЕТА РАДУГИ', NULL, 'Старость, как и небо; где светлая, где мглистая.</br>\nСтарость разрисовывает разной краской время:</br>\n------------------------------------------------------------</br>\nСтарость для глупца – коричневое бремя.</br>\nСтарость для невежды – белёсая зима.</br>\nСтарость для потерянного – черная тюрьма.</br>\nСтарость для ученого – жатва золотистая.</br>\nСтарость, цвета радуги, для мудрого ума.</br>', NULL, NULL, NULL),
(1078, 5, 1, 'ТЕЛО ОТВЛЕКАЕТ ОТ ДУШИ', NULL, 'Утвержденьем: Пей, гуляй, греши!</br>\nТело отвлекает от души.</br>\nУ него не будь на поводу,</br>\nЧтоб не впасть в животную среду.</br>', NULL, NULL, NULL),
(1079, 5, 1, 'СТАРОСТЬ, КАК ОСЕНЬ', 'По изречению А. Камю', 'Старость, как осень, бывает славна,</br>\nШум листопада и мил, и знаком.</br>\nСтарость, как осень – вторая весна,</br>\nКаждый листок виден ярким цветком.</br>', NULL, NULL, NULL),
(1080, 5, 1, 'ЧЕТЫРЕ КОНВЕРТА', NULL, 'Мы прячем на сердце</br>\nС печатью конверты:</br>\nС проблемой рожденья,</br>\nСтаренья и смерти,</br>\nС проблемой взросленья.</br>\nНе лучше ль их вскрыть,</br>\nПриняв то, что есть,</br>\nМы решим, как нам быть.</br>', NULL, NULL, NULL),
(1081, 5, 1, 'СТОЛЬКО ЖЕ ВНИМАНИЯ', 'По изречению А. Давидовича', 'Будут те шаги для нас полезней,</br>\nЕсли станем вдруг готовые к условью;</br>\nСколько уделяем мы внимания болезни,</br>\nСтолько ж уделять внимания здоровью.</br>', NULL, NULL, NULL),
(1082, 5, 1, 'РЕЖИМ ОГРАНИЧЕНИЙ', NULL, 'Без меры ко всему внутри охота,</br>\nВо вседозволенность открытые ворота.</br>\nПереступаем грань, не видя нормы,</br>\nГрустны, больны, несчастны и тлетворны.</br>\nВ нас несварение от множества влечений.</br>\nНо есть спасение – режим ограничений!</br>', NULL, NULL, NULL),
(1083, 5, 1, 'РАЗ НЕ БЕГАЕШЬ', 'По изречению Горация', 'Сбросьте лености цепляющий покров,</br>\nВместе с леностью плоть незаметно тлеет.</br>\nКто не бегает, когда силён, здоров,</br>\nТот побегает, когда вдруг заболеет.</br>', NULL, NULL, NULL),
(1084, 5, 1, 'ЛОЖНАЯ ДОВОЛЬНОСТЬ', NULL, 'Избегая нежелательную хворость,</br>\nПодключаем дополнительную скорость,</br>\nБодрости искусственной гастроль,</br>\nГде играют жизненную роль:</br>\nКофе, сигареты, алкоголь…</br>\nНо напомнить нужно, в чём же соль:</br>\nАнтидепрессантов произвольность</br>\nПорождают ложную довольность,</br>\nС горькой правдой этот факт соотносим,</br>\nМы летаем и над пропастью висим.</br>', NULL, NULL, NULL),
(1085, 5, 1, 'ЗДОРОВЬЕ – ВОЗМОЖНОСТЬ', 'По изречению Г. Малкина', 'Ну куда же объяснить понятней,</br>\nЧто здоровье даст возможность для всего.</br>\n----------------------------------------------------</br>\nСторону мы ж видим одного:</br>\nПуть к инфаркту веселей, приятней,</br>\nЧем побег обратный от него.</br>', NULL, NULL, NULL),
(1086, 5, 1, 'СО ЗДОРОВЬЕМ НЕ ШУТИ', 'По изречению С. Балашова', 'Для себя пусть каждый разумеет,</br>\nРаз здоров – свободны все пути.</br>\nСо здоровьем лучше не шути,</br>\nЧувство юмора здоровье не имеет.</br>', NULL, NULL, NULL),
(1087, 5, 1, 'РАЗ НЕ СМОЖЕШЬ БЫТЬ В ГАРМОНИИ', NULL, 'Будешь награждён опять невзгодой,</br>\nБудешь нагружён опять терзаньем,</br>\nВновь болезнь послужит наказаньем,</br>\nРаз не сможешь жить в гармонии с природой.</br>', NULL, NULL, NULL),
(1088, 5, 1, 'НЕРВНАЯ СИСТЕМА', 'По изречению У. Джеймса', 'Не живи во искушенье впавши,</br>\nВедь потом не исчерпать вреда:</br>\nБог всегда простит грехи все наши,</br>\nНервная система – никогда.</br>', NULL, NULL, NULL),
(1089, 5, 1, 'КТО БЕСПОКОЕН', 'По изречению А. Карреля', 'Пускай ты будешь крепким, деловым,</br>\nПускай ты будешь сильным, рисковым,</br>\nРаз беспокоен, вряд ли будешь ты седым,</br>\nКто беспокоен, умирает молодым.</br>', NULL, NULL, NULL),
(1090, 5, 1, 'ЗДОРОВЬЕ НЕ КУПИШЬ', NULL, 'Здоровье стремись укрепить,</br>\nПока не пришлось спохватиться,</br>\nЗдоровье не сможешь купить,</br>\nТы сможешь лишь им расплатиться.</br>', NULL, NULL, NULL),
(1091, 5, 1, 'ЛУЧШЕ СИНИЦА, ЧЕМ УТКА', NULL, 'Здоровье не сыщешь в силках,</br>\nЗнать видно верна прибаутка,</br>\nЧто, лучше синица в руках,</br>\nЧем где-то поблизости утка.</br>', NULL, NULL, NULL),
(1092, 5, 1, 'ЯЗВА', NULL, 'Заблуждаясь, своё тело не щадим,</br>\nИ напрасно кто-то утверждает:</br>\nЯзва – от того, что мы едим.</br>\n------------------------------------</br>\nЯзва – от того, что нас съедает.</br>', NULL, NULL, NULL),
(1093, 5, 1, 'НАС ИЗЛЕЧИТ ПРИРОДА', 'По изречению Гиппокрита', 'Не решим мы всех задач,</br>\nНе узнаем к тайнам кода.</br>\nПусть болезни лечит врач,</br>\nНо излечит нас природа.</br>', NULL, NULL, NULL),
(1094, 5, 1, 'ВСЕХ ДОЛЬШЕ ПРОЖИВЁТ', NULL, 'Кто беспокойства никому не причиняет,</br>\nКто всех прощая, добротой слывёт,</br>\nКто отношений никогда не выясняет,</br>\nТот говорят, всех дольше проживёт.</br>', NULL, NULL, NULL),
(1095, 5, 1, 'ПОЭЗИЯ', NULL, 'Чарует поэзия <em>мудрых</em> сердца,<br>Ритм, рифма, размер – как постройка дворца.<br>Сокрытый в игре слов тончайший узор<br>Глубинных красот открывает обзор.<br>Поэзия <em>непосвященных</em> томит.<br>На нечто святое наложен лимит.<br><em>Поэзия – шифр, в этом смысл есть и толк.</em><br><em>Пророк вечно жив в нём, хотя и умолк.</em><br>\r\n', NULL, NULL, NULL),
(1096, 5, 1, 'test#', NULL, NULL, NULL, NULL, NULL),
(1097, 5, 1, 'test#', NULL, NULL, NULL, NULL, NULL);
